INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "National House of Public Pensions (CNPP)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"https://www.cnpp.ro/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The CNPP is the national authority in the field of insurance against work accidents and occupational diseases.</p><p>The former National House of Pensions and Other Social Insurance Rights was established through Law No 19 of 17 March 2000 on the public system of pensions and other social insurance rights, and, therefore, managed the system of public pensions at the national level.</p><p>The Insurance System Against Work Accidents and Occupational Diseases entered into force on 1 January 2005 through amended Law No 346/2002.</p><p>Law No 346/2002 defines insurance against work accidents and occupational diseases as ‘a person related insurance belonging to the social insurance system that is guaranteed by the state’. It regulates the social protection of employees against a reduction in or loss of working capacity in the case of work accidents and occupational diseases.</p><p>The CNPP acts as a compensation and insurance body through the Directorate-General of Work Accidents and Occupational Diseases.</p><p>The services provided by the Directorate-General of Work Accidents and Occupational Diseases include:</p><ul><li>the medical rehabilitation of workers and recovery of their working capacity;</li><li>workers’ rehabilitation and reintegration;</li><li>indemnities for a temporary loss of or reduction in working capacity, etc.</li></ul><p>In addition to its role as the national insurance body, the CNPP is also a significant stakeholder in terms of prevention, that is, a reduction in the amounts paid by companies to the CNPP constitutes an incentive for companies to increase prevention activities and reduce work accidents and occupational diseases.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 1, 0, 0, @maxId+1, @maxId+2, @maxId+3);
