INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Labour Inspectorate");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Labour Inspectorate is the competent public authority in charge of the unitary enforcement of OSH legislation within public and private undertakings in all the economic sectors.<br />The establishment and organisational structure of the Labour Inspectorate are regulated by republished Law No 108/1999 and the amended Regulation of Organisation and Functioning, approved through Government Decision No 1377/2009.<br />The Labour Inspectorate’s main objective resides in monitoring the compliance of employers from the public and private spheres of all economic sectors with legal obligations, both in terms of labour relations and the employees’ safety and health at work. <br />The Labour Inspectorate has 41 labour territorial inspectorates within its organisational structure. These inspectorates represent the 41 districts of the country and enforce labour inspection policy at territorial level. The Labour Inspectorate of Bucharest constitutes a separate entity, corresponding to the size of the capital of the country and its significant number of companies.</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
