INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Ministry of Health");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.ms.gov.ro/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The Ministry of Health is a legal entity and central administration body in the field of public health care.</p><p>&nbsp;</p><p>Like the MMFPSPV, the Ministry of Health transposes governmental policies for the healthcare sector into practice, both within the public healthcare system and in private medical units.</p><p>&nbsp;</p><p>In the area of occupational health, the Ministry of Health is currently drafting regulations for the medical sector, particularly for the development of the health-at-work activities, in line with the EU legislation currently in force.</p><p>&nbsp;</p><p>Furthermore, the Ministry of Health promotes the education of occupational medicine physicians at the academic level, in collaboration with the Ministry of National Education, through occupational medicine clinics hosted by the largest hospitals in the country.</p><p>&nbsp;</p><p>The Ministry of Health is also drafting and implementing adequate strategies and programmes aimed at improving workers’ health in the workplace and reducing/eliminating risk factors that may result in severe occupational diseases, with significant costs in terms of human life and financial resources.</p><p>&nbsp;</p><p>The Ministry of Health also coordinates the activity of the Public Health Institute, which is in charge of monitoring public health at the national level, including work-related diseases, as well as evidence of occupational diseases.</p>");
