INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.mmuncii.ro/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The MMFPSPV is a specialised body of the central public administration and a legal entity subordinated to the Government of Romania that is in charge of the implementation of governmental policies in the areas of labour, family, equal opportunities, social protection and the elderly.</p><p>The MMFPSPV is involved in the occupational safety and health (OSH) field through the Direction of Safety and Health at Work, which is coordinated at the ministry’s top management level by a secretary of state. This direction coordinates two national research and development institutes in the fields of OSH and social security, respectively, while monitoring the implementation of policies and legislative acts in these areas.</p>");
