INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "This statutory institution is the national body for sickness insurance, pension insurance, accident insurance, unemployment insurance and guarantee insurance. Its headquarters regulates, controls and methodologically guides 37 branch offices. It is in charge of pension insurance and two types of benefits from accident insurance. The branch offices of the executive body are responsible for the collection and recovery of sickness, unemployment and guarantee contributions, and benefits pay outs, as well as direct contact with clients.");
