INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"https://www.ujd.gov.sk/ujd/www1.nsf/viewByKeyMenu/En-01\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia \">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The ÚJD SR is a central government authority of the Slovak Republic for nuclear regulation. The authority exercises state supervision over nuclear safety of nuclear installations, including radioactive waste management, spent fuel management and other stages of the fuel cycle; nuclear materials, including their inspection and registration; and the physical protection of nuclear installations and nuclear materials provided for by the relevant licence holder. The ÚJD SR assesses plans for the use of nuclear energy, the quality of classified equipment and nuclear technology devices, and the commitments of the Slovak Republic arising from international treaties on nuclear safety of nuclear installations and nuclear materials management. The ÚJD SR is a legal entity; as a result, the rules for managing funds of a state budgetary organisation apply. The ÚJD SR has a separate chapter in the state budget, it has no subordinate bodies on regional or district levels, and it is not a founder of any state budgetary or state contributory organisation or any other legal entities.");
