INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Health Insurance Institute of Slovenia");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.zzzs.si \">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The Health Insurance Institute of Slovenia is a public institution that provides compulsory health insurance. Compulsory health insurance is covered by contributions of insured persons, employers, the Republic of Slovenia and other stakeholders. The rates of contributions are determined by the Social Security Contributions Act. The groups that are insured by the compulsory health insurance include employees in Slovenia; Slovenian nationals employed in foreign companies; the self-employed; farmers; apprentices; top sportsmen, and top chess players. Compulsory health insurance allows insured persons to claim the right to health services, medicines and medical devices, and to request compensation, such as salary compensation during a temporary absence from work, compensation for fatal accidents or wrongful death and funeral expenses, and reimbursement of travel expenses in connection with exercising the right to health services.");
