INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Directorate-General of Labour Relations and Labour Rights (Direktorat za delovna razmerja in pravice iz dela)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p>See more in its <a href=\"http://www.mddsz.gov.si/en/areas_of_work/labour_relations_and_labour_rights/\" target=\"blank\">website</a> and in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia\" target=\"_blank\">OSHwiki</a>&nbsp;</p>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The main executive body within the Ministry of Labour, Family, Social Affairs and Equal Opportunities is the Directorate-General of Labour Relations and Labour Rights, which encompasses the Health and Safety at Work Section (Sektor za varnost in zdravje pri delu). Other key bodies within the Ministry of Labour, Family, Social Affairs and Equal Opportunities are the Labour Inspectorate (Inšpektorat Republike Slovenije za delo) and the Council for Health and Safety at Work, which is the expert advisory body that considers and adopts views and recommendations on innovation, strategy and the implementation of integrated policies and priorities for OSH.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Ministry of Labour, Family, Social Affairs and Equal Opportunities of the Republic of Slovenia (Ministrstvo Za Delo, Družino, Socialne Zadeve in Enake Možnosti)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.mddsz.gov.si/en/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "Key occupational safety and health (OSH) authorities are located within the Ministry of Labour, Family, Social Affairs and Equal Opportunities of the Republic of Slovenia.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 1, 0, 0, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Spanish Association of Standardisation and Certification (AENOR — Asociación Española de Normalización y Certificación)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.aenor.es/aenor/inicio/home/home.asp\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>AENOR was founded in 1986 as a private, non-profit organisation. AENOR is a member organisation of the International Standardisation Organisation and the European Committee for Standardisation.</p><p>AENOR offers certification for safety and health management systems in accordance with the Occupational Health and Safety Assessment Series 18001 standard. It also monitors legal compliance in companies (e.g. carrying out risk assessments, checking documentation, etc.).</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 0, 1, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Foundation for the Prevention of Occupational Risks (FUNPRL — Fundación para la Prevención de Riesgos Laborales)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.funprl.es/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The FUNPRL is a national body under tripartite governance that grants funding for prevention activities. Representatives of the national government, the autonomous communities, the trade unions and employers’ associations make decisions on the board (patronato) and the bureau (comisión delegada). The foundation is funded in part by the Prevention and Rehabilitation Fund, which draws on MATEPSS management surpluses. Applications for funding can be presented by national, regional or sectoral stakeholders, as well as OSH authorities and social partners&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 1, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Trade Union Institute for Work, Environment and Health (ISTAS — Instituto Sindical de Trabajo, Ambiente y Salud)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.istas.net/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "ISTAS was founded in 1996 by the Spanish trade union Comisiones Obreras (CC.OO), which still supports its work. This non-profit, independent research and education institute was set up as a foundation. The work is supervised by a board of directors, which is responsible for strategic and financial planning. The majority of the members are nominated by the CC.OO.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 1, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "The National Institute for Safety and Hygiene at Work (Instituto nacional de seguridad e higiene en el trabajo, INSHT)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.insht.es/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "<p>The National Institute for Safety and Hygiene at Work (Instituto nacional de seguridad e higiene en el trabajo, INSHT) is the specialized scientific/technical body of the Spanish Government Administration for OSH matters. It was created in 1978 as an autonomous body of the Ministry of Employment and Social Security. It focuses on analysing, promoting, supporting and improving OSH issues. Its main tasks are:</p><ul><li>Technical assistance: providing specialized technical assistance to departments within the Administration, to stakeholders, and to OSH experts from public institutions or private companies. This activity represents approximately 30% of its global resources.</li><li>OSH research: Social research (i.e. working conditions surveys); epidemiological studies based on working accidents; field and laboratory research on chemicals, biological, physical agents and ergonomics. It also promotes and funds research through grants.</li><li>Training: An annual programme of activities.</li><li>Dissemination of information: several specialised publications are issued, e.g. on methods for measuring chemicals, or collections of preventive technical notes, OSH posters, leaflets, etc.</li><li>Guidelines and standardisation: Technical assistance in developing new OSH-related legislation, regulations and guidelines. Standardisation process: INSHT collaborates on technical committees for the formulation of UNE, EN and ISO standards.</li><li>Testing and certification of protective equipment and machinery.</li><li>Secretariat of the National Commision for Safety and Heath at Work: INSHT give technical support and general assistance to the National Commission for Safety and Health at Work.</li><li>National Reference Center for EU-OSHA and EU Institutions, ensuring the coordination and transmission of information on OSH, a national level.</li></ul>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 1, 0, @maxId+1, @maxId+2, @maxId+3);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Social Security Work Accident and Professional Sickness Provident Entities  (MATEPSS — Mutuas de Accidentes de Trabajo y Enfermedades Profesionales de la Seguridad Social)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.amat.es/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain\">OSHwiki</a>");
