INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"https://www.nebosh.org.uk/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_United_Kingdom\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "NEBOSH was formed in 1979 as an independent examining board and awarding (certification) body with charitable status. It offers a comprehensive range of globally recognised, vocationally related qualifications designed to meet the health, safety, environmental and risk management needs of all places of work in both the private and the public sectors. Courses leading to NEBOSH qualifications attract around 35,000 candidates annually and are offered by over 500 course providers, with exams taken in over 100 countries around the world. These qualifications are recognised by the relevant professional membership bodies, such as IOSH and the IRRSM. NEBOSH examinations and assessments are set by its professionally qualified staff who are assisted by external examiners, most of whom are chartered safety and health practitioners operating within industry, the public sector or in enforcement.");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id)  VALUES ("MATRIX_AUTHORITY", @nutsId, 0, 0, 0, 1, @maxId+1, @maxId+2, @maxId+3);
