INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.bsigroup.co.uk/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_United_Kingdom\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The BSI is a national organisation responsible for the development and publication of standards in the United Kingdom. The BSI is a semi-governmental institution with juridical responsibility. Since its foundation in 1901 as the world’s first standardisation body, the BSI has grown into a leading independent business services organisation on a global scale providing standard-based solutions in more than 140 countries. The BSI develops private, national and international standards, certifies management systems and products, provides assessment, certification, and testing of products and services, and provides training and information on standards and international trade. The BSI developed the first standard used for developing occupational health and safety management systems, BS 8800, which went on to become the OHSAS 18000 standard series— the most widely used OSH standard series globally. The BSI also developed the first guidance standard on psychosocial risk management in the workplace — PAS1010.");
