INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "See more in its <a target=\"blank\" href=\"http://www.cieh.org/\">website</a> and in <a target=\"_blank\" href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_United_Kingdom\">OSHwiki</a>");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+3, NULL, NULL, "MATRIX_AUTHORITY_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+3, "EN", 1, "The CIEH is a registered charity and the professional voice for environmental health. Its history can be traced back to the 1840s. The CIEH sets standards, and accredits courses and qualifications for the education of members and other environmental health practitioners. It provides over 50 certification training programmes that include food safety, health and safety, and environmental awareness through a network of over 10,000 registered trainers and 6,000 registered training centres across the United Kingdom. It also provides information, evidence and policy advice to local and national governments and environmental and public health practitioners in the public and private sectors. As an awarding body, the CIEH provides qualifications, events and support materials on topics relevant to health, well-being and safety to develop workplace skills and best practice.");
