INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.emploi.belgique.be/moduleTab.aspx?id=556&amp;idM=127\" target=\"_blank\">Link</a> to the institute</p><p><strong>Short abstract<br /></strong>Belgium does not run a national OSH research institute. Research in the field of health and safety at work is mainly carried out by research groups at universities. The Belgian Directorate-General for the Humanisation of Work runs a directorate, DIRACT-DIOVA, that is responsible for the coordination and promotion of research on the improvement of working conditions.<br /><br />See more in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
