INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://ncpha.government.bg/index.php?lang=en\" target=\"_blank\">Link</a> to the institute</p><p><br /><strong>Short abstract</strong></p><p>&nbsp;</p><p><span style=\"font-size: xx-small;\">The NCPHA is a structure within the national healthcare system. It carries out activities for protecting public health, promoting health and preventing diseases, and provides information on healthcare management.</span></p><p><span style=\"font-size: xx-small;\">In accordance with its main activities for the purpose of protecting and promoting health, the NCPHA examines the population’s health status and the relationship between environmental factors and living conditions; conducts epidemiological surveys and evaluates risk factors for chronic non-communicable diseases; participates in the development, coordination and performance of national programmes and action plans as well as international research programmes oriented to strengthening public health (including mental, reproductive and sexual); carries out intervention activities and training workshops, and publishes information and methodologiical materials&nbsp; ; assesses exposure and the health risk to the population of the impact of biological, chemical and physical hazards on the environment and in the workplace, since appropriate recommendations have been prepared; monitors nutrition and the nutritional status of the population and develops manuals for the nutrition of target population groups.</span></p><p>See more in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
