INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.iphs.eu/n/en/\" target=\"_blank\">Link</a> to the institute</p><p><strong>Short abstract<br /></strong></p><p>The IPHS is an academic centre for theoretical and applied research in different fields of demography and psychology.</p><p>The mission of the IPHS is to carry out demographic and psychological research aimed at studying regularities and determinants of population and individual development. Analyses of reproduction processes and psychological mechanisms for the regulation of behaviour are carried out with the purpose of assisting institutions in the implementation of strategies and policies for social and personal growth in the process of European integration and world globalisation.</p><p>See more in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
