INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.knsb-bg.org/index.php/deinosti1/2015-04-03-15-00-57\" target=\"_blank\">Link</a> to the institute</p><p><strong>Short abstract<br /></strong></p><p><span lang=\"EN-GB\">The main components of ISTUR’s activity are research, consultations and training in the following areas: development of trade unionism and industrial relations — historical development; structures; social partnership; collective bargaining; collective labour disputes; protest actions and strikes; the international trade union movement; labour market and trade union strategies; privatisation; social policy — living standards, prices, income, social protection.</span></p><p>See more in <a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
