INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><strong>Short abstract<br /></strong></p><p>There is no national OSH research institute in Hungary. The Occupational Health Directorate of OKI performs research related to work hygiene, epidemiology, applied occupational physiology — ergonomics — and work psychology. Certain universities deal with research related to occupational safety.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Hungary\" target=\"_blank\">OSHWiki</a></p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
