INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"https://www.inail.it\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>Act No 122 of 30 July 2010 reassigns to INAIL the functions formerly carried out by the National Institute for Prevention and Safety at Work (ISPESL — Istituto Superiore per la Prevenzione e la Sicurezza sul Lavoro) in the field of technical and scientific activities, research, experimentation, control, consultancy, assistance and higher education. (See below&nbsp; the description of the prevention activities of INAIL)&nbsp; &nbsp;</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Italy\" target=\"_blank\">OSHWiki</a>");
