INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"https://www.lu.lv/en/about-us/structure/institutes/ul-institute-of-chemical-physics/\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>Some research activities are also carried out by the Faculty of Chemistry of the University of Latvia. Its recent research includes studies on not only chemical risks but also ergonomic and organisational factors of workplaces.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Latvia\" target=\"_blank\">OSHWiki</a></p>");
