INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.dgsk.st.vgtu.lt/\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>The VGTU — one of the largest state schools of higher education — strives for the leading position in technical and engineering education, as well as in the field of scientific research, among the Baltic countries. The Department of Labour Safety and Fire Protection is involved in educating and training students in the OSH field, carrying out research in the OSH and fire safety field, preparing OSH and fire safety regulations, etc.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania%20\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
