INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><strong>Short abstract<br /></strong></p><p>There is no research institute specialising in OSH issues in Luxembourg. However, LISER&nbsp; &nbsp;(Luxembourg Institute of Socio-Economic Research)&nbsp; &nbsp; is a public institution — overseen by the Ministry of Culture, Higher Education and Research — that is actively involved in the field of research concerning economic and social sciences. One of its activities covers labour relations and employment in a broad sense, as well as issues associated with the labour market.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Luxembourg\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
