INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Netherlands Organisation for Applied Scientific Research (TNO — Nederlandse Organisatie voor Toegepast Natuurwetenschappelijk Onderzoek)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"https://www.tno.nl/en/focus-areas/defence-safety-security/\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p><span style=\"font-size: xx-small;\">The TNO connects people and knowledge to create innovations that boost the competitive strength of industry and the well-being of society in a sustainable way. The TNO is an independent research organisation that focuses on transitions or changes in nine social themes:</span></p><ul><li><span style=\"font-size: xx-small;\">buildings, infrastructure and maritime&nbsp; — ‘Robust constructions, sustainable use’;</span></li><li><span style=\"font-size: xx-small;\">the circular economy and the environment — ‘Directing and accelerating sustainability’;</span></li><li><span style=\"font-size: xx-small;\">defence, safety and security — ‘We’re putting our knowledge and technology to work for safety and security’;</span></li><li><span style=\"font-size: xx-small;\">energy: ‘Faster towards a sustainable energy supply’</span></li><li><span style=\"font-size: xx-small;\">healthy living — ‘Focusing on participation, not on the disease’;</span></li><li><span style=\"font-size: xx-small;\">industry — ‘Innovating for employment, welfare and well-being’;</span></li><li><span style=\"font-size: xx-small;\">information and communication technology (ICT) — ‘Interpreting and accelerating digital transformation’;</span></li><li><span style=\"font-size: xx-small;\">strategic analysis and policy — ‘Turning complex issues into concrete innovations’;</span></li><li><span style=\"font-size: xx-small;\">traffic and transport — ‘Helping to create liveable, sustainable cities’.</span></li></ul><p>&nbsp;</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands\" target=\"_blank\">OSHWiki</a></p>");
