INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"https://rivm.nl/en\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p><span lang=\"EN-US\"><span lang=\"EN-GB\">The RIVM conducts research and provides advice to assist with this task. The </span><span lang=\"EN-GB\">RIVM works to prevent and control outbreaks of infectious diseases. It promotes public health and consumer safety and helps to protect the quality of the environment. The RIVM collects and collates knowledge and information from various sources, at national and international levels. The RIVM applies this knowledge, and places it at the disposal of policy-makers, researchers, regulatory authorities and the general public. Each year, the RIVM produces numerous reports on all aspects of public health, nutrition and diet, health care, disaster management, nature and the environment.</span><br /></span></p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
