INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Netherlands Centre for Occupational Diseases (NCvB)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"https://www.occupationaldiseases.nl/\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>The NCvB registers and reports occupational diseases via the national notification and registration system and a number of specific surveillance projects.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
