INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.imp.sosnowiec.pl/\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>IMPiZŚ is a scientific research centre focused on occupational medicine and environmental health research as well as study, implementation, training, diagnostic and treatment activities. IMPiZŚ has been engaged in promoting public health and disease prevention awareness campaigns</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland%20\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
