INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Institute of Rural Health (Instytut Medycyny Wsi, IMW)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.imw.lublin.pl/\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>The IMW is a scientific research and treatment services institution dedicated to a wide range of rural public health and environmental health issues. The institute’s activities cover the following areas:</p><ul><li>assessing public health risks in rural areas;</li><li>assessing environmental and working conditions in rural areas;</li><li>influencing the rural healthcare policy.<span style=\"text-decoration: underline;\"></span></li></ul><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland%20\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
