INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><strong>Short abstract</strong></p><p>Portugal does not run a national occupational safety and health (OSH) research institute.</p><p>&nbsp;</p><p>Research in the field of safety and health at work is mainly carried out by research groups at universities and technology centres, which often have projects in collaboration with universities. There is a national network that encompasses the eight existing technology centres, promoting cooperation among them.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
