INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "National Research and Development Institute of Occupational Safety ‘Alexandru Darabont’ (INCDPM)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.inpm.ro/\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>In addition to the constant role it plays in OSH prevention, the INCDPM is the national OSH research body, operating under the coordination of the Ministry of National Education as State Authority for Scientific Research, Technological Development and Innovation, in accordance with Government Decision No&nbsp;185 of 16&nbsp;April 2013, and the MMFPSPV.</p><p>&nbsp;</p><p>The INCDPM develops fundamental and applicative research studies that are aimed at improving safety and health at work, in line with the provisions of EU directives transposed to national legislation currently in force. The topics addressed by the INCDPM’s research include risk assessment, studies on the elimination/reduction of major work-related risk factors (chemical and biological risks, mechanical and physical risks, musculoskeletal disorders, psychosocial risks, ergonomics, etc.), personal protective equipment (PPE), and the certification of machines and PPE.</p><p>&nbsp;</p><p>The research that the INCDPM carries out also includes the development of OSH research projects and programmes aimed at improving working conditions within all activity sectors.</p><p>&nbsp;</p><p>The INCDPM’s research and development studies contribute, to a large extent, to the drafting and substantiation of OSH policies drawn up at governmental level by MMFPSPV and the Ministry of National Education, in collaboration with social partners and the most significant OSH promoters at national level.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "National Scientific Research Institute for Labour and Social Protection (INCSMPS)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.incsmps.ro/index.php?lang=romanian\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>This institute was set up in 1990 as a research and development body under the coordination of the MMFPSPV. Government Decisions No 1305/1996 and No 1773/2004 establish its main objectives: carrying out research studies in the areas of human resources management, employment and social protection; increasing the level of expertise in the area of labour and social protection; integrating the activity of the institute into that of the main national and European research and development networks.&nbsp; &nbsp;At present, the INCSMPS also develops its activity under the coordination of the Ministry of National Education (Government Decision No 185 of 16 April 2013).</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
