INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "National Research and Development Institute for Mining Safety and Protection Against Explosion — INCD INSEMEX Petrosani");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.insemex.ro/\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>This institute carries out scientific research and specific prevention activities in the field of OSH and the environment, under the joint coordination of the Ministry of Economy and the Ministry of National Education.</p><p>&nbsp;</p><p>INSEMEX research activity particularly addresses the mining sector and the high risks that workers are exposed to within potentially explosive atmospheres.</p><p>&nbsp;</p><p>The institute develops its activity in the area of OSH related to the mining sector, in close collaboration with the University of Petrosani, which has a long tradition of over 100 years in Romania. The University of Petrosani is a key promoter of OSH education through its organisation of OSH courses for the mining sector at the academic level.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
INSERT INTO matrix_page(page, nuts_id, check_1, check_2, check_3, check_4, text_1_literal_id, text_2_literal_id, text_3_literal_id) VALUES ("MATRIX_STATISTICS", @nutsId, 0, 0, 1, false, @maxId+1, @maxId+2, null);
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+1, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Institute for Labour and Family Research (IVPR — Inštitút pre výskum práce a rodiny)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.ivpr.gov.sk/\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>The IVPR is a state allowance departmental research organisation. Its research is focused on:</p><ul type=\"square\"><li>the management of occupational risk factors;</li><li>the quality of working conditions and the working environment;</li><li>psycho-social aspects of OSH;</li><li>socioeconomic aspects of OSH;</li><li>systems of employees’ individual protection.</li></ul><p>&nbsp;</p><p>The IVPR participates in the coordination of publicly financed research on OSH (projects of the Slovak Research and Development Agency, the Ministry of Education, Science, Research and Sport of the Slovak Republic, and the Ministry of Labour, Social Affairs of the Slovak Republic). The institute has created an agency of institutions that regularly provides information on these institutions’ work and on the OSH research that they carry out.&nbsp;</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia%20\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
