INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Slovak University of Technology in Bratislava — Faculty of Materials Science and Technology (STU-MTF — Materiálovo-technologická fakulta Slovenskej technickej university)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.mtf.stuba.sk/generate_page.php?page_id=1\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>The OSH research of the STU-MTF mainly focuses on:</p><ul><li>the determination of safety characteristics of substances;</li><li>the development of new methods for the assessment of technological risks;</li><li>the assessment of dangerous substances in term of their environmental effect;</li><li>the measurement of working environment agents.</li></ul><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia%20\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
