INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Technical University of Košice — Faculty of Mechanical Engineering (SJF-TUKE — Strojnícka fakulta Technickej univerzity v Košiciach)");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.tuke.sk/wps/portal/tuke/faculties/sjf%20\" target=\"_blank\">Link</a>&nbsp;to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>The OSH research of the SJF-TUKE mainly focuses on:</p><ul><li>the safety of technical systems;</li><li>the management of technical equipment risk and risk simulation;</li><li>OSH management and integrated management systems;</li><li>the risks of major industrial accidents and emergency planning;</li><li>protection from noise effects;</li><li>ergonomic aspects of occupational safety;</li><li>the transporting of dangerous substances;</li><li>safety in maintenance, new methods and approaches.</li></ul><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia%20\" target=\"_blank\">OSHWiki</a></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
