INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+1, "EN", 1, "Clinical Institute of Occupational Medicine, Traffic and Sports");
INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"https://www.kclj.si/index.php?dir=/divisions__%20departments/independent_units/institute_of_occupational%20_traffic_and_sports_medicine%20\" target=\"_blank\">Link&nbsp;</a>to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>The Clinical Institute of Occupational Medicine, Traffic and Sports is a unit of the University Medical Centre of Ljubljana (<em>Univerzitetni klinični center Ljubljana</em>). It is principal authority in the field of OSH in Slovenia. It carries out healthcare activities at the primary, secondary and tertiary levels. Within these activities, the institute provides teaching, performs research and development and carries out professional methodological work in the field of workers’ health. In addition, the institute prepares the professional grounds for drafting legal acts.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia\" target=\"_blank\">OSHWiki</a></p><p><strong>&nbsp;</strong></p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
