INSERT INTO literal (id, chart_id, section_id, type)  VALUES (@maxId+2, NULL, NULL, "MATRIX_STATISTICS_TEXT");
INSERT INTO translation(literal_id, language, is_default, text)  VALUES (@maxId+2, "EN", 1, "<p><a href=\"http://www.kth.se/\" target=\"_blank\">Link&nbsp;</a>to the institute</p><p><br /><strong>Short abstract<br /></strong></p><p>The KTH conducts research into the main fields of environmental sciences: work science and environmental technology. The aim of the research is to achieve practical improvements in work environments and conditions, especially with regard to addressing environmental analyses and the development of strategies and techniques to minimise waste and emissions of contaminating substances.</p><p>See more in&nbsp;<a href=\"https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Sweden\" target=\"_blank\">OSHWiki</a></p><p><strong>&nbsp;</strong></p><p><strong>&nbsp;</strong></p><p>&nbsp;</p><p><strong>&nbsp;</strong></p><p>&nbsp;</p>");
