INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2002, 20180, 'EN', 1, 'Evaluation: a report about the implementation of the strategy has to be provided to the Ministry of Social Security and Labour of the Republic of Lithuania by 30 January every year.<br/>Ex ante indicators for the years 2012 to 2020<br/>Number of prepared requirements, guidances, orders.<br/>Number of developed OiRA tools, Occupational healthcare services provision model for micro and small enterprises. Number of held training. Number of inspections, events concerning mental health carried out. Number of implemented campaigns.<br/>A detailed list of indicators assigned to the tasks/measures are provided in the <a target="_blank" href="https://www.e-tar.lt/portal/lt/legalAct/1c12ca80447711e7b66ae890e1368363">strategy</a>.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2003, 20181, 'EN', 1, '<p>The Action Plan has been prepared in accordance with the provisions of the EU Strategic Framework on Health and Safety at Work 2014-2020.<br />Objective 1 is related to challenge 1of the EU Strategic Framework on Health and Safety at Work<br />Objective 2 is related to challenge 2 EU Strategic Framework on Health and Safety at Work<br />Task 2.3 is related to challenge 3 of the EU Strategic Framework on Health and Safety at Work<br /><br /></p>');
