INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2466, 22094, 'EN', 1, 'The competent authority in OSH matters is the Federal Ministry of Labour, Social Affairs, Health and Consumer Protection. It is responsible for most employment related issues, namely: social security (insurance for pensions and unemployment), industrial relations, labour market policy, labour conditions and inspections, tatistics, OSH, and vocational training.');
