INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2471, 22099, 'EN', 1, 'See more in its <a target="_blank" href="https://www.sozialministerium.at/siteEN/Labour_Disabilities/Occupational_Safety_and_Health/Labour_Inspectorate/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria">OSHwiki</a>.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2472, 22100, 'EN', 1, 'The general Labour Inspectorate (Arbeitsinspektion) is by far the largest of those bodies that have been installed to monitor safety and health of the employees. It covers industries, services, the public sector, and transport - following the July 2012 merger with Inspectorate of Transport. It is centralised and acts nationwide through 16 regional departments and one central office. Newly recruited inspectors follow a two-year training programme, with the emphasis on legislation and general OSH principles. At level of the Federal States, nine regional Labour Inspectorates for Agriculture and Forestry are installed.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2473, 22101, 'EN', 1, 'Austrian Social Insurance for Occupational Risks (Allgemeine Unfallversicherungsanstalt, AUVA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2474, 22102, 'EN', 1, 'See more in its <a target="_blank" href=" https://www.auva.at/">website</a> and in <a target="_blank" href=" https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria ">OSHwiki</a>.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2475, 22103, 'EN', 1, '<p>The Austrian Social Insurance for Occupational Risks is the largest body in Austria. Like the Labour Inspectorate, AUVA is a centralized national organisation. Founded in 1889 as occupational accident liability insurance for employers, AUVA is responsible for the prevention of occupational accidents and diseases, as well as for the compensation of industrial injuries and accidents, and for rehabilitation. It provides services for more than 3 million employees, plus more than 1 million school children and students. These services include advice, training, expert opinion and information, as well as general assistance on OSH questions, as well as working processes, workplace design, individual protection, and implementing rules, regulations and standards.</p><p>Their range of activities includes regional public relations and advertising campaigns, safety training in schools, and ensuring efficient first aid in co-operation with ambulance services. AUVA has a central office in Vienna, four regional offices, and five local offices. It also has six trauma centers and four rehabilitation centers.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2476, 22104, 'EN', 1, 'Social Insurance for the Public Sector (Versicherungsanstalt öffentlich Bediensteter, formerly Beamtenversicherungsanstalt, BVA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2477, 22105, 'EN', 1, 'See more in its <a target="_blank" href="http://bva.at/portal27/bvaportal/content?content-id=10007.676561&viewmode=content ">website</a> and in <a target="_blank" href=" https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria ">OSHwiki</a>.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2478, 22106, 'EN', 1, 'The Social Insurance for the Public Sector was originally the social insurance body (workplace accidents, health insurance and pensions fund) for public servants only. Since 2004 it has become also workplace accident and health insurance body for Austrian university staff. It also insures its own workers and voluntary probation officers against workplace accidents.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2479, 22107, 'EN', 1, 'The Social Insurance for Railway and Mining Industry (Versicherungsanstalt für Eisenbahnen und Bergbau, VAEB)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2480, 22108, 'EN', 1, 'See more in its <a target="_blank" href="https://www.vaeb.at">website</a> and in <a target="_blank" href=" https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2481, 22109, 'EN', 1, 'The Social Insurance for Railway and Mining Industry is the workplace accident insurance body for workers of the railway, public transport and the cable-car sector. The workplace accident insurance of mining workers is, however, provided by AUVA.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
