INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2490, 22118, 'EN', 1, 'Austrian Standards stands for Austrian Standards International (formerly Österreichisches Normungsinstitut), a non-profit service organization (association), as well as for its subsidiary Austrian Standards plus GmbH founded in 2008 and Austrian Standards Operations GmbH founded in 2018, a 100% subsidiary of Austrian Standards plus GmbH');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2491, 22119, 'EN', 1, 'Federal Public Service Employment, Labour and Social Dialogue.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2492, 22120, 'EN', 1, 'See more in its <a target="_blank" href="http://www.emploi.belgique.be">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2493, 22121, 'EN', 1, 'Occupational safety and health (OSH) is a matter within the competence of the Federal Public Service Employment, Labour and Social Dialogue. The main executive agencies are the Directorate General for the Humanization of Work (DG Humanisation du travail – DGHT) and the Directorate General for the Control of the well-being at work (DG Contrôle du bien-être au travail). The Federal Public Service is the responsible administrative body led by the Federal Minister for Work.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2494, 22122, 'EN', 1, 'DG Control of the Well-being at work (inspection services)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2495, 22123, 'EN', 1, 'See more in its <a target="_blank" href="http://www.emploi.belgique.be/defaultTab.aspx?id=6550">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2496, 22124, 'EN', 1, '<p>The Directorate General for the Control of the Well-being at work is organized in three main Divisions: (1) a Division for Regional Control with 8 Regional Directorates, (2) a Division for chemical risks control with three directorates, one for the prevention of major accidents, one for control policies and one for industrial toxicology, and (3) a Division for knowledge management. It covers all topics as defined by the well-being at work law.</p><p>&nbsp;</p><p>The task of the inspection services is to help reduce accidents and health problems in businesses and public services based on the regulations. They ensure that the rules are adhered to and have an advisory, preventive and repressive role. Another of its tasks is indicating all the loopholes in the legislation and helping the authorities to close these.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2497, 22125, 'EN', 1, 'DG Humanization of work');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2498, 22126, 'EN', 1, 'See more in its <a target="_blank" href="http://www.emploi.belgique.be/defaultTab.aspx?id=332">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2499, 22127, 'EN', 1, '<p>The Directorate General for the Control of the Well-being at work is organized in three Divisions: (1) a Division for the preparation of the legislation, (2) a Division for the promotion of the well-being at work and (3) a Division for social consultation.</p><p>&nbsp;</p><p>The Division for the preparation of the legislation draws up the norms regarding well-being on the shop floor and interprets and evaluates these norms. It also deals with preparing these norms on a European and international level and the implementing of the European and international norms into Belgian law.</p><p>&nbsp;</p><p>The Division of promoting well-being at work arranges for promotion and information regarding well-being at work on an international and Belgian level. It develops action programmes on the distribution and use of “good practices” and networks for exchanging information on well-being at work.</p><p>&nbsp;</p><p>There are provincial committees for promoting work in each province with a view to reaching small businesses more effectively. They are tripartite committees comprising representatives of employers, employees and the administration of which the governors of the provinces are the president. They carry out regional information campaigns controlled by the DG Humanization of Work.</p><p>&nbsp;</p><p>The Division for social consultation on well-being at work organizes social consultation among other things, on the level of norms regarding well-being at work. It also manages the procedures for recognition and approval of the external actors involved in well-being at work.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2500, 22128, 'EN', 1, 'Belgian Social Insurance for Occupational Risks');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2501, 22129, 'EN', 1, 'See more in its <a target="_blank" href="https://www.assuralia.be">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2502, 22130, 'EN', 1, 'Industrial accident insurance is in the hands of private insurers. This system is an important aspect of social security given that it is compulsory for each employer. Accident insurance is compulsory for employers in the private sectors, trainees and persons in apprentiship. Self-employed are exempted from the compulsory system. <p>Industrial accident insurance is in the hands of private insurers. This system is an important aspect of social security given that it is compulsory for each employer. Accident insurance is compulsory for employers in the private sectors, trainees and persons in apprentiship. Self-employed are exempted from the compulsory system. There exist special regulations for the public sector.</p><p>Most insurance institutions have set up a prevention service; its task is to stimulate the prevention of industrial accidents in affiliated businesses. The activities of these prevention services are threefold: Assisting in working out and applying a prevention policy in the affiliated businesses, study and analysis of industrial accidents, study and assessment of the risks, taking action in the areas of information, training and promotion aimed at employees and employers.</p><p>Assuralia is the professional association of insurance companies and represents the Belgian and foreign insurance companies operating on the Belgian market.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2503, 22131, 'EN', 1, 'Federal Agency of professional risks (FEDRIS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2504, 22132, 'EN', 1, 'See more in its <a target="_blank" href="https://www.fedris.be/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2505, 22133, 'EN', 1, '<p>The remuneration of occupational illnesses is in the hands of Fedris (Federal Agency of professional risks), the former Occupational Illness Fund (Fonds des Maladies Professionnelles). This is a state-controlled institution managed by a management committee in which the social partners are represented. Fedris forms part of the social security, which means that employers are obliged to spend a certain percentage of the total wage sum on financing Fedris. Fedris is under the guardianship of the FPS Social Security.</p><p>Since january 2017, the former Occupational Illness Fund and the former Accidents at Work Fund were merged in a new institution called Fedris, the Federal Agency of professional risks.\'</p><p>The insurance premiums are in relation to the compensation paid to the victims of industrial accidents that have taken place in recent years. The premiums are therefore related to the frequency of the accidents in the company.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2506, 22134, 'EN', 1, 'Constructiv');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2507, 22135, 'EN', 1, 'See more in its <a target="_blank" href="http://www.constructiv.be">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2508, 22136, 'EN', 1, 'Constructiv is a private service organisation for the construction industry. It is jointly managed by employers and construction workers. It provides information, advice and assistance on the prevention of occupational accidents and illnesses for employers and employees in this sector.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2509, 22137, 'EN', 1, 'Prevent');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2510, 22138, 'EN', 1, 'See more in its <a target="_blank" href="https://www.prevent.be/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2511, 22139, 'EN', 1, 'Prevent is a multidisciplinary and private institute, which promotes the quality of working conditions. The institute is active in research and supports, advises and informs companies and institutions as well as insurance companies, external prevention services, professional associations, the social partners, authorities and other stakeholders in society.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2512, 22140, 'EN', 1, 'Provinciaal Veiligheidsinstituut Antwerpen (PVI');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2513, 22141, 'EN', 1, 'See more in its <a target="_blank" href="https://www.provincieantwerpen.be/aanbod/doe/pvi.html">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2514, 22142, 'EN', 1, 'PVI is a public institution funded by the Province of Antwerp. The institute provides information and guidance concerning prevention, protection and well-being at work and in the private sphere.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2515, 22143, 'EN', 1, 'Prevention & Interim (P&I)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2516, 22144, 'EN', 1, 'See more in its <a target="_blank" href="http://www.p-i.be/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2517, 22145, 'EN', 1, 'For the sector of the temporary employment agencies, a Central Prevention Service P&I (Service Central de Prévention pour le Secteur du Travail Intérimaire, Prévention et Interim) [50] has been created in 1998 by national collective agreement. It is jointly managed by the employers organization Federgon and the unions representing the employees in the sector of the temporary employment agencies. The goal of this non-profit OHS-service is to reduce the degree of working accidents in the sector by enhancing the amount of prevention campaigns and health and safety investigations in the sector.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2518, 22146, 'EN', 1, 'NBN – Bureau de Normalisation');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2519, 22147, 'EN', 1, 'See more in its <a target="_blank" href="https://www.nbn.be">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2520, 22148, 'EN', 1, 'The NBN – Bureau de Normalisation is the Belgian organization responsible for the development and publication of standards in Belgium. NBN is a semi-governmental institution with juridical responsibility. The NBN represents Belgium at European level, as member of the European Standardization Committee (CEN) and at worldwide level as a member of the International Standardization Organization (ISO).');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2521, 22149, 'EN', 1, 'The Ministry of Labour and Social Policy (MLSP)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2522, 22150, 'EN', 1, 'See more in its <a target="_blank" href="https://www.mlsp.government.bg/index.php?lang=_eng">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2523, 22151, 'EN', 1, '<p>The MLSP is part of the Bulgarian Council of Ministers (the executive state body).MLSP is in charge with developing, coordinating and implementing the policy on labour, professional qualification, income and living standard, industrial relations, health and safety at work, social security and social assistance.</p><p>According to the Health and Safety at Work Law the tasks of MLSP in the field of health and safety at work are:</p><ol><li>jointly with the Ministry of Health to analyze the status, trends and problems in providing healthy and safe working conditions and to propose measures for its improvement;</li><li>alone or with other ministries issues regulations on the provision of health and safety, organizes and coordinates the development of legislation in this area and establishes rules to ensure safe and healthy working conditions;</li><li>carries out an integrated control trough the Executive Agency "General Labour Inspectorate" of legislative compliance and fulfillment of obligations to ensure healthy and safe working conditions in all sectors and activities regardless of the form of ownership;</li></ol><p>establishes the terms and requirements for training, measurements and consulting in the field of safety at work;</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2524, 22152, 'EN', 1, 'General Labour Inspectorate (???????????? ??????? "?????? ????????? ?? ?????" - ?? "???")');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2525, 22153, 'EN', 1, 'See more in its <a target="_blank" href="http://www.gli.government.bg/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2526, 22154, 'EN', 1, 'General Labour Inspectorate Executive Agency – GLI EA is subordinated to MLSP. It is in charge with monitoring legal compliance and reporting legislative deficiencies to MLSP. It provides information and technical advice to both employers and employees. GLI EA has Labour Inspection offices in the administrative regions of Bulgaria.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2527, 22155, 'EN', 1, 'The Ministry of Health (???????????? ?? ????????????????)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2528, 22156, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2529, 22157, 'EN', 1, 'The Ministry of Health leads specific activities related to health protection, cooperates with MLSP in elaborating laws and norms and coordinates occupational health services. Jointly with MLSP it conducts annual analyses of working conditions and proposes measures.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2530, 22158, 'EN', 1, 'National Social Security Institute (NSSI):');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2531, 22159, 'EN', 1, 'See more in its <a target="_blank" href="http://www.noi.bg/en/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2532, 22160, 'EN', 1, '<p>Bulgarian social security contributions are managed by the National Social Security Institute. According to HSWL employees must have compulsory social security contribution for work accidents and occupational diseases paid by the employer, which is part of the social security system.</p><p>In addition, employees engaged in work where there is danger to life and health, must be insured for the risk "accident" by the employer under the terms and procedures established by the Council of Ministers. In determining the terms and conditions of this insurance the economic activity of the enterprise and the national average level of the coefficients for frequency and severity of accidents is considered. The sectors are defined yearly (MLSP) and usually include branches like construction, wood processing, mining and machine building that have accidents’ rates higher than the national average.</p><p>There are also differences for the pension contribution of employees that worked in hazardous conditions. Those employed under the conditions of first and second labour category have mandatory contributions in a supplementary professional pensions fund. Activities under the first and second labor categories are presented in the Ordinance for Labor Categorization upon Retirement[35]. The categories are assigned according to the nature and specific conditions of work.</p><p>The contribution is 12%, for first category and 7% for the second category. These contributions are made exclusively at the employer’s expense[36] and cover early retirement of workers.&nbsp;</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2533, 22161, 'EN', 1, 'National Revenue Agency');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2534, 22162, 'EN', 1, 'See more in its <a target="_blank" href="http://www.nap.bg/en/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2535, 22163, 'EN', 1, 'Since 2006 notifications about labor contracts, declarations about insured persons, and returns about social security contributions due are also filed at the offices of the National Revenue Agency. Also at the Agency are filed the returns about the health insurance contributions for persons insured by the state budget, as well as the returns for social security and health insurance contributions paid in advance.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2536, 22164, 'EN', 1, 'National Centre of Public Health and Analyses\r\nDepartment');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2537, 22165, 'EN', 1, 'See more in its <a target="_blank" href="http://hp-whp.ncphp.government.bg/index.php?page=en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2538, 22166, 'EN', 1, 'The National network of health and safety promoting companies (NNHSPC) was designed as a social network – organization and actions for mobilization and facilitation of the social support for activities focused on promotion of health and safety at work. The National Centre of Public Health and Analyses NCPHA (formerly the National Centre of Public Health Protection NCPHP) in Sofia, Bulgaria, incorporates units with 50 years of experience in the field. Its main activities include research and development, expert consultancy, methodological and training activities in the area of public health protection, assessment of health risks due to occupational and environmental factors, personal behaviour and lifestyle, health promotion and integral diseases prevention');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2539, 22167, 'EN', 1, 'Bulgarian Institute for Standardization (BDS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2540, 22168, 'EN', 1, 'See more in its <a target="_blank" href="http://www.bds-bg.org/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2541, 22169, 'EN', 1, 'The Bulgarian Institute for Standardization (BDS) is the national executive body for standardization in the Republic of Bulgaria. \r\nBDS develops, accepts and approves Bulgarian standards, participates in the work of international and European organizations for standardization, as its main target is to defend the Bulgarian interests in that sphere.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2542, 22170, 'EN', 1, 'Ministry of Labour and Pension System');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2543, 22171, 'EN', 1, 'See more in its <a target="_blank" href="http://www.mrms.hr ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_National_Level_-_Croatia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2544, 22172, 'EN', 1, 'The mandate of the Ministry of Labour and Pension System includes drafting legislation, monitoring OSH, making suggestions for campaigns to raise awareness, and organizing activities to promote OSH.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2545, 22173, 'EN', 1, 'The State Labour Inspectorate');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2546, 22174, 'EN', 1, 'See more in its <a target="_blank" href="http://www.inspektorat.hr/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_National_Level_-_Croatia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2547, 22175, 'EN', 1, 'The State Labour Inspectorate is a government organization. It is responsible for enforcing OSH legislation and performing inspections. There are 42 branches in five headquarters, corresponding to the administrative centres of Osijek, Rijeka, Split, Varazdin, and Zagreb. Each has an OSH Department and a Labour Department. OSH inspectors are in charge of first degree inspections in trade and crafts, mining, the energy sector, vessels under pressure, catering and tourism. Inspectors working in OSH Departments are tasked with the second degree procedures.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2548, 22176, 'EN', 1, 'Croatian Institute for Health Insurance');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2549, 22177, 'EN', 1, 'See more in its <a target="_blank" href="http://www.hzzo-net.hr/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_National_Level_-_Croatia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2550, 22178, 'EN', 1, 'The Croatian Institute for Health Insurance (Hrvatski zavod za zdravstveno osiguranje) is the administrative body for general health insurance, including industrial accident insurance under the ministerial responsibility of the Ministry of Health. From 2006-2010, the Croatian Institute for Occupational Health Insurance was responsible for the allocation of funds for prevention activities, diagnosis and treatment for occupational diseases and accidents, and sick leave compensation. These tasks have been performed by the Croatian Institute for Health Insurance since 2011.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2551, 22179, 'EN', 1, 'Croatian Pension Insurance Institute');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2552, 22180, 'EN', 1, 'See more in its <a target="_blank" href="http://www.mirovinsko.hr/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_National_Level_-_Croatia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2553, 22181, 'EN', 1, 'The Croatian Pension Insurance Institute is a public institution established on the basis of the Pension Insurance Act which at the same time designated the commencement of the pension system reform aimed to providing for the mandatory pension insurance entitlements to the employees, farmers, craftsmen and others. The Institute is a legal successor of the former republic pension and invalidity insurance funds of workers, self-employed persons and farmers i.e. of all previous institutions.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2554, 22182, 'EN', 1, 'Croatian Institute for Health Protection and Safety at Work');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2555, 22183, 'EN', 1, 'See more in its <a target="_blank" href="http://www.hzzzsr.hr/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_National_Level_-_Croatia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2556, 22184, 'EN', 1, 'The Croatian Institute for Health Protection and Safety at Work (Hrvatski zavod za zaštitu zdravlja i sigurnost na radu) is a public institute under the ministerial responsibility of the Ministry of Health, responsible for implementing relevant legislation. Its main objective is to improve the quality of occupational health for all Croatian employees. To this end, CIOH is involved in defining measures for health protection, developing standards and working methods, as well as providing advice, supervision and training on occupational health protection. CIOH is the body responsible for maintaining registers and databases, (Reporting and monitoring occupational accidents and diseases in Europe | and collecting and analysing data on working conditions and occupational accidents and diseases)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2557, 22185, 'EN', 1, 'Croatian Standards Institute');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2558, 22186, 'EN', 1, 'See more in its <a target="_blank" href="http://www.hzn.hr/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_National_Level_-_Croatia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2559, 22187, 'EN', 1, 'The Croatian Standards Institute, the national standards body of the Republic of Croatia, is an autonomous non-profit public institution. It pursues the following objectives related to standardisation and certification:\r\n•	increasing the safety level of products and processes\r\n•	protecting human health, lives and the environment\r\n•	promoting the quality of products, processes and services\r\n•	ensuring the appropriate use of work, materials and energy\r\n•	improving production efficiency\r\n•	controlling variety, ensuring compatibility and interchangeability\r\n•	removing technical barriers to international trade.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2560, 22188, 'EN', 1, 'Department of Labour Inspection');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2561, 22189, 'EN', 1, 'See more in its <a target="_blank" href="http://www.mlsi.gov.cy/mlsi/mlsi.nsf/mlsi16_gr/mlsi16_gr?OpenDocument">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Cyprus ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2562, 22190, 'EN', 1, '<p>The Department of Labour Inspection (Labour Inspectorate) belongs to the Ministry of Labour, Wellfare and Social Insurance and is responsible for safeguarding adequate levels of safety and health at work. The Department consists of one area and five sectors:</p><p>a) The Safety and Health at Work Area b) The Machinery and Equipment Sector c) The Industrial Pollution and Control Sector d) The Air Quality Sector e) The Radiation Protection Sector f) The Chemical Substances Sector</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2563, 22191, 'EN', 1, 'The Department of Social Insurance Services');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2564, 22192, 'EN', 1, 'See more in its <a target="_blank" href="http://www.mlsi.gov.cy/mlsi/mlsi.nsf/mlsi13_gr/mlsi13_gr?OpenDocument">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Cyprus ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2565, 22193, 'EN', 1, 'The Department of Social Insurance Services of the Ministry of Labour, Wellfare and Social Insurance is responsible for the administration of the Social Insurance Law (Law 41 of 1980-2007 and its Regulations). Based on the above legislation, a Social Insurance Scheme was put into operation in 1980. Law 41 covers “employment accidents” and “occupational diseases” and provides injury benefits (temporary incapacities), disablement benefits and death benefits.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2566, 22194, 'EN', 1, 'Cyprus Safety and Health Association (CySHA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2567, 22195, 'EN', 1, 'See more in its <a target="_blank" href="http://www.cysha.org.cy">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Cyprus ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2568, 22196, 'EN', 1, 'The Cyprus Safety and Health Association (CySHA) was founded in 1991. Its objective is to contribute and participate in efforts to promote health and safety at work and prevent risks. It has more than two hundred and fifty members. Most of them come from the private sector but many Labour Inspectors are also its members. From 2003 CySHA is an active member of the European Network of Safety and Health Professional Organisations (ENSHPO), which deals with the mutual recognition of qualifications in health and safety in Europe.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2569, 22197, 'EN', 1, 'The Cyprus Organization for Standardization (CYS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2570, 22198, 'EN', 1, 'See more in its <a target="_blank" href="http://www.cys.org.cy">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Cyprus ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2571, 22199, 'EN', 1, 'The Standardization, Accreditation and Technical Information Law (156(I)/2002) has allocated standardization activities to the Cyprus Certification Company which was renamed the Cyprus Organization for Standardization (CYS). It is the national standardization body and it participates in the European and international standardization process. “Mirror Committee 04” follows international activities in the health and safety sector');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2572, 22200, 'EN', 1, 'Ministry of Labour and Social Affairs (Ministerstvo práce a sociálních v?cí)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2573, 22201, 'EN', 1, 'See more in its <a target="_blank" href="http://www.mpsv.cz/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2574, 22202, 'EN', 1, 'In the Czech Republic occupational safety and occupational health has traditionally two distinct features. While the Ministry of Labour and Social Affairs (Ministerstvo práce a sociálních v?cí) is responsible for occupational safety and labour inspection, the Ministry of Health (Ministerstvo zdravotnictví) is competent for public health that includes occupational health and is in charge of occupational health inspection. Although this distinction is also followed by the executive agencies of the two ministries, they co-operate closely on international, national and regional levels in the field of OSH. The systems of administrative authorities are run from state budget. Both are authorized to impose sanctions if they find severe deviations during inspection activities.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2575, 22203, 'EN', 1, 'Ministry of Health (Ministerstvo zdravotnictví)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2576, 22204, 'EN', 1, 'See more in its <a target="_blank" href="http://www.mzcr.cz/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2577, 22205, 'EN', 1, 'In the Czech Republic occupational safety and occupational health has traditionally two distinct features. While the Ministry of Labour and Social Affairs (Ministerstvo práce a sociálních v?cí) is responsible for occupational safety and labour inspection, the Ministry of Health (Ministerstvo zdravotnictví) is competent for public health that includes occupational health and is in charge of occupational health inspection. Although this distinction is also followed by the executive agencies of the two ministries, they co-operate closely on international, national and regional levels in the field of OSH. The systems of administrative authorities are run from state budget. Both are authorized to impose sanctions if they find severe deviations during inspection activities.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2578, 22206, 'EN', 1, 'State Labour Inspection Office (Státní ú?ad inspekce práce – SÚIP)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2579, 22207, 'EN', 1, 'See more in its <a target="_blank" href="http://www.suip.cz/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2580, 22208, 'EN', 1, 'Labour inspectorates were established as a separate authority in 2005 by Act No. 251/2005 Coll. on Labour Inspection, based on the predecessor Czech Office of Labour Safety. The State Labour Inspection Office and its eight subordinated Regional Labour Inspectorates are concerned with the inspection of compliance with occupational safety and health protection, as well as the control of protection of labour relations and working conditions. Employers and employees can make advantage of the seminars and individual consultancy services provided by the central headquarters and the regional inspectorates.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2581, 22209, 'EN', 1, 'The Czech Insurance Company (?eská pojiš?ovna)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2582, 22210, 'EN', 1, 'See more in its <a target="_blank" href="http://www.ceskapojistovna.cz/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2583, 22211, 'EN', 1, '<p>Currently Decree No. 125/1993 assigns two private insurance companies that provide accident insurance:</p><ol><li>The Czech Insurance Company (?eská pojiš?ovna)</li><li>Kooperativa Insurance Company (Kooperativa pojiš?ovna)</li></ol><p>The system does not cover self-employed.</p><p>Their activity does not extend to prevention.</p><p>In the case of ?eská pojiš?ovna, those employers who have been covered by this insurance contract with ?eská pojiš?ovna until 31 December 1992 are insured, and employers are also insured to whom the rights and obligations of the employment relationship are transferred to the legal successor of these employers. All other employers are insured with Kooperativa pojiš?ovny.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2584, 22212, 'EN', 1, 'Kooperativa Insurance Company (Kooperativa pojiš?ovna)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2585, 22213, 'EN', 1, 'See more in its <a target="_blank" href="http://www.koop.cz/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2586, 22214, 'EN', 1, '<p>Currently Decree No. 125/1993 assigns two private insurance companies that provide accident insurance:</p><ol><li>The Czech Insurance Company (?eská pojiš?ovna)</li><li>Kooperativa Insurance Company (Kooperativa pojiš?ovna)</li></ol><p>The system does not cover self-employed.</p><p>Their activity does not extend to prevention.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2587, 22215, 'EN', 1, 'Centre of Occupational Health (Centrum hygieny práce a pracovního léka?ství - CHPPL)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2588, 22216, 'EN', 1, 'See more in its <a target="_blank" href="http://www.szu.cz/centrum-hygieny-prace-a-pracovniho-lekarstvi">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2589, 22217, 'EN', 1, 'The mission of the Centre is to study the effects of working conditions and occupational risks on health of workers and to act as an expert body for the Ministry of Health in the field of occupational health. It does research, provides expert background for legislation and maintains international co-operation, including the areas of health effects of work and working conditions, prevention, health promotion, diagnosis, therapy and medico-legal aspects of occupational and/or other work-related diseases. The Centre is an organizational unit of the National Institute of Public Health (Státní zdravotní ústav - SZÚ).');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2590, 22218, 'EN', 1, 'The Occupational Safety Research Institute (Výzkumný ústav bezpe?nosti práce – VÚBP)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2591, 22219, 'EN', 1, 'See more in its <a target="_blank" href="http://www.vubp.cz/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2592, 22220, 'EN', 1, 'The Occupational Safety Research Institute (VÚBP) is a public research institution founded by the Ministry of Labour and Social Affairs of the Czech Republic. One of the key activities of VÚBP is the application of methods and means in the area of risk prevention relating to health and safety at work. The institute also focuses on education and promotion of OSH, and is a consultation centre for OSH expertise.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2593, 22221, 'EN', 1, 'Czech Office for Standards, Metrology and Testing (Ú?ad pro technickou normalizaci, metrologii a státní zkušebnictví - ÚNMZ)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2594, 22222, 'EN', 1, 'See more in its <a target="_blank" href="http://www.unmz.cz/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2595, 22223, 'EN', 1, 'The Czech Office for Standards, Metrology and Testing (Ú?ad pro technickou normalizaci, metrologii a státní zkušebnictví – ÚNMZ), which was established by Act No. 20/1993 Coll. on the Organisation of the State Administration in the Field of Standards, Metrology and Testing, is a state administration body responsible to the Ministry of Industry and Trade. The competency of the office covers technical standardization, metrology, testing, harmonization of technical regulations and ensuring development and publication of Czech standards.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2596, 22224, 'EN', 1, 'The Danish Working Environment Authority (DWEA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2597, 22225, 'EN', 1, 'See more in its <a target="_blank" href="http://engelsk.arbejdstilsynet.dk/en  ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Denmark ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2598, 22226, 'EN', 1, '<p>The Danish Working Environment Authority (DWEA / Danish: Arbejdstilsynet - abbreviated AT) is an agency that works under the auspices of the Ministry of Employment at national and regional level, contributing to the creation of safe working conditions. It is empowered with supervision and enforcement rights, which are commonly designated to labour inspectorates in other countries. The three main fields of activity are: Carrying out inspections in companies. Drawing up OSH rules. Providing OSH information. The DWEA has the authority to penalise companies which do not comply with the working environment rules. They can also issue administrative fines for clear violations of the Working Environment Act, and in cases of extreme danger, they may also order work to be suspended. The DWEA is managed by an Administrative Board made up of the Director-General, 2 Deputy Directors and three Inspection Directors. The headquarters and two of the four centres are located in Copenhagen. The regional Inspection Centres with approximately 100 employees each. The Working Environment Authority is responsible for the inspection of OSH conditions in companies. This includes verifying that a written evaluation has been carried out, specific to that particular workplace. This is equivalent to a risk assessment in the terminology of the EU-OSH Directives. If the company’s working environment and the health and safety policies are found to be in good order, no reason is seen for a new inspection in the near future. However, if problems are found, the company will be visited within a period of 12-18 months.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2599, 22227, 'EN', 1, 'Labour Market Insurance AES');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2600, 22228, 'EN', 1, 'See more in its <a target="_blank" href="https://www.aes.dk/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Denmark ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2601, 22229, 'EN', 1, '<p>Labour Market Insurance (Arbejdsmarkedets Erhvervssikring – AES) is an independent institution administrated by the Danish Labour Market Supplementary Pension Scheme (Arbejdsmarkedets Tillægspension – ATP).</p><p>AES follows the applicable regulations in determining whether an injury or illness can be classified as an industrial injury. When an injury or illness can be recognised as an industrial injury, AES then decides the amount of compensation payable.</p><p>AES funds and pays compensation for recognised occupational diseases. It is mandatory for all private and public sector employers in Denmark to contribute to AES.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2602, 22230, 'EN', 1, 'NONE');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2603, 22231, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Denmark ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2604, 22232, 'EN', 1, '<p>Labour Market Insurance (Arbejdsmarkedets Erhvervssikring – AES) is an independent institution administrated by the Danish Labour Market Supplementary Pension Scheme (Arbejdsmarkedets Tillægspension – ATP).</p><p>AES follows the applicable regulations in determining whether an injury or illness can be classified as an industrial injury. When an injury or illness can be recognised as an industrial injury, AES then decides the amount of compensation payable.</p><p>AES funds and pays compensation for recognised occupational diseases. It is mandatory for all private and public sector employers in Denmark to contribute to AES.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2605, 22233, 'EN', 1, 'Danish Standards (Dansk Standard)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2606, 22234, 'EN', 1, 'See more in its <a target="_blank" href="https://www.ds.dk/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Denmark ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2607, 22235, 'EN', 1, 'Danish standards develops and publishes standards, provides trainings and offers consultancy. Certification or accreditation by a recognised body are used to ensure that national standards correspond with training.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2608, 22236, 'EN', 1, 'Ministry of Social Affairs (Sotsiaalministeerium)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2609, 22237, 'EN', 1, 'See more in its <a target="_blank" href="http://www.sm.ee/et">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2610, 22238, 'EN', 1, '<p>In the Estonian occupational health and safety system, the Ministry of Social Affairs is the executive authority which regulates the entire field, whereas two of its structural units (the Working Life Development Department, and the Health Department) are directly involved in occupational health and safety-related policy-making.&nbsp;</p><p>The Working Life Development Department is responsible for the overall co-ordination and management of the activities in the field of occupational health and safety. The Working Life Development Department:</p><ul type="square"><li>Plans and implements the working environment policy;&nbsp;</li><li>Elaborates regulations on occupational health and safety.</li></ul>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2611, 22239, 'EN', 1, 'Health Board (Terviseamet)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2612, 22240, 'EN', 1, 'See more in its <a target="_blank" href="http://www.terviseamet.ee/en/information/about-hb.html">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2613, 22241, 'EN', 1, '<p>Three Estonian governmental health authorities - Health Protection Inspectorate, Health Care Board and Chemicals Notification Centre are unified in the joint Health Boardin 2010.&nbsp;</p><p>In the field of occupational health the Health Board\'s role is to:</p><ul type="square"><li>Participate in the preparation of occupational health programmes and organize their implementation;</li><li>Analyze information concerning occupational illnesses and illness related to the work of *Organize refresher courses for occupational health specialists;</li><li>Register occupational health service providers.</li></ul>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2614, 22242, 'EN', 1, 'The Labour Inspectorate (Tööinspektsioon)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2615, 22243, 'EN', 1, 'See more in its <a target="_blank" href="http://www.ti.ee/index.php?page=3& \r\r\n">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2616, 22244, 'EN', 1, '<p>The Labour Inspectorateis a government agency operating under the jurisdiction of the Ministry of Social Affairs, and its main functions are to:</p><ul type="square"><li>Arrange for the exercise of state supervision in the working environment with respect to compliance with the requirements of legislation regulating occupational health and safety and labour relations and to apply enforcement by the state on the bases and to the extent prescribed by law;&nbsp;</li><li>Exercise market supervision over safety of the personal protective equipment in the use in the working environment and to ensure the protective properties of the equipment at sites of manufacturing and sale;&nbsp;</li><li>Exercise supervision over investigations into occupational accidents and diseases and over the implementation of measures for the prevention of occupational accidents and diseases;&nbsp;</li><li>Investigate fatal and if there is a need, then conduct an analysis of the causes of serious occupational accidents and diseases and analyze their causes;&nbsp;</li><li>Collect statistics of accidents at work and conduct analysis thereof;&nbsp;</li><li>Exercise supervision over the use of genetically modified micro-organisms in closed environments to the extent prescribed by law;&nbsp;</li><li>Take decisions in cases prescribed by law on issuing or withholding approval;&nbsp;</li><li>Carry out administrative proceedings; approve administrative acts and rules;&nbsp;</li><li>Carry out extrajudicial proceeding of misdemeanours to the extent prescribed by law;&nbsp;</li><li>Initiate criminal proceedings and carry out urgent investigative actions;&nbsp;</li><li>Carry out and resolve petitions from individuals on working environment issues;&nbsp;</li><li>Resolve individual labour disputes pursuant to the procedures prescribed by law.</li></ul>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2617, 22245, 'EN', 1, 'The Estonian Health Insurance Fund (Eesti haigekassa)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2618, 22246, 'EN', 1, 'See more in its <a target="_blank" href="http://www.haigekassa.ee/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2619, 22247, 'EN', 1, '<p>The basic principle followed in Estonia is that there is no specific insurance against employment injuries and occupational diseases. These risks are covered by the health insurance (short-term benefits) and pension insurance (long-term benefits). Under the employer\'s civil liability, additional benefits can be provided as compensation. All employees are the beneficiaries of the system.</p><p>Estonia has no formal system of state insurance for occupational accidents and diseases even though there has been significant developments and improvements of occupational health and safety legislation has taken place in the past few years.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2620, 22248, 'EN', 1, 'NONE');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2621, 22249, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2622, 22250, 'EN', 1, 'In Estonia, there are no prevention institutes as unlike the situation in some European countries. However, preventive activities are included and implemented, in the work of the Ministry of Social Affairs and the Labour Inspectorate most commonly through awareness raising campaigns.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2623, 22251, 'EN', 1, 'Estonian Centre for Standardization (EVS) (Eesti standardikeskus)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2624, 22252, 'EN', 1, 'See more in its <a target="_blank" href="http://www.evs.ee/Esileht/tabid/111/language/en-US/Default.aspx">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2625, 22253, 'EN', 1, 'The Estonian Centre for Standardization (EVS) is a non-profit association, which was established by three founding members (Republic of Estonia, Chamber of Commerce and Industry, Confederation of Employers and Industry) in 1999. The Estonian Centre for Standardization operates as the Estonian standard organization pursuant to the Technical Regulations and Standards Act. EVS has accepted the WTO Code of Good Practice for the Preparation, Adoption and Application of Standards for the removal of technical barriers to trade.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2626, 22254, 'EN', 1, 'European Commission');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2627, 22255, 'EN', 1, 'See more in its <a target="_blank" href="http://ec.europa.eu/social/main.jsp?langId=en&catId=656">website</a> and in <a target="_blank" href="https://www.eurofound.europa.eu/country/eu-level">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2628, 22256, 'EN', 1, 'The European Commission is the EU’s politically independent executive arm. It is responsible for drawing up proposals for European legislation, and it implements the decisions of the European Parliament and of the Council of the European Union. It is organised into directorate-generals (DGs). DG Employment, Social Affairs and Inclusion is the main DG responsible for employment, social dialogue and working conditions.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2629, 22257, 'EN', 1, 'Federal Ministry of Labour, Social Affairs, Health and Consumer Protection (Bundesministerium für Arbeit, Soziales, Gesundheit und Konsumentenschutz)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2630, 22258, 'EN', 1, 'See more in its <a target="_blank" href=" https://www.sozialministerium.at/siteEN/ ">website</a> and in <a target="_blank" href=" https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria ">OSHwiki</a>.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2631, 22259, 'EN', 1, 'The competent authority in OSH matters is the Federal Ministry of Labour, Social Affairs, Health and Consumer Protection. It is responsible for most employment related issues, namely: social security (insurance for pensions and unemployment), industrial relations, labour market policy, labour conditions and inspections, tatistics, OSH, and vocational training.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2632, 22260, 'EN', 1, 'Labour inspectorate (Arbeitsinpektion)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2633, 22261, 'EN', 1, 'See more in its <a target="_blank" href="https://www.sozialministerium.at/siteEN/Labour_Disabilities/Occupational_Safety_and_Health/Labour_Inspectorate/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria">OSHwiki</a>.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2634, 22262, 'EN', 1, 'The general Labour Inspectorate (Arbeitsinspektion) is by far the largest of those bodies that have been installed to monitor safety and health of the employees. It covers industries, services, the public sector, and transport - following the July 2012 merger with Inspectorate of Transport. It is centralised and acts nationwide through 16 regional departments and one central office. Newly recruited inspectors follow a two-year training programme, with the emphasis on legislation and general OSH principles. At level of the Federal States, nine regional Labour Inspectorates for Agriculture and Forestry are installed.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2635, 22263, 'EN', 1, 'Austrian Social Insurance for Occupational Risks (Allgemeine Unfallversicherungsanstalt, AUVA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2636, 22264, 'EN', 1, 'See more in its <a target="_blank" href=" https://www.auva.at/">website</a> and in <a target="_blank" href=" https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria ">OSHwiki</a>.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2637, 22265, 'EN', 1, '<p>The Austrian Social Insurance for Occupational Risks is the largest body in Austria. Like the Labour Inspectorate, AUVA is a centralized national organisation. Founded in 1889 as occupational accident liability insurance for employers, AUVA is responsible for the prevention of occupational accidents and diseases, as well as for the compensation of industrial injuries and accidents, and for rehabilitation. It provides services for more than 3 million employees, plus more than 1 million school children and students. These services include advice, training, expert opinion and information, as well as general assistance on OSH questions, as well as working processes, workplace design, individual protection, and implementing rules, regulations and standards.</p><p>Their range of activities includes regional public relations and advertising campaigns, safety training in schools, and ensuring efficient first aid in co-operation with ambulance services. AUVA has a central office in Vienna, four regional offices, and five local offices. It also has six trauma centers and four rehabilitation centers.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2638, 22266, 'EN', 1, 'Social Insurance for the Public Sector (Versicherungsanstalt öffentlich Bediensteter, formerly Beamtenversicherungsanstalt, BVA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2639, 22267, 'EN', 1, 'See more in its <a target="_blank" href="http://bva.at/portal27/bvaportal/content?content-id=10007.676561&viewmode=content ">website</a> and in <a target="_blank" href=" https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria ">OSHwiki</a>.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2640, 22268, 'EN', 1, 'The Social Insurance for the Public Sector was originally the social insurance body (workplace accidents, health insurance and pensions fund) for public servants only. Since 2004 it has become also workplace accident and health insurance body for Austrian university staff. It also insures its own workers and voluntary probation officers against workplace accidents.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2641, 22269, 'EN', 1, 'The Social Insurance for Railway and Mining Industry (Versicherungsanstalt für Eisenbahnen und Bergbau, VAEB)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2642, 22270, 'EN', 1, 'See more in its <a target="_blank" href="https://www.vaeb.at">website</a> and in <a target="_blank" href=" https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2643, 22271, 'EN', 1, 'The Social Insurance for Railway and Mining Industry is the workplace accident insurance body for workers of the railway, public transport and the cable-car sector. The workplace accident insurance of mining workers is, however, provided by AUVA.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2644, 22272, 'EN', 1, 'The Social Insurance Institution for Farmers (Sozialversicherungsanstalt der Bauern, SVB)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2645, 22273, 'EN', 1, 'See more in its <a target="_blank" href="http://www.svb.at/">website</a> and in <a target="_blank" href=" https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2646, 22274, 'EN', 1, 'The Social Insurance Institution for Farmers is the body that provides workplace accident insurance, health insurance and pensions fund for the agricultural and forestry sector (including employers and self-employed farmers). It also grants benefits to help family members and dependent workers. It was founded on the basis of the Law on Social Insurance in the Agricultural Sector (Bauernsozialversicherungsgesetz)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2647, 22275, 'EN', 1, 'Austrian Social Insurance for Occupational Risks (Allgemeine Unfallversicherungsanstalt, AUVA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2648, 22276, 'EN', 1, 'See more in its <a target="_blank" href=" https://www.auva.at/ ">website</a> and in <a target="_blank" href=" https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2649, 22277, 'EN', 1, 'Their range of activities includes regional public relations and advertising campaigns, safety training in schools, and ensuring efficient first aid in co-operation with ambulance services. AUVA has a central office in Vienna, four regional offices, and five local offices. It also has six trauma centers and four rehabilitation centers.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2650, 22278, 'EN', 1, 'Austrian Standards International');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2651, 22279, 'EN', 1, 'See more in its <a target="_blank" href="https://www.austrian-standards.at">website</a> and in <a target="_blank" href=" https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Austria ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2652, 22280, 'EN', 1, 'Austrian Standards stands for Austrian Standards International (formerly Österreichisches Normungsinstitut), a non-profit service organization (association), as well as for its subsidiary Austrian Standards plus GmbH founded in 2008 and Austrian Standards Operations GmbH founded in 2018, a 100% subsidiary of Austrian Standards plus GmbH');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2653, 22281, 'EN', 1, 'Federal Public Service Employment, Labour and Social Dialogue.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2654, 22282, 'EN', 1, 'See more in its <a target="_blank" href="http://www.emploi.belgique.be">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2655, 22283, 'EN', 1, 'Occupational safety and health (OSH) is a matter within the competence of the Federal Public Service Employment, Labour and Social Dialogue. The main executive agencies are the Directorate General for the Humanization of Work (DG Humanisation du travail – DGHT) and the Directorate General for the Control of the well-being at work (DG Contrôle du bien-être au travail). The Federal Public Service is the responsible administrative body led by the Federal Minister for Work.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2656, 22284, 'EN', 1, 'DG Control of the Well-being at work (inspection services)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2657, 22285, 'EN', 1, 'See more in its <a target="_blank" href="http://www.emploi.belgique.be/defaultTab.aspx?id=6550">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2658, 22286, 'EN', 1, '<p>The Directorate General for the Control of the Well-being at work is organized in three main Divisions: (1) a Division for Regional Control with 8 Regional Directorates, (2) a Division for chemical risks control with three directorates, one for the prevention of major accidents, one for control policies and one for industrial toxicology, and (3) a Division for knowledge management. It covers all topics as defined by the well-being at work law.</p><p>&nbsp;</p><p>The task of the inspection services is to help reduce accidents and health problems in businesses and public services based on the regulations. They ensure that the rules are adhered to and have an advisory, preventive and repressive role. Another of its tasks is indicating all the loopholes in the legislation and helping the authorities to close these.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2659, 22287, 'EN', 1, 'DG Humanization of work');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2660, 22288, 'EN', 1, 'See more in its <a target="_blank" href="http://www.emploi.belgique.be/defaultTab.aspx?id=332">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2661, 22289, 'EN', 1, '<p>The Directorate General for the Control of the Well-being at work is organized in three Divisions: (1) a Division for the preparation of the legislation, (2) a Division for the promotion of the well-being at work and (3) a Division for social consultation.</p><p>&nbsp;</p><p>The Division for the preparation of the legislation draws up the norms regarding well-being on the shop floor and interprets and evaluates these norms. It also deals with preparing these norms on a European and international level and the implementing of the European and international norms into Belgian law.</p><p>&nbsp;</p><p>The Division of promoting well-being at work arranges for promotion and information regarding well-being at work on an international and Belgian level. It develops action programmes on the distribution and use of “good practices” and networks for exchanging information on well-being at work.</p><p>&nbsp;</p><p>There are provincial committees for promoting work in each province with a view to reaching small businesses more effectively. They are tripartite committees comprising representatives of employers, employees and the administration of which the governors of the provinces are the president. They carry out regional information campaigns controlled by the DG Humanization of Work.</p><p>&nbsp;</p><p>The Division for social consultation on well-being at work organizes social consultation among other things, on the level of norms regarding well-being at work. It also manages the procedures for recognition and approval of the external actors involved in well-being at work.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2662, 22290, 'EN', 1, 'Belgian Social Insurance for Occupational Risks');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2663, 22291, 'EN', 1, 'See more in its <a target="_blank" href="https://www.assuralia.be">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2664, 22292, 'EN', 1, 'Industrial accident insurance is in the hands of private insurers. This system is an important aspect of social security given that it is compulsory for each employer. Accident insurance is compulsory for employers in the private sectors, trainees and persons in apprentiship. Self-employed are exempted from the compulsory system. <p>Industrial accident insurance is in the hands of private insurers. This system is an important aspect of social security given that it is compulsory for each employer. Accident insurance is compulsory for employers in the private sectors, trainees and persons in apprentiship. Self-employed are exempted from the compulsory system. There exist special regulations for the public sector.</p><p>Most insurance institutions have set up a prevention service; its task is to stimulate the prevention of industrial accidents in affiliated businesses. The activities of these prevention services are threefold: Assisting in working out and applying a prevention policy in the affiliated businesses, study and analysis of industrial accidents, study and assessment of the risks, taking action in the areas of information, training and promotion aimed at employees and employers.</p><p>Assuralia is the professional association of insurance companies and represents the Belgian and foreign insurance companies operating on the Belgian market.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2665, 22293, 'EN', 1, 'Federal Agency of professional risks (FEDRIS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2666, 22294, 'EN', 1, 'See more in its <a target="_blank" href="https://www.fedris.be/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2667, 22295, 'EN', 1, '<p>The remuneration of occupational illnesses is in the hands of Fedris (Federal Agency of professional risks), the former Occupational Illness Fund (Fonds des Maladies Professionnelles). This is a state-controlled institution managed by a management committee in which the social partners are represented. Fedris forms part of the social security, which means that employers are obliged to spend a certain percentage of the total wage sum on financing Fedris. Fedris is under the guardianship of the FPS Social Security.</p><p>Since january 2017, the former Occupational Illness Fund and the former Accidents at Work Fund were merged in a new institution called Fedris, the Federal Agency of professional risks.\'</p><p>The insurance premiums are in relation to the compensation paid to the victims of industrial accidents that have taken place in recent years. The premiums are therefore related to the frequency of the accidents in the company.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2668, 22296, 'EN', 1, 'Constructiv');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2669, 22297, 'EN', 1, 'See more in its <a target="_blank" href="http://www.constructiv.be">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2670, 22298, 'EN', 1, 'Constructiv is a private service organisation for the construction industry. It is jointly managed by employers and construction workers. It provides information, advice and assistance on the prevention of occupational accidents and illnesses for employers and employees in this sector.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2671, 22299, 'EN', 1, 'Prevent');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2672, 22300, 'EN', 1, 'See more in its <a target="_blank" href="https://www.prevent.be/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2673, 22301, 'EN', 1, 'Prevent is a multidisciplinary and private institute, which promotes the quality of working conditions. The institute is active in research and supports, advises and informs companies and institutions as well as insurance companies, external prevention services, professional associations, the social partners, authorities and other stakeholders in society.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
