INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2675, 22303, 'EN', 1, 'See more in its <a target="_blank" href="https://www.provincieantwerpen.be/aanbod/doe/pvi.html">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2676, 22304, 'EN', 1, 'PVI is a public institution funded by the Province of Antwerp. The institute provides information and guidance concerning prevention, protection and well-being at work and in the private sphere.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2677, 22305, 'EN', 1, 'Prevention & Interim (P&I)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2678, 22306, 'EN', 1, 'See more in its <a target="_blank" href="http://www.p-i.be/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2679, 22307, 'EN', 1, 'For the sector of the temporary employment agencies, a Central Prevention Service P&I (Service Central de Prévention pour le Secteur du Travail Intérimaire, Prévention et Interim) [50] has been created in 1998 by national collective agreement. It is jointly managed by the employers organization Federgon and the unions representing the employees in the sector of the temporary employment agencies. The goal of this non-profit OHS-service is to reduce the degree of working accidents in the sector by enhancing the amount of prevention campaigns and health and safety investigations in the sector.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2680, 22308, 'EN', 1, 'NBN – Bureau de Normalisation');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2681, 22309, 'EN', 1, 'See more in its <a target="_blank" href="https://www.nbn.be">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Belgium">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2682, 22310, 'EN', 1, 'The NBN – Bureau de Normalisation is the Belgian organization responsible for the development and publication of standards in Belgium. NBN is a semi-governmental institution with juridical responsibility. The NBN represents Belgium at European level, as member of the European Standardization Committee (CEN) and at worldwide level as a member of the International Standardization Organization (ISO).');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2683, 22311, 'EN', 1, 'The Ministry of Labour and Social Policy (MLSP)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2684, 22312, 'EN', 1, 'See more in its <a target="_blank" href="https://www.mlsp.government.bg/index.php?lang=_eng">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2685, 22313, 'EN', 1, '<p>The MLSP is part of the Bulgarian Council of Ministers (the executive state body).MLSP is in charge with developing, coordinating and implementing the policy on labour, professional qualification, income and living standard, industrial relations, health and safety at work, social security and social assistance.</p><p>According to the Health and Safety at Work Law the tasks of MLSP in the field of health and safety at work are:</p><ol><li>jointly with the Ministry of Health to analyze the status, trends and problems in providing healthy and safe working conditions and to propose measures for its improvement;</li><li>alone or with other ministries issues regulations on the provision of health and safety, organizes and coordinates the development of legislation in this area and establishes rules to ensure safe and healthy working conditions;</li><li>carries out an integrated control trough the Executive Agency "General Labour Inspectorate" of legislative compliance and fulfillment of obligations to ensure healthy and safe working conditions in all sectors and activities regardless of the form of ownership;</li></ol><p>establishes the terms and requirements for training, measurements and consulting in the field of safety at work;</p>');
