INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2699, 22327, 'EN', 1, 'See more in its <a target="_blank" href="http://hp-whp.ncphp.government.bg/index.php?page=en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Bulgaria">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(2700, 22328, 'EN', 1, 'The National network of health and safety promoting companies (NNHSPC) was designed as a social network – organization and actions for mobilization and facilitation of the social support for activities focused on promotion of health and safety at work. The National Centre of Public Health and Analyses NCPHA (formerly the National Centre of Public Health Protection NCPHP) in Sofia, Bulgaria, incorporates units with 50 years of experience in the field. Its main activities include research and development, expert consultancy, methodological and training activities in the area of public health protection, assessment of health risks due to occupational and environmental factors, personal behaviour and lifestyle, health promotion and integral diseases prevention');
