INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(3633, 23261, 'EN', 1, 'The Occupational Health Division of the Ministry of Health[42] is in charge of organising, coordinating and overseeing the functioning of the occupational health services. It is also an appeal body for employers and workers in cases where the opinion of an occupational doctor is disputed.');
