INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4923, 24551, 'EN', 1, '<p>Currently Decree No. 125/1993 assigns two private insurance companies that provide accident insurance:</p><ol><li>The Czech Insurance Company (?eská pojiš?ovna)</li><li>Kooperativa Insurance Company (Kooperativa pojiš?ovna)</li></ol><p>The system does not cover self-employed.</p><p>Their activity does not extend to prevention.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4924, 24552, 'EN', 1, 'Centre of Occupational Health (Centrum hygieny práce a pracovního léka?ství - CHPPL)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4925, 24553, 'EN', 1, 'See more in its <a target="_blank" href="http://www.szu.cz/centrum-hygieny-prace-a-pracovniho-lekarstvi">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4926, 24554, 'EN', 1, 'The mission of the Centre is to study the effects of working conditions and occupational risks on health of workers and to act as an expert body for the Ministry of Health in the field of occupational health. It does research, provides expert background for legislation and maintains international co-operation, including the areas of health effects of work and working conditions, prevention, health promotion, diagnosis, therapy and medico-legal aspects of occupational and/or other work-related diseases. The Centre is an organizational unit of the National Institute of Public Health (Státní zdravotní ústav - SZÚ).');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4927, 24555, 'EN', 1, 'The Occupational Safety Research Institute (Výzkumný ústav bezpe?nosti práce – VÚBP)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4928, 24556, 'EN', 1, 'See more in its <a target="_blank" href="http://www.vubp.cz/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4929, 24557, 'EN', 1, 'The Occupational Safety Research Institute (VÚBP) is a public research institution founded by the Ministry of Labour and Social Affairs of the Czech Republic. One of the key activities of VÚBP is the application of methods and means in the area of risk prevention relating to health and safety at work. The institute also focuses on education and promotion of OSH, and is a consultation centre for OSH expertise.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4930, 24558, 'EN', 1, 'Czech Office for Standards, Metrology and Testing (Ú?ad pro technickou normalizaci, metrologii a státní zkušebnictví - ÚNMZ)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4931, 24559, 'EN', 1, 'See more in its <a target="_blank" href="http://www.unmz.cz/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Czech_Republic">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4932, 24560, 'EN', 1, 'The Czech Office for Standards, Metrology and Testing (Ú?ad pro technickou normalizaci, metrologii a státní zkušebnictví – ÚNMZ), which was established by Act No. 20/1993 Coll. on the Organisation of the State Administration in the Field of Standards, Metrology and Testing, is a state administration body responsible to the Ministry of Industry and Trade. The competency of the office covers technical standardization, metrology, testing, harmonization of technical regulations and ensuring development and publication of Czech standards.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4933, 24561, 'EN', 1, 'The Danish Working Environment Authority (DWEA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4934, 24562, 'EN', 1, 'See more in its <a target="_blank" href="http://engelsk.arbejdstilsynet.dk/en  ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Denmark ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4935, 24563, 'EN', 1, '<p>The Danish Working Environment Authority (DWEA / Danish: Arbejdstilsynet - abbreviated AT) is an agency that works under the auspices of the Ministry of Employment at national and regional level, contributing to the creation of safe working conditions. It is empowered with supervision and enforcement rights, which are commonly designated to labour inspectorates in other countries. The three main fields of activity are: Carrying out inspections in companies. Drawing up OSH rules. Providing OSH information. The DWEA has the authority to penalise companies which do not comply with the working environment rules. They can also issue administrative fines for clear violations of the Working Environment Act, and in cases of extreme danger, they may also order work to be suspended. The DWEA is managed by an Administrative Board made up of the Director-General, 2 Deputy Directors and three Inspection Directors. The headquarters and two of the four centres are located in Copenhagen. The regional Inspection Centres with approximately 100 employees each. The Working Environment Authority is responsible for the inspection of OSH conditions in companies. This includes verifying that a written evaluation has been carried out, specific to that particular workplace. This is equivalent to a risk assessment in the terminology of the EU-OSH Directives. If the company’s working environment and the health and safety policies are found to be in good order, no reason is seen for a new inspection in the near future. However, if problems are found, the company will be visited within a period of 12-18 months.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4936, 24564, 'EN', 1, 'Labour Market Insurance AES');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4937, 24565, 'EN', 1, 'See more in its <a target="_blank" href="https://www.aes.dk/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Denmark ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4938, 24566, 'EN', 1, '<p>Labour Market Insurance (Arbejdsmarkedets Erhvervssikring – AES) is an independent institution administrated by the Danish Labour Market Supplementary Pension Scheme (Arbejdsmarkedets Tillægspension – ATP).</p><p>AES follows the applicable regulations in determining whether an injury or illness can be classified as an industrial injury. When an injury or illness can be recognised as an industrial injury, AES then decides the amount of compensation payable.</p><p>AES funds and pays compensation for recognised occupational diseases. It is mandatory for all private and public sector employers in Denmark to contribute to AES.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4939, 24567, 'EN', 1, 'NONE');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4940, 24568, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Denmark ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4941, 24569, 'EN', 1, '<p>Labour Market Insurance (Arbejdsmarkedets Erhvervssikring – AES) is an independent institution administrated by the Danish Labour Market Supplementary Pension Scheme (Arbejdsmarkedets Tillægspension – ATP).</p><p>AES follows the applicable regulations in determining whether an injury or illness can be classified as an industrial injury. When an injury or illness can be recognised as an industrial injury, AES then decides the amount of compensation payable.</p><p>AES funds and pays compensation for recognised occupational diseases. It is mandatory for all private and public sector employers in Denmark to contribute to AES.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4942, 24570, 'EN', 1, 'Danish Standards (Dansk Standard)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4943, 24571, 'EN', 1, 'See more in its <a target="_blank" href="https://www.ds.dk/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Denmark ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4944, 24572, 'EN', 1, 'Danish standards develops and publishes standards, provides trainings and offers consultancy. Certification or accreditation by a recognised body are used to ensure that national standards correspond with training.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4945, 24573, 'EN', 1, 'Ministry of Social Affairs (Sotsiaalministeerium)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4946, 24574, 'EN', 1, 'See more in its <a target="_blank" href="http://www.sm.ee/et">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4947, 24575, 'EN', 1, '<p>In the Estonian occupational health and safety system, the Ministry of Social Affairs is the executive authority which regulates the entire field, whereas two of its structural units (the Working Life Development Department, and the Health Department) are directly involved in occupational health and safety-related policy-making.&nbsp;</p><p>The Working Life Development Department is responsible for the overall co-ordination and management of the activities in the field of occupational health and safety. The Working Life Development Department:</p><ul type="square"><li>Plans and implements the working environment policy;&nbsp;</li><li>Elaborates regulations on occupational health and safety.</li></ul>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4948, 24576, 'EN', 1, 'Health Board (Terviseamet)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4949, 24577, 'EN', 1, 'See more in its <a target="_blank" href="http://www.terviseamet.ee/en/information/about-hb.html">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4950, 24578, 'EN', 1, '<p>Three Estonian governmental health authorities - Health Protection Inspectorate, Health Care Board and Chemicals Notification Centre are unified in the joint Health Boardin 2010.&nbsp;</p><p>In the field of occupational health the Health Board\'s role is to:</p><ul type="square"><li>Participate in the preparation of occupational health programmes and organize their implementation;</li><li>Analyze information concerning occupational illnesses and illness related to the work of *Organize refresher courses for occupational health specialists;</li><li>Register occupational health service providers.</li></ul>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4951, 24579, 'EN', 1, 'The Labour Inspectorate (Tööinspektsioon)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4952, 24580, 'EN', 1, 'See more in its <a target="_blank" href="http://www.ti.ee/index.php?page=3& \r\r\n">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4953, 24581, 'EN', 1, '<p>The Labour Inspectorateis a government agency operating under the jurisdiction of the Ministry of Social Affairs, and its main functions are to:</p><ul type="square"><li>Arrange for the exercise of state supervision in the working environment with respect to compliance with the requirements of legislation regulating occupational health and safety and labour relations and to apply enforcement by the state on the bases and to the extent prescribed by law;&nbsp;</li><li>Exercise market supervision over safety of the personal protective equipment in the use in the working environment and to ensure the protective properties of the equipment at sites of manufacturing and sale;&nbsp;</li><li>Exercise supervision over investigations into occupational accidents and diseases and over the implementation of measures for the prevention of occupational accidents and diseases;&nbsp;</li><li>Investigate fatal and if there is a need, then conduct an analysis of the causes of serious occupational accidents and diseases and analyze their causes;&nbsp;</li><li>Collect statistics of accidents at work and conduct analysis thereof;&nbsp;</li><li>Exercise supervision over the use of genetically modified micro-organisms in closed environments to the extent prescribed by law;&nbsp;</li><li>Take decisions in cases prescribed by law on issuing or withholding approval;&nbsp;</li><li>Carry out administrative proceedings; approve administrative acts and rules;&nbsp;</li><li>Carry out extrajudicial proceeding of misdemeanours to the extent prescribed by law;&nbsp;</li><li>Initiate criminal proceedings and carry out urgent investigative actions;&nbsp;</li><li>Carry out and resolve petitions from individuals on working environment issues;&nbsp;</li><li>Resolve individual labour disputes pursuant to the procedures prescribed by law.</li></ul>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4954, 24582, 'EN', 1, 'The Estonian Health Insurance Fund (Eesti haigekassa)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4955, 24583, 'EN', 1, 'See more in its <a target="_blank" href="http://www.haigekassa.ee/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4956, 24584, 'EN', 1, '<p>The basic principle followed in Estonia is that there is no specific insurance against employment injuries and occupational diseases. These risks are covered by the health insurance (short-term benefits) and pension insurance (long-term benefits). Under the employer\'s civil liability, additional benefits can be provided as compensation. All employees are the beneficiaries of the system.</p><p>Estonia has no formal system of state insurance for occupational accidents and diseases even though there has been significant developments and improvements of occupational health and safety legislation has taken place in the past few years.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4957, 24585, 'EN', 1, 'NONE');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4958, 24586, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4959, 24587, 'EN', 1, 'In Estonia, there are no prevention institutes as unlike the situation in some European countries. However, preventive activities are included and implemented, in the work of the Ministry of Social Affairs and the Labour Inspectorate most commonly through awareness raising campaigns.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4960, 24588, 'EN', 1, 'Estonian Centre for Standardization (EVS) (Eesti standardikeskus)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4961, 24589, 'EN', 1, 'See more in its <a target="_blank" href="http://www.evs.ee/Esileht/tabid/111/language/en-US/Default.aspx">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_–_Estonia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4962, 24590, 'EN', 1, 'The Estonian Centre for Standardization (EVS) is a non-profit association, which was established by three founding members (Republic of Estonia, Chamber of Commerce and Industry, Confederation of Employers and Industry) in 1999. The Estonian Centre for Standardization operates as the Estonian standard organization pursuant to the Technical Regulations and Standards Act. EVS has accepted the WTO Code of Good Practice for the Preparation, Adoption and Application of Standards for the removal of technical barriers to trade.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4963, 24591, 'EN', 1, 'European Commission');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4964, 24592, 'EN', 1, 'See more in its <a target="_blank" href="http://ec.europa.eu/social/main.jsp?langId=en&catId=656">website</a> and in <a target="_blank" href="https://www.eurofound.europa.eu/country/eu-level">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4965, 24593, 'EN', 1, 'The European Commission is the EU’s politically independent executive arm. It is responsible for drawing up proposals for European legislation, and it implements the decisions of the European Parliament and of the Council of the European Union. It is organised into directorate-generals (DGs). DG Employment, Social Affairs and Inclusion is the main DG responsible for employment, social dialogue and working conditions.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4966, 24594, 'EN', 1, 'European Parliament');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4967, 24595, 'EN', 1, 'See more in its <a target="_blank" href="http://www.europarl.europa.eu">website</a> and in <a target="_blank" href="https://www.eurofound.europa.eu/country/eu-level">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4968, 24596, 'EN', 1, 'The European Parliament is the EU’s law-making body, directly elected by EU voters every five years. The most recent elections were in May 2014. The Parliament has three main roles: legislative; supervisory; and budgetary. The European Parliament’s Employment and Social Affairs Committee deals with all issues that relate to employment and social legislation.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4969, 24597, 'EN', 1, 'Council of the European Union');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4970, 24598, 'EN', 1, 'See more in its <a target="_blank" href="https://europa.eu/european-union/about-eu/institutions-bodies/council-eu_en">website</a> and in <a target="_blank" href="https://www.eurofound.europa.eu/country/eu-level">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4971, 24599, 'EN', 1, '<p>The Council of the European Union comprises government ministers from each EU country, who meet to discuss, amend and adopt laws. In most cases, they work together with the European Parliament through the co-decision procedure to jointly adopt legislation and to coordinate policies. The ministers have the authority to commit their governments to the actions agreed in the meetings.</p><p>&nbsp;</p><p>Together with the European Parliament, the Council is the main decision-making body of the EU. The Council of the EU is a single legal entity, but it meets in 10 different configurations, depending on the subject being discussed. The Employment, Social Policy, Health and Consumer Affairs Council configuration (Employment, Social Policy, Health and Consumer Affairs Council configuration (EPSCO) brings together ministers responsible for employment, social affairs, health and consumer policy from all EU Member States. The advisory committees of EPSCO that deal with employment and social protection issues are the Employment Committee and the Social Protection Committee.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4972, 24600, 'EN', 1, 'The Senior Labour Inspectors’ Committee (SLIC)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4973, 24601, 'EN', 1, 'See more in its <a target="_blank" href="http://ec.europa.eu/social/main.jsp?catId=148&intPageId=685">website</a> and in <a target="_blank" href="https://www.eurofound.europa.eu/country/eu-level">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4974, 24602, 'EN', 1, 'The Senior Labour Inspectors’ Committee (SLIC) is one of the main institutions and mechanisms that monitor the enforcement of employees’ rights. SLIC started to meet in an informal way in 1982 to assist the European Commission in monitoring the enforcement of EU legislation at national level. A Commission decision (95/319/EC) gave the committee formal status in 1995 with a mandate to give its opinion to the Commission, either at the Commission’s request or on its own initiative, on all problems relating to Member States’ enforcement of EU law on health and safety at work.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4975, 24603, 'EN', 1, 'EUROSTAT');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4976, 24604, 'EN', 1, 'See more in its <a target="_blank" href="http://ec.europa.eu/eurostat">website</a> and in <a target="_blank" href="https://www.eurofound.europa.eu/country/eu-level">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4977, 24605, 'EN', 1, 'Eurostat is the statistical office of the European Union situated in Luxembourg. Its mission is to provide high quality statistics for Europe.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4978, 24606, 'EN', 1, 'European Agency for Safety and Health at Work (EU-OSHA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4979, 24607, 'EN', 1, 'See more in its <a target="_blank" href="https://osha.europa.eu">website</a> and in <a target="_blank" href="https://www.eurofound.europa.eu/country/eu-level">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4980, 24608, 'EN', 1, 'The tripartite decentralised European Agency for Safety and Health at Work has a mandate to make European workplaces safer, healthier and more productive for the benefit of businesses, employees and governments. It promotes a culture of risk prevention to improve working conditions in Europe, and provides tools and advice to organisations to help them improve health and safety in the workplace. Its ‘healthy workplaces’ campaigns run over a two-year period across Europe and are aimed at raising awareness about occupational safety and health topics.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4981, 24609, 'EN', 1, 'European Foundation for the Improvement of Living and Working Conditions (Eurofound)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4982, 24610, 'EN', 1, 'See more in its <a target="_blank" href="https://www.eurofound.europa.eu/">website</a> and in <a target="_blank" href="https://www.eurofound.europa.eu/country/eu-level">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4983, 24611, 'EN', 1, 'The European Foundation for the Improvement of Living and Working Conditions (Eurofound) is a tripartite EU decentralised agency. Its role is to provide knowledge, information, advice and expertise on living and working conditions, industrial relations and work-related policies in Europe for key actors in EU social policy.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4984, 24612, 'EN', 1, 'The European Centre for the Development of Vocational Training (Cedefop)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4985, 24613, 'EN', 1, 'See more in its <a target="_blank" href="http://www.cedefop.europa.eu/fr">website</a> and in <a target="_blank" href="https://www.eurofound.europa.eu/country/eu-level">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4986, 24614, 'EN', 1, 'The European Centre for the Development of Vocational Training (known by the acronym of its French name, Cedefop) is another tripartite EU decentralised agency. It is charged with assisting the European Commission, Member States and social partner organisations across Europe to develop policy on vocational training in the European Union. Cedefop, based in Thessaloniki, Greece, was established in 1975 by Council regulation 337/75 as a non-profit-making body and was one of the first (together with Eurofound) of the specialised, decentralised agencies set up to provide scientific and technical knowledge in specific fields, and to promote exchanges of ideas between different European partners.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4987, 24615, 'EN', 1, 'European Committee for Standardization (CEN)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4988, 24616, 'EN', 1, 'See more in its <a target="_blank" href="https://www.cen.eu">website</a> and in <a target="_blank" href="https://www.eurofound.europa.eu/country/eu-level">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4989, 24617, 'EN', 1, '<p>CEN, the European Committee for Standardization, is an association that brings together the National Standardization Bodies of 34 European countries.</p><p>CEN provides a platform for the development of European Standards and other technical documents in relation to various kinds of products, materials, services and processes.</p><p>CEN supports standardization activities in relation to a wide range of fields and sectors including: air and space, chemicals, construction, consumer products, defence and security, energy, the environment, food and feed, health and safety, healthcare, ICT, machinery, materials, pressure equipment, services, smart living, transport and packaging</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4990, 24618, 'EN', 1, 'Ministry of Social Affairs and Health');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4991, 24619, 'EN', 1, 'See more in its <a target="_blank" href="http://stm.fi/en/frontpage">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4992, 24620, 'EN', 1, 'In Finland the Ministry of Social Affairs and Health is responsible for the preparation of OSH legislation, the enforcement and development of OSH, statutory insurance, gender equality and the development of occupational health care. This Ministry works closely with other ministries on any matters that may be related to OSH. \r\nThe Department of Health is responsible for the development of occupational health service legislation and is also formulates the national strategies for the occupational health services. The Department for Occupational Safety and Health is responsible for the development of national occupational safety and health policies, the coordination of research in occupational safety and health issues, and the efficient application of research information.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4993, 24621, 'EN', 1, 'Ministry of Employment and the Economy');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4994, 24622, 'EN', 1, 'See more in its <a target="_blank" href="http://tem.fi/en/frontpage">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4995, 24623, 'EN', 1, 'One of the main tasks of the Ministry of Employment and the Economy is to smooth out difficulties in the operation of the labour market and work organization. It prepares essential labour legislation together with the labour market organizations.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4996, 24624, 'EN', 1, 'Department for Occupational Safety and Health');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4997, 24625, 'EN', 1, 'See more in its <a target="_blank" href="http://stm.fi/en/working-life">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4998, 24626, 'EN', 1, 'The Department for Occupational Safety and Health with the Ministry of Social Affairs and Health is the highest Government body for OSH Administration and it supervises independent Labour Inspectorates. Finland is divided into six independent Occupational Safety and Health Inspectorates (Regional State Administrative Agencies; Aluehallintovirasto, AVI; Regionförvaltningsverket). The responsibilities and rights of the OSH authorities are described in the Act on Occupational Safety and Health Enforcement and Cooperation on Occupational Safety and Health at Workplaces (44/2006). The Act on Occupational Safety and Health Administration (16/1993) defines the tasks to be undertaken by the occupational safety and health authorities.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(4999, 24627, 'EN', 1, 'Regional State Administrative Agencies');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5000, 24628, 'EN', 1, 'See more in its <a target="_blank" href="http://www.avi.fi/fi/web/avi/tyosuojelu#.Wqos65POUdU">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5001, 24629, 'EN', 1, 'The Inspectorates provide instructions and advice on applying the regulations on occupational safety and health matters, working conditions, employment and equality, and ensures that there is adherence to these regulations and guidelines in the workplaces.\r\nThe OSH authorities need to monitor compliance with more than 100 items of legislation. The most important of these legislative acts are the Occupational Safety and Health Act and the Decrees based upon it, the Occupational Health Care Act, the Employment Contracts Act, the Working Hours Act, the Annual Holidays Act and the Young Workers’ Protection Act. The OSH authorities also investigate serious occupational accidents and major cases of occupational diseases.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5002, 24630, 'EN', 1, 'Safety Technology Authority (Turvallisuus- ja kemikaalivirasto, TUKES; Säkerhets- och kemikalieverket)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5003, 24631, 'EN', 1, 'See more in its <a target="_blank" href="http://www.tukes.fi/en/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5004, 24632, 'EN', 1, 'The Finnish Safety and Chemicals Agency (Tukes)  is a national licensing and supervisory authority to endorse the safety and reliability of products, services and industrial activities in Finland.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5005, 24633, 'EN', 1, 'Radiation and Nuclear Safety Authority (Säteilyturvakeskus, STUK; Strålsäkerhetscentralen)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5006, 24634, 'EN', 1, 'See more in its <a target="_blank" href="http://www.stuk.fi/web/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5007, 24635, 'EN', 1, 'Radiation and Nuclear Safety Authority (STUK) supervises radiation and nuclear safety in Finland. The purpose of STUK is to protect people, society, the environment and future generations from the detrimental effects of radiation.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5008, 24636, 'EN', 1, 'National Consumer Administration (Kuluttajavirasto; Konsumentverket)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5009, 24637, 'EN', 1, 'See more in its <a target="_blank" href="https://www.kkv.fi/en/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5010, 24638, 'EN', 1, 'The Finnish Competition and Consumer Authority (FCCA) is tasked with ensuring good market performance. The Competition Division removes barriers to competition, ensuring that consumers are offered competitive alternatives. The Consumer Division, on the other hand, ensures that consumers have access to sufficient, accurate, and truthful information for making choices, and that the practices companies use in marketing and their customer relations are appropriate and the contract terms applied by them are reasonable.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5011, 24639, 'EN', 1, 'The National Supervisory Authority for Welfare and Health (Sosiaali- ja terveysalan lupa- ja valvontavirasto, Valvira; Tillstånds- och tillsynsverket för social- och hälsovärd)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5012, 24640, 'EN', 1, 'See more in its <a target="_blank" href="http://www.valvira.fi/web/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5013, 24641, 'EN', 1, 'Valvira is a national agency operating under the Ministry of Social Affairs and Health, charged with the supervision of the social and health care, alcohol and environmental health sectors. Valvira provides licensing for social and health care providers and offers guidance to the Regional State Administrative Agencies to achieve harmonised licensing, guidance and supervisory practices throughout Finland.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5014, 24642, 'EN', 1, 'The Finnish Transport Safety Agency (Liikenteen turvallisuusvirasto, Trafi; Trafiksäkerhetsverket)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5015, 24643, 'EN', 1, 'See more in its <a target="_blank" href="https://www.trafi.fi/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5016, 24644, 'EN', 1, 'Trafi develops the safety of the transport system, promotes environmentally friendly transport solutions and is responsible for transport system regulatory duties.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5017, 24645, 'EN', 1, 'The Federation of Accident Insurance Institutions (Tapaturmavakuutuslaitosten liitto, TVL; Olycksfallsförsäkringsanstalternas förbund (OFF))');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5018, 24646, 'EN', 1, 'See more in its <a target="_blank" href="http://www.tvk.fi/en/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5019, 24647, 'EN', 1, 'In Finland, occupational accident insurance is offered by 12 private accident insurance companies under the supervision of the Ministry of Social Affairs and Health. The accident insurance system is financed through premiums paid by the employers. Worker´ Compensation center (Tapaturmavakuutuskeskus, TVK) coordinates the statutory accident insurance. The TVK is a liaison organisation for the insurance companies. It combines statistics on occupational accidents and diseases together with their causes and consequences, and helps prevent these in the future. An occupational safety and health committee, with representatives from the labour market organisations and the insurance sector, directs TVK’s OSH activities and co-ordinates the insurance sector. In addition, TVK investigates fatal occupational injuries. These are examined by an investigation board which has representatives from the central labour market organisations, insurance companies and the Centre for Occupational Safety (Työturvallisuuskeskus, TTK; Arbetarskyddscentralen)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5020, 24648, 'EN', 1, 'The Social Insurance Institution of Finland (Kansaneläkelaitos, KELA; Folkpensionsanstalten)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5021, 24649, 'EN', 1, 'See more in its <a target="_blank" href="http://www.kela.fi/web/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5022, 24650, 'EN', 1, 'According to Health Insurance Act (1224/2004) employers are entitled to reimbursement from KELA to compensate for 60% of reasonable expenses for preventive services, if the workplace has a procedure for management, follow-up and early support. The reimbursement provided for organizing voluntary curative services is 50% of reasonable expenses. KELA pays sickness allowance as compensation for loss of earnings caused by an illness after a 10 day waiting period.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5023, 24651, 'EN', 1, 'NONE');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5024, 24652, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5025, 24653, 'EN', 1, 'In Finland, prevention and preventive actions are included in all activities for maintaining and improving safety and health at work. Therefore there are no specific organisations handling only preventive activities, instead all OSH actors carry out preventive activities.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5026, 24654, 'EN', 1, 'The Finnish Standards Association (Suomen Standardoimisliitto SFS ry.)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5027, 24655, 'EN', 1, 'See more in its <a target="_blank" href="https://www.sfs.fi/en">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Finland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5028, 24656, 'EN', 1, 'The Finnish Standards Association (Suomen Standardoimisliitto SFS ry.) is the central body for standardisation in Finland. Its membership includes the central government and professional, commercial and industrial organisations. The Association approves national SFS standards and coordinates national standardisation. National Standards are sold in Finland exclusively by SFS. Standardization is carried out at international, regional, national and company levels. The Association also manages the Nordic and European environmental labelling systems for products and services. SFS promotes the use of standards in Finland by disseminating information on international, foreign and Finnish standards and drafts. SFS is the Finnish source for all international standards and drafts of the International Organization for Standardization (ISO), for European standards and drafts of CEN, and for publications of ISO member bodies. SFS also distributes other foreign standards and technical regulations');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5029, 24657, 'EN', 1, 'The Ministry of Labour (Ministère du Travail)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5030, 24658, 'EN', 1, 'See more in its <a target="_blank" href="http://travail-emploi.gouv.fr/ministere/\r\nhttp://travail-emploi.gouv.fr/ministere/organisation/article/dgt-direction-generale-du-travail">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5031, 24659, 'EN', 1, '<p>The Ministry of Labour implements Government policy in the fields of labour, social dialogue, prevention of occupational accidents and diseases, employment, and vocational training.</p><p>&nbsp;</p><p>The General Directorate for Labour (Direction générale du travail - DGT) is the arm of the ministry of Labour that prepares, leads, and coordinates labour policy in order to improve collective and individual relations at work, to support and monitor collective agreements, to improve working conditions in companies, and to protect occupational health and safety. To this end, it is in charge of drafting and applying legislative and regulatory texts, and developing actions in that fields.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5032, 24660, 'EN', 1, 'The Social Security Directorate (Direction de la sécurité sociale - DSS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5033, 24661, 'EN', 1, 'See more in its <a target="_blank" href="http://solidarites-sante.gouv.fr/ministere/organisation/directions/article/dss-direction-de-la-securite-sociale">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5034, 24662, 'EN', 1, 'The Social Security Directorate (Direction de la Sécurité Sociale, DSS) is attached to the Ministry of Solidarities and Health and to the Ministry for Action and Public Accounts. It oversees the Social Security bodies of the funds of the general social security scheme, of the funds of the basic social security scheme for the self-employed other than in the farming sector, and of the funds of the special social security schemes.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5035, 24663, 'EN', 1, 'Regional Directorates for Companies, Competition, Consumption, Labour and Employment (Directions Régionales des Entreprises, de la Concurrence, de la Consommation, du Travail et de l’Emploi- DIRECCTEs)– Labour Inspectorates');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5036, 24664, 'EN', 1, 'See more in its <a target="_blank" href="http://direccte.gouv.fr/Organisation-d-une-Direccte.html\r\nhttp://travail-emploi.gouv.fr/emploi/accompagnement-des-tpe-pme/tpe-pme/gerer-mes-ressources-humaines/article/le-role-de-l-inspection-du-travail">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5037, 24665, 'EN', 1, 'The Labour Inspectorate (Inspection du Travail) services have been attached to the DIRECCTEs that were set up by Decree 2009-1377 of 10 November 2009. Labour inspection is carried out mainly by the labour inspectors and controllers in charge of inspecting companies and informing the public. They check that labour law is being properly implemented, they advise and inform employers, employees and staff representatives about their rights and obligations, and they facilitate out-of-court conciliation between the parties, in particular for collective disputes. The Labour Medical Inspectorate (Inspection Médicale du Travail) constitutes a specific technical support body within the Labour Inspectorate services.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5038, 24666, 'EN', 1, 'French National Health Insurance Fund for Salaried Workers (Caisse Nationale de l’Assurance Maladie des Travailleurs Salariés-CNAMTS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5039, 24667, 'EN', 1, 'See more in its <a target="_blank" href="https://www.ameli.fr/l-assurance-maladie/connaitre-l-assurance-maladie/missions-et-organisation/index.php">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5040, 24668, 'EN', 1, '<p>Since the French Law No. 46-2426 of 30 October 1946, occupational risk insurance has been incorporated into the assignments of the French Social Security system. In addition to providing compensation, the Social Security bodies also have a risk prevention mission. In 1967, a national Fund dedicated to Health Insurance was set up, namely the CNAMTS. It was also assigned the task of managing occupational risks. The French Law No. of 25 April 1994&nbsp;enabled the Health Insurance – Occupational Risks section (Occupational Accidents and Diseases (AT/MP) Branch) to acquire a certain amount of independence, in particular financial independence, while remaining with the CNAMTS.</p><p>Occupational accidents and diseases are insured collectively by the companies. Contribution rates depend on the size of the establishment, on its sector of activity, and on the frequency and seriousness of the accidents or diseases that might be suffered by its employees. The compensation mission consists in meeting the needs of the victims by bearing the expenses incurred by victims of occupational accidents and diseases, by reimbursing treatment expenses, and by paying daily allowances and pensions, annuities or lump sums in the event of permanent disability.</p><p>The CNAMTS (<em>Caisse Nationale de l’Assurance Maladie des Travailleurs Salariés</em>) is an administrative national public establishment overseen by the Social Security Directorate. Its responsibilities and missions are specified in particular by French Law No. 2004-810 of 13 August 2004<span style="text-decoration: underline;"><sup><a href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France#cite_note-64">[64]</a></sup></span>. The activities of the CNAMTS consist, in particular, in:</p><ul><li>defining the focuses, principles, and objectives that guide its action and the action of the French National Health Insurance scheme for salaried workers (the general Social Security scheme, or “<em>Assurance Maladie</em>”), and ensuring that they are implemented,</li><li>defining and promoting prevention of disease, of occupational accidents, and of occupational disease by taking worthwhile national actions,</li><li>determining and co-ordinating the activities of the bodies of the network of Health Insurance branches and Occupational Accidents and Diseases branches&nbsp;: regional and local health insurance funds (<em>Caisses primaires d\'assurance maladie</em>, CPAMs), occupational health and pension insurance funds (<em>Caisses d’Assurance Retraite et de la Santé au Travail-CARSATs</em>), CRAMIF (Paris Region Regional Health Insurance Fund,&nbsp;<em>Caisse Régionale d’Assurance Maladie d’Île de France</em>), and CGSSs (General Social Security Funds for French overseas territories,&nbsp;<em>Caisses Générales de la Sécurité Sociale</em>)). These&nbsp;local and Regional Health Insurance Fundshave, in particular, the mission of assisting companies in assessing the risks of occupational accidents and diseases with a view to preventing those risks, and the mission of taking part in pricing the insurance for occupational accidents and diseases. They advise companies in their risk prevention approach through direct action at the workplace. They also act through collective actions with the various trade sectors, designing risk assessment and risk prevention systems, offering training, and disseminating good practices and distributing financial incentives.</li></ul><p>The Occupational Accidents and Diseases Commission (<em>Commission des Accidents du Travail et des Maladies Professionnelles</em>, CAT/MP) is in charge of balancing the finances of the Occupational Accidents and Diseases (AT/MP) Branch, of pricing, compensation, and prevention of occupational accidents and diseases. To these ends, it determines the focuses of the Agreement on Objectives and Management (COG,&nbsp;<em>Convention d’Objectifs et de Gestion</em>) for the Branch. It approves the budgets for the French National Fund for the Prevention of Occupational Accidents and Diseases (<em>Fonds National de Prévention des Accidents du Travail et des Maladies Professionnelles</em>, FNPATMP.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5041, 24669, 'EN', 1, 'French Agricultural Social Security Scheme (Mutualité Sociale Agricole - MSA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5042, 24670, 'EN', 1, 'See more in its <a target="_blank" href="http://www.msa.fr/lfy">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5043, 24671, 'EN', 1, 'The existence of agricultural mutual benefit societies in France dates back to the 19th century. The role and the responsibilities of the MSA were defined by the Orders of 1945 setting up the French Social Security system. The MSA is the mandatory welfare protection body for farmers and agricultural workers. It covers, in particular, the risks of occupational accidents and diseases in agriculture, landscaping, forestry, and agri-food industries. An Agreement on Objectives and Management (COG, Convention d’Objectifs et de Gestion) defines the mutual commitments of the French State and the MSA. The Central Fund of the MSA (Caisse Centrale de la MSA (CCMSA)) represents the MSA at national level and leads the network of 35 regional funds. A specific occupational health and security plan for the agriculture sector has been adopted for 2016-2020.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5044, 24672, 'EN', 1, 'The Social Security scheme for the self-employed (Régime Social des Indépendants - RSI)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5045, 24673, 'EN', 1, 'See more in its <a target="_blank" href="https://www.secu-independants.fr/nous-connaitre.html\r\n">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5046, 24674, 'EN', 1, 'The RSI took over from three previously existing schemes and was set up pursuant to the provisions of French Law No. 2004-1343 of 9 December 2004. It is run by the representatives of its insured parties, who are craftspeople, shopkeepers, and self-employed professionals. It performs a public service mission by managing the mandatory welfare protection of over four million self-employed enterprise managers and of their dependants and beneficiaries, under an Agreement on Objectives and Management signed between the French State and the RSI.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5047, 24675, 'EN', 1, 'Other Schemes');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5048, 24676, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5049, 24677, 'EN', 1, 'There are 16 other special social security schemes, including the schemes for SNCF (French Railways, Société Nationale des Chemins de Fer) personnel, for civil servants and state workers, for the mining sector, and for the electricity and gas industries.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5050, 24678, 'EN', 1, 'EUROGIP');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5051, 24679, 'EN', 1, 'See more in its <a target="_blank" href="https://www.eurogip.fr">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5052, 24680, 'EN', 1, 'EUROGIP is a public interest group formed by the CNAMTS (Caisse Nationale de l’Assurance Maladie des Travailleurs Salariés) and INRS (Institut National de Recherche et de Sécurité), set up to study issues relating to insurance for and prevention of occupational accidents and diseases at European level.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5053, 24681, 'EN', 1, 'The French National Agency for Improvement of Working Conditions (Agence Nationale pour l\'Amélioration des Conditions de Travail - ANACT)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5054, 24682, 'EN', 1, 'See more in its <a target="_blank"href="https://www.anact.fr">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5055, 24683, 'EN', 1, '<p>The ANACT is an administrative public establishment set up by French Law No. 73-1195 of 27 December 1973. The decree 2015-968 of 31 July 2015 on the new missions and functioning of the ANACT reinforced its national and regional network and focused its missions on working conditions.</p><p>&nbsp;</p><p>The ANACT network for improvement of working conditions is made up of the National Agency, overseen by the Ministry for Labour, and 17 Regional Agencies (ARACTs, Agences Régionales pour l’Amélioration des Conditions de Travail), which are independant associations under French Law of 1 July 1901. The national agency relies on a tripartite governance (including the governement), and the regional agencies have bipartite boards, bringing together equally representatives of employer and employee organisations.</p><p>&nbsp;</p><p>The ANACT network helps companies and other organisations to develop innovative projects concerning labour, and encourages them to place labour on the same level as the other economic determinants (products, markets, technologies, etc). Its work plan is defined in a Performance and Objectives Agreement with the General directorate of Labour.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5056, 24684, 'EN', 1, 'The French Professional Agency for Risk Prevention in Building and Civil Engineering (Organisme Professionnel de Prévention du Bâtiment et des Travaux Publics - OPPBTP');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5057, 24685, 'EN', 1, 'See more in its <a target="_blank" href="https://www.preventionbtp.fr">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5058, 24686, 'EN', 1, '<p>The OPPBTP was set up in 1947. Its role and organisation were specified by Decree No. 85-682 of 4 July 1985[75]. Its missions are to advise, train, and inform the players in the building and civil engineering sector as regards risk prevention, safety, health, and the improvement of working conditions. Its governance is bipartite, as national and regional boards are made up of five representatives of employer federations and of five representatives of employee union organisations.</p><p>&nbsp;</p><p>The OPPBTP has a staff of 300, who are mainly engineers and technicians from the building and civil engineering sector, distributed throughout France and organised into 18 agencies. The Secretary-General, a representative of the CNAMTS (French National Health Insurance Fund, Caisse Nationale de l’Assurance Maladie), and a representative of the Minister for Labour attend the sessions of the Board of the national committee with consultative votes.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5059, 24687, 'EN', 1, 'The French National Agency for Food, Environmental and Occupational Health and Safety (Agence Nationale de Sécurité Sanitaire de l\'Alimentation, de l\'Environnement et du Travail - ANSES)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5060, 24688, 'EN', 1, 'See more in its <a target="_blank" href="https://www.anses.fr">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5061, 24689, 'EN', 1, '<p>Set up by Order No. 2010-18 of 7 January 2010, ANSES is an administrative public establishment placed under the supervision of the Ministers for Health, Agriculture, the Environment, Labour, and Consumption.</p><p>&nbsp;</p><p>Its mission consist in risk assessment, as well as expertise and scientific and technical support for drawing up legislative and regulatory provisions and for implementing risk management measures.</p><p>&nbsp;</p><p>With a view to accomplishing its missions, the ANSES organises collective expertise in its field of competence, drawing in particular on specialised committees of experts</p>\r\n\r\nIts mission consist in risk assessment, as well as expertise and scientific and technical support for drawing up legislative and regulatory provisions and for implementing risk management measures.\r\n\r\nWith a view to accomplishing its missions, the ANSES organises collective expertise in its field of competence, drawing in particular on specialised committees of experts');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5062, 24690, 'EN', 1, 'The French National Public Health Agency (Santé Publique France)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5063, 24691, 'EN', 1, 'See more in its <a target="_blank" href="http://www.santepubliquefrance.fr">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5064, 24692, 'EN', 1, 'S<p>Santé Publique France was created in April 2016 as the national public health agency, resulting from the merging of the French Institute for Public Health Surveillance (InVS), the French institute for Health Promotion and health Education (Inpes), and the Establishment for Public Health Emergency preparedness and response (Eprus). Santé Publique France serves the population in all aspects of public health based on scientific knowledge, data, and information, including on OSH issues. It supports the government and society in improving the health and well-being of the population, with the objective of reducing social health inequities in all areas of public health : infectious diseases, non infectious diseases, environmental health and occupational health. It contributes to better knowledge of work related diseases, plays an important role in health monitoring on OSH and provide important guidance in order to establish the table of occupational diseases. Santé Publique France analyzes up-to-date knowledge and data on the determinants of health and risk factors, provides decision makers with independant evidence-based guidance and proposes measures to protect the population from health threats, develops evidence-based interventions for prevention and health promotion.</p><p>&nbsp;</p><p>The agency, under the authority of Ministry of Health, is present throughout the national territory with regional units. Operating as a network, cooperation with partners and health professionals, including occupational physicians, enables to combine strengths.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5065, 24693, 'EN', 1, 'French Standards Association (Association Française de Normalisation - AFNOR)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5066, 24694, 'EN', 1, 'See more in its <a target="_blank" href="https://www.afnor.org">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_France">OSHwiki</a');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5067, 24695, 'EN', 1, '<p>Standardisation and promotion of standardisation are handled by AFNOR and its delegated bodies and approved by the Minister for Industry. AFNOR is under the supervision of the Ministry for Industry. It represents France at the CEN (European Committee for Standardisation) and at the ISO (International Organization for Standardization).</p><p>&nbsp;</p><p>The technical work on standards is conducted through major standardization programs. Each program is steered by a Strategy Committee (CoS, Comité Stratégique), and the Occupational Safety and Health Strategy Committee (CoS Santé et Sécurité au Travail) covers OSH topics.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5068, 24696, 'EN', 1, 'Federal Ministry of Labour and Social Affairs');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5069, 24697, 'EN', 1, 'See more in its <a target="_blank" href="http://www.bmas.de/EN/Our-Topics/our-topics.html">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5070, 24698, 'EN', 1, '<p>The Federal Ministry of Labour and Social Affairs (Bundesministerium für Arbeit und Soziales, BMAS)is the ministry in the areas of labour market, labour law, occupational safety and health, initial and continuing training, social security, pensions and the participation of persons with disabilities.</p><p>The BMAS prepares laws, ordinances and technical rules in the field of occupational safety and health. Furthermore, the Ministry supervises the social accident insurance institutions and the Federal Institute for Occupational Safety and Health (Bundesanstalt für Arbeitsschutz und Arbeitsmedizin, BAuA) and it is represented in the National Occupational Safety and Health Conference of the Joint German OSH Strategyas well as in the National Prevention Conference.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5071, 24699, 'EN', 1, 'The Federal Institute for Occupational Safety and Health (Bundesanstalt für Arbeitsschutz und Arbeitsmedizin, BAuA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5072, 24700, 'EN', 1, 'See more in its <a target="_blank" href="https://www.baua.de/EN/Home/Home_node.html">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5073, 24701, 'EN', 1, 'The Federal Institute for Occupational Safety and Health (Bundesanstalt für Arbeitsschutz und Arbeitsmedizin, BAuA) is a public-law institution without legal capacity. As a federal authority (Bundesbehörde), it is directly responsible to the Federal Ministry of Labour and Social Affairs (BMAS). As a governmental research institution (Ressortforschungseinrichtung), it advises the Federal Ministry of Labour and Social Affairs in all matters of occupational safety and health and on the humane design of working conditions. As a federal institution with R&D functions, BAuA operates at the interface between science and politics and renders transfer services from the science system into policy, corporate practice and the broader society and vice versa. BAuA\'s tasks range from policy advice, research and development in OSH and the performance of sovereign duties through to transferring knowledge into corporate practice and the educational and instructional work done by the DASA Working World Exhibition.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5074, 24702, 'EN', 1, 'German Social Accident Insurance (DGUV)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5075, 24703, 'EN', 1, 'See more in its <a target="_blank" href="http://www.dguv.de/en/index.jsp">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5076, 24704, 'EN', 1, 'The German Social Accident Insurance (Deutsche Gesetzliche Unfallversicherung, DGUV) is the umbrella organisation of the social accident insurance institutions for the industrial and the public sector[90]. The DGUV members consist of nine branch-specific social accident insurance institutions for the industrial sector (called Berufsgenossenschaften) and 19 social accident insurance institutions for the public sector (called Unfallkassen or Gemeindeunfallversicherungsverbände), four social accident insurance institutions for fire services (Feuerwehr-Unfallkassen) and the accident insurance of the Federal Government and the Railway Services (Unfallversicherung Bund und Bahn).');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5077, 24705, 'EN', 1, 'Social Insurance for Agriculture, Forestry and Horticulture (SVLFG)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5078, 24706, 'EN', 1, 'See more in its <a target="_blank" href="http://www.svlfg.de/11-wir/index.html">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5079, 24707, 'EN', 1, '<p>The Social Insurance for Agriculture, Forestry and Horticulture (SVLFG) covers the social protection for accidents, health, pensions and nursing care of persons working in agriculture and forestry.</p><p>Since January 2013 the Social Insurance for Agriculture, Forestry and Horticulture is the succeeding umbrella organisation on federal level of the former Agricultural Social Insurance (Spitzenverband der landwirtschaftlichen Sozialversicherung, LSV) of the agricultural accident insurances (landwirtschaftlichen Berufsgenossenschaften), the agricultural pension funds (landwirtschaftlichen Alterskassen), the agricultural health insurances (landwirtschaftlichen Krankenkassen) and the agricultural long term care insurances (landwirtschaftlichen Pflegekassen).</p><p>The SVLFG attends to the interests of its members and promotes their tasks for the benefit of the insured and the companies. It represents the agricultural social security face to face with politics, federal, European and other national and international institutions, the social partners, federal courts and the European Court.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5080, 24708, 'EN', 1, 'German Social Accident Insurance (DGUV)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5081, 24709, 'EN', 1, 'See more in its <a target="_blank" href="http://www.dguv.de/en/index.jsp">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5082, 24710, 'EN', 1, 'The DGUV supports its members (Berufsgenossenschaften, Unfallkassen, Gemeindeunfallversicherungsverbände) in the development of common measures for OSH prevention, the promotion of the ongoing development of rehabilitation and addresses new methods and approaches in vocational training. In addition, the German Social Accident Insurance is divided into six regional associations. These regional associations take over common regional tasks in the areas of prevention and rehabilitation.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5083, 24711, 'EN', 1, 'The German Institute for Standardisation (Deutsches Institut für Normung e.V., DIN)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5084, 24712, 'EN', 1, 'See more in its <a target="_blank" href="http://www.din.de/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Germany">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5085, 24713, 'EN', 1, 'The German Institute for Standardisation is the official German standardisation body. This registered non-profit association was acknowledged by an agreement with the German Federal Government. DIN represents German interests in European and international standards organisations. The primary task is to work closely together with its stakeholders (industry, government, associations) to develop consensus-based national and international standards that meet market requirements.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5086, 24714, 'EN', 1, 'The Directorate for Safety & Health at Work of the Ministry of Labour, Social Security & Social Solidarity');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5087, 24715, 'EN', 1, 'See more in its <a target="_blank" href="http://www.ypakp.gr">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5088, 24716, 'EN', 1, '<p>The&nbsp;Directorate for Safety &amp; Health at Work which promotes all issues related to occupational safety and health (except of the inspection and the enforcement of the national OSH legislation), is the principal OSH administration authority in the Ministry of Labour, Social Security &amp; Social Solidarity. The Directorate is responsible among other duties for the planning, organization, implementation, monitoring and evaluation of the national OSH strategy and policies, the national OSH System as well as the National OSH Programme, in accordance to the national priorities and the running EU OSH strategy. Its activities are mainly directed in the fields of OSH legislation, policy making, decision making, administration, information management and documentation, awareness raising, networking, education, training and research.&nbsp;</p><p>To bring out these actions the aforementioned Directorate, with principle operational objective to plan, monitor and evaluate actions under the National Strategy for Safety and Health at Work, is divided into (6) six departments:</p><ul type="square"><li>Department of Working Conditions.</li><li>Department of Health Promotion, Harmful factors, Safety and Ergonomics.</li><li>Department of the National Focal Point for OSH Information and Documentation.</li><li>Department for the Major Accidents (under the Seveso Directive).</li><li>Department for OSH Training and Monitoring of Policies on OSH issues.</li><li>Department for People with Disabilities.</li></ul>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5089, 24717, 'EN', 1, 'The Labour Inspectorate (SEPE)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5090, 24718, 'EN', 1, 'See more in its <a target="_blank" href="http://www.ypakp.gr/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5091, 24719, 'EN', 1, 'Law 2639/1998 [21] instituted the Labour Inspectorate (SEPE) [22] in its current form. Its Central Services are located in Athens and a number of Regional Services are located throughout the country. The Regional Services are divided into two categories: Sixteen (16) Regional Directorates for the Inspection of Social Matters such as illegal occupation, lack of social security cover etc. and five (5) Regional Directorates for the Inspection of Safety and Health at Work (former Centres for the Prevention of Occupational Risk-KEPEK) which involve inspection of work conditions for safety, health and hygiene matters. The Labour Inspectorate is responsible for the inspection and application of the national legislation either on social matters (work times, wages, social security, occupational legality) or on the physical conditions of work (safety and health) and plays also an important consulting role for properly informing and guiding the employers in order to achieve a better compliance level. Law 3996/2011 [23] has introduced reforms to the Inspectorate.\r\nTo fulfil its role, the  inspectors for the safety and health at work can enter at any time in private or public workplaces without prior notice. It examines the implementation of OSH legislation. In case of violations it can impose administrative sanctions and fines. In case of major violations the inspectorate can order the temporary or permanent closure of a section or of the entire company. It investigates and reports on the causes of serious and fatal accidents. It investigates the causes of occupational diseases, takes samples and determines physical, chemical and biological parameters. Finally, it intervenes to resolve disputes in the workplace.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5092, 24720, 'EN', 1, 'The Social Insurance Institute (IKA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5093, 24721, 'EN', 1, 'See more in its <a target="_blank" href="http://www.ika.gr/en/home.cfm">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5094, 24722, 'EN', 1, '<p>The Social Insurance Institute (IKA) is by far the largest social security organization in Greece. IKA covers salaried employees in Greece or abroad for an employer who is based in Greece, as well as those employees who offer full-time or part-time personal labour on commissioned work agreements and who are not insured with any other main insurance agency.</p><p>In Greece the old Royal Decree 473/1961 concerning "Contributions for Occupational Risk” is still in force. This Decree obliges nearly all employers to pay an insurance contribution to IKA amounting to 1% of the total wages. Theoretically this contribution is paid for protection against occupational risks. However, in reality the sum is not used for this purpose and is redirected to further finance IKA’s obligations (pensions etc). Given that occupational diseases are significantly underreported, this leads to reduced insurance coverage. A continuous demand by the Trade Unions is the establishment of a Body of Insurance against Occupational Risks under IKA’s umbrella.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5095, 24723, 'EN', 1, 'The single State Social Security Organization (EFKA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5096, 24724, 'EN', 1, 'See more in its <a target="_blank" href="http://www.efka.gov.gr/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5097, 24725, 'EN', 1, 'Since the 1st of  January 2017 a single State Social Security Organization named EFKA has been put in operation as the result of the unification of the pre-existing social security institutions operating in Greece. Regulated by the law n.4387 (FEK 85/A’/12-5-2016) and with a view to ensuring a decent living and social protection, in terms of equality, social justice, redistribution and solidarity of generations, all state social benefits are granted within the framework of the umbrella organization EFKA which includes the National Health System for health benefits, the National Social Solidarity System for the welfare benefits and the National Social Security Funds for the  insurance benefits. Everyone is now insured with EFKA, employers and employees either as wage earners or self-employed pay their contributions to EFKA and all pensioners receive their main pension from the same organization.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5098, 24726, 'EN', 1, 'The Hellenic Organization for Standardization (ELOT)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5099, 24727, 'EN', 1, 'See more in its <a target="_blank" href="http://www.elot.gr/default_en.aspx">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Greece">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5100, 24728, 'EN', 1, 'The Hellenic Organization for Standardization (ELOT) is the national body responsible for the elaboration, approval, publication and distribution of Hellenic Standards. The elaboration of Standards is entrusted to ELOT’s Technical Committees and Working Groups. Representatives from both the public and the private sector express their opinions on the different issues in order to achieve maximum possible consensus among them. The Technical Committee 59 (Health and Safety at Work) is responsible for the elaboration of standards on OSH issues. The Ministry of Labour, the Ministry of Development, the Technical Chamber of Greece (TEE), the Greek Chemists Association (EEX), the Greek Society for Environmental and Occupational Medicine (EEIEP), the General Confederation of Greek Workers (GSEE), the Federation of Greek Industries (SEV) as well as representatives from the private sector provide their expert opinion to this Technical Committee.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5101, 24729, 'EN', 1, 'Ministry for National Economy (Nemzetgazdasági Minisztérium – NGM)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5102, 24730, 'EN', 1, 'See more in its <a target="_blank" href="http://www.kormany.hu/en/ministry-for-national-economy">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Hungary">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5103, 24731, 'EN', 1, 'The minister is responsible for employment policy, professionally controls and is the authority for OSH inspection activities of the district offices of the government offices that are acting as OSH authority. The Department of Occupational Safety and Health (Munkavédelmi F?osztály) is within the State Secretariat for Vocational Training and the Labour Market (Munkaer?piacért és Képzésért Felel?s Államtitkárság). The Department performs legislative, coordination and functional activities. The most important tasks are the authoritative and professional control, state control and supervision tasks related to occupational safety, data collection and statistical analysis. Furthermore, it informs and counsels the public about issues concerning occupational safety and health. The Department performs certain authority activities as occupational safety expert and operates the information technology system of the OSH authority. In addition to this, the Department participates in national and international committees and operates the Hungarian Focal Point of the European Union Agency for Safety and Health at Work');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5104, 24732, 'EN', 1, 'National Public Health Institute (Országos Közegészségügyi Intézet – OKI)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5105, 24733, 'EN', 1, 'See more in its <a target="_blank" href="https://www.antsz.hu/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Hungary">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5106, 24734, 'EN', 1, 'The National Public Health Institute (Országos Közegészségügyi Intézet – OKI) is the background institution of the ministry responsible for health (EMMI), which was founded by merging other institutes (National Centre for Epidemiology – OEK, National Public Health Center – OKK, Office of the Chief Medical Officer – OTH), delegating task to the Ministry and reducing personnel. Its Occupational Health Directorate (Munkahigiénés és Foglalkozás-egészségügyi Igazgatóság – MFI) is the successor of the National Institute of Occupational Health (Országos Munkahigiénés és Foglalkozás-egészségügyi Intézet – OMFI). The Directorate professionally controls the field of occupational health (occupational medicine and work hygiene), being the organisational part of OKI, who is the body granted with national occupational health powers. Most important tasks in the (i) professional control, (ii) expertise, (iii) training, further education, information provision, (iv) international professional fields are: development of standard protocols, giving background support and expertise, activities specified in legislation, assessment and analysis, (further)education, publication of professional journals, and international co-operation. In addition to this, the Directorate performs laboratory measurements and special medical examinations.[38]');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5107, 24735, 'EN', 1, 'The occupational safety and health units of the Metropolitan and County Government Offices (F?városi és Megyei Kormányhivatalok munkavédelmi egységei)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5108, 24736, 'EN', 1, 'See more in its <a target="_blank" href="http://www.kormanyhivatal.hu/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Hungary">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5109, 24737, 'EN', 1, 'The organisation of metropolitan and county government agencies belongs to the Prime Minister\'s Office. District offices in the county-seats perform the OSH inspections. There are occupational safety and occupational health experts available to control the meeting of legal requirements (inspection, sanctions), counselling and the investigation of work accidents and occupational diseases. The units can ask for support from other departments within the office (e.g. public health).\r\nOther supervisory organs also perform occupational safety tasks such as the Hungarian Office for Mining and Geology (Magyar Bányászati és Földtani Hivatal). In case of certain distinct areas (armed forces, law enforcement and disaster management bodies, etc.) the given sector issues safety and health regulations pursuant to special authorization in the law. Furthermore, these bodies supervise the enforcement of the OSH regulations falling within their competence. Recently there is an approximation towards the civilian legislation.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5110, 24738, 'EN', 1, 'NONE');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5111, 24739, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Hungary">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5112, 24740, 'EN', 1, '<p>The current Hungarian regulations do not separate the accident insurance system, although the issue of its introduction is repeatedly being raised in national strategies. The employer pays social contribution tax after the employees, which partly covers health care costs as well. The value of the contribution, which is set in legislation, is the same for every employer and employee and is independent from the dangerousness of the job. Thus the employer is not directly interested in decreasing the risks inherent in the work environment.</p><p>Following the dissolution of the National Office for Rehabilitation and Social Affairs (Nemzeti Rehabilitációs és Szociális Hivatal – NRSZH), tasks like the establishment of disability pension, accident disability pension, accident-related allowance, rehabilitation allowance, health damage annuity for miners and the establishment of degree of lasting health damage and the portion thereof arisen in relation to occupation […]are administered at various places. Generally, cases are handled in district offices, while the Rehabilitation Department in the Government Office of Budapest (Budapest F?város Kormányhivatala Rehabilitációs F?osztálya) and és a (Szociális és Gyermekvédelmi F?igazgatóság – SZGYF) national level.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5113, 24741, 'EN', 1, 'NOT SPECIFIED');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5114, 24742, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Hungary">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5115, 24743, 'EN', 1, 'There are a number of market-based, well-equipped nationwide occupational health and occupational safety service providers which aim at satisfying the requirements for services.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5116, 24744, 'EN', 1, 'Hungarian Standards Institution (Magyar Szabványügyi Testület – MSZT)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5117, 24745, 'EN', 1, 'See more in its <a target="_blank" href="Hungarian Standards Institution (Magyar Szabványügyi Testület – MSZT)">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Hungary">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5118, 24746, 'EN', 1, 'The Hungarian Standards Institution (Magyar Szabványügyi Testület – MSZT) is an independent, non-profit public body the main activities and services of which include: standardisation, certification, adult education, issue of standards, distribution of standards, information supply');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5119, 24747, 'EN', 1, 'The Health and Safety Authority (HSA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5120, 24748, 'EN', 1, 'See more in its <a target="_blank" href="http://www.hsa.ie">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5121, 24749, 'EN', 1, '<p>The Health and Safety Authority (HSA) is the national statutory body with responsibility for enforcing occupational safety and health law, promoting and encouraging accident prevention, and providing information and advice to all companies, organisations and individuals. The aim of the Authority is to make occupational safety, health and welfare an integral part of doing business in every Irish workplace.</p><p>&nbsp;</p><p>To ensure compliance with the legislation, the HSA seeks, primarily, to reduce workplace accidents by providing guidance and support to employers and employees. Inspectors of the HSA carry out reactive and pro-active inspections of workplaces. Reactive inspections may arise following an accident, incident or complaint. Pro-active inspections may be routine or targeted. The purpose of an inspection is to assist the HSA in carrying out some of the general functions assigned by Section 34 of the Safety, Health and Welfare at Work Act 2005 and include:</p><p>&nbsp;</p><ul><li>Monitoring the implementation of, and compliance with, statutory health, safety and welfare requirements</li><li>Enforcing any relevant statutory provisions set out in safety, health and welfare legislation</li><li>Promoting, encouraging and advising employers and employees in relation to health and safety training</li><li>Promoting and encouraging measures aimed at the prevention of accidents, dangerous occurrences and personal injury at work</li><li>Providing information and advice on matters relating to safety, health and welfare</li><li>Promoting and encouraging the implementation of best practice methodologies and processes</li></ul>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5122, 24750, 'EN', 1, 'Personal Injuries Assessment Board');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5123, 24751, 'EN', 1, 'See more in its <a target="_blank" href="https://www.piab.ie">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5124, 24752, 'EN', 1, 'Under the Personal Injuries Assessment Board Act 2003, anyone intending to seek compensation for a personal injury (other than a personal injury arising out of medical negligence) must make an application through InjuriesBoard.ie. They are the statutory body which provides independent assessment of personal injury compensation for victims of accidents, including workplace accidents. Injuries Board.ie is staffed with public servants with relevant legal, insurance and medical expertise to assess OSH compensation claims, with an established independent panel of medical experts throughout Ireland. Injuries and ill-health from work related accidents and illnesses are claimed for by employees using employer’s public liability insurance through the courts for civil compensation. The state also has a system of Occupational injury and illness benefits paid to those out of work. These are paid from the budget of the Dept of Social and Family Protection (DSFP).');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5125, 24753, 'EN', 1, 'The Institute of Occupational Safety and Health (IOSH)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5126, 24754, 'EN', 1, 'See more in its <a target="_blank" href="https://www.iosh.co.uk/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5127, 24755, 'EN', 1, 'IOSH was founded in 1945 and is the Chartered body for health and safety professionals. With more than 40,000 individual members, they are the biggest professional health and safety organisation in the world. The Ireland branch of IOSH was founded in 1982. Currently in the Ireland Branch there are five districts and five specialist sections. The Ireland Branch committee consists of the Chairperson, Vice-Chairperson, Treasurer, Secretary, Development Officer, some non-officer members, a representative from each of the districts and specialist sections.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5128, 24756, 'EN', 1, 'National Irish Safety Organisation (NISO)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5129, 24757, 'EN', 1, 'See more in its <a target="_blank" href="https://niso.ie/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5130, 24758, 'EN', 1, '<p>NISO provides information and services to help improve safety and the workplace. Their services include:</p><ul><li>Providing leading information, advisory and training services</li><li>Promoting a culture of excellence in workplace health &amp; safety</li></ul><p>Playing a leading role in advancing the national health &amp; safety agenda</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5131, 24759, 'EN', 1, 'The Occupational Health Nurses Association of Ireland (OHNAI)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5132, 24760, 'EN', 1, 'See more in its <a target="_blank" href="http://www.ohnai.ie/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5133, 24761, 'EN', 1, '<p>OHNAI is the national body for Occupational Health Nurses who work either as individual practitioners or team members in a workplace setting. The association supports it\'s members in their role of:</p><ul><li>Preventing accidents and ill health at work.</li><li>Providing care to employees through health promotion, health surveillance, rehabilitation and management of health and safety.</li></ul><p>Working to ensure professionalism, quality and legal compliance in occupational health practice.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5134, 24762, 'EN', 1, 'NSAI (National Standards Authority of Ireland)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5135, 24763, 'EN', 1, 'See more in its <a target="_blank" href="https://www.nsai.ie">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Ireland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5136, 24764, 'EN', 1, 'NSAI (National Standards Authority of Ireland) is Ireland’s official standards body. They operate under the National Standards Authority of Ireland Act (1996) and are accountable to the Minister of Jobs, Enterprise and Innovation. NSAI are the national certification authority for CE Marking and provide a certification service to enable business demonstrate that Irish goods and services conform to applicable standards. As Ireland’s Official standards body, NSAI aims to inspire consumer confidence and create the infrastructure for products and services to be recognized and relied on, all over the world.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5137, 24765, 'EN', 1, 'The Ministry of Labour and Social Policies');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5138, 24766, 'EN', 1, 'See more in its <a target="_blank" href="http://www.lavoro.gov.it/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Italy">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5139, 24767, 'EN', 1, '<p>The Ministry of Labour and Social Policies, in line with the provisions laid down in Legislative Decree n. 81 of April 9th 2008, is committed to build and disseminate safety and prevention culture, giving higher importance to all activities and initiatives contributing to promote responsible and self-aware behaviors in workers, addressed not only to one’s own safety but also of others, and to identify strategies aimed at effectively fighting against the occurrence of accidents at work. With regard to that, the Ministry, sustained by an ongoing dialogue with social actors working every day on the ground of a concrete implementation of safety rules, in addition to proceeding on the legal side to finalise the reform of the regulation in force in this sector, also carries on its monitoring action of the implementation process and the situation of the provisions in force; such an action is aimed at examining relevant implementation issues and, in view of this, to develop interventions and actions aimed at improving the same regulation.</p><p>&nbsp;</p><p>In such a context, the competences of Division III and VI of the General Direction of Working Conditions Protection fall within this scope (whose competences were included in the current General Direction of industrial relations and labour relationships.</p><p>&nbsp;</p><p>Division III manages the procedures for the registration in qualified expert and authorized medical doctors lists, the Special Fund for Accidents for the financing of studies and research in the field of accidents and injuries; it provides for the collection and cataloguing of examination results carried on at the Provincial Directions of Labour concerning the qualification for handling and driving steam generators; it carries on consultancy activities relating to moral violence (so called “mobbing”) at workplace and collaborates at the organization of events and workshops on health and safety at work. Division VI works, in general, on all the profiles of health and safety of workers, in particular through the participation to European projects and preparation of texts for the implementation of relevant directives. In addition, it expresses opinions about the application of the regulation of reference with special regard to the Legislative Decree of April 9th 2008, no. 81 Implementation of the Article 1 of the Act no. 123, August 3rd, 2007, concerning the protection of health and safety at workplaces (workplaces, use of equipment and personal protection equipment, etc.); it provides for the authentication of a “personal book of radioprotection” for workers exposed to ionizing radiation risk; it manages the permanent advisory committee for health and safety at work.</p><p>&nbsp;</p><p>The inspection staff of the Ministry of Labour and Social Policies performs an inspection activity on the implementation of legislation concerning health and safety at work in the following activities, in the framework of the territorial coordination provided by regional Committees:</p><ol><li>Activities in the sector of building, construction and civil engineering and, more in particular, works of construction, maintenance, repair, demolition, preservation and renovation of stable, permanent or temporary manufacts, both in armed concrete and brickwork, road works, railway works, hydraulic works, excavations, assembling and disassembling of pre-manufactured elements; subterranean and gallery works, including the use of explosives;</li><li>Works using compressed air containers and works to be carried out underwater;</li><li>Any other working activities involving particularly high risks.</li></ol>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5140, 24768, 'EN', 1, 'Ministry of Health');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5141, 24769, 'EN', 1, 'See more in its <a target="_blank" href="http://www.salute.gov.it/portale/home.html">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Italy">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5142, 24770, 'EN', 1, 'The General Directorate for prevention at the Ministry of Health performs, among others, the following functions: -	Prevention of occupational diseases and accidents, including other health-related competences, as defined by the Legislative Decree 81, Implementation of the Article 1 of the Act no. 123, August 3rd, 2007, concerning the protection of health and safety at workplaces of April 9th, 2008. Prevention of exposure to chemical, physical and biological agents in the natural and living environment, in water for human consumption and in working environment. The functions of the Conference of Regions and Autonomous Provinces may be distinguished into 3 major priority areas: improving the connection to and the dialogue with the central State through the development of documents shared by the entire “system of regional Governments”; the establishment of a permanent interregional dialogue in order to disseminate best practices; the necessity of representing the “system of regional Governments” on an ongoing basis at the external and in the institutional relations In particular, the IX Commission on Education, Labour, Innovation and Research is competent in relation to: education and professional training, labour policies, safety at work, university and scientific research, professions. Inspection services According to the provisions laid down in Art. 13 of legislative Decree no. 81/2008 Legislative decree no. 81, April 9th 2008, Implementation of the Article 1 of the Act no. 123, August 3rd, 2007, concerning the protection of health and safety at workplaces, the inspection services on compliance with safety and health regulations are carried on by ASL, which are competent on the territory and, as for its specific competence, by the national Fire brigade department.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5143, 24771, 'EN', 1, 'Italian Workers\' Compensation Authority - Istituto Nazionale per l’assicurazione contro gli infortuni sul lavoro (INAIL)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5144, 24772, 'EN', 1, 'See more in its <a target="_blank" href="https://www.inail.it">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Italy">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5145, 24773, 'EN', 1, 'INAIL, the Italian Workers’ compensation Authority, pursues a plurality of objectives: to reduce injury occurrence; to provide an insurance for workers carrying on activities at risk; to guarantee the reinsertion of injured workers in the working life. The protection of workers, also in the light of the recent law developments, has assumed an ever more character of integrated system of protection, stretching from prevention actions at workplaces, to health and economic benefits, care, rehabilitation and reinsertion in social and occupational life in the case of workers who suffered from physical injuries due to accidents at workplace or occupational diseases. In order to reduce accidents, INAIL carries on important actions aimed at monitoring, on an ongoing basis, the employment and accident trend, training and assistance to small and medium enterprises in relation to prevention and funding of companies investing in the improvement of safety.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5146, 24774, 'EN', 1, 'Italian Workers\' Compensation Authority - Istituto Nazionale per l’assicurazione contro gli infortuni sul lavoro (INAIL)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5147, 24775, 'EN', 1, 'See more in its <a target="_blank" href="https://www.inail.it">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Italy">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5148, 24776, 'EN', 1, 'The Act no. 122  of July 30th, 2010 states the assignment to INAIL of the functions formerly carried on by ISPESL(Istituto Superiore per la Prevenzione e la Sicurezza sul Lavoro - National Institute for prevention and safety at work) in the field of technical-scientific activites, research, experimentation, control, consultancy, assistance and higher training. Accidents prevention , occupational safety, protection of health in living and working environments, training and promotion of safety culture are the main issues dealt with in the research projects that the Institution performs and/or finances. Other functions include the activites of first verification, verification of first set up of work equipment under this regime and random verification of putting to earth and protection against atmospherical discharge in all working activities; certification and acknowledgement of laboratories and certification bodies provided by European regulation and international agreements, as well as inspection and control of industries at risk of severe accidents in relation to specific activites. In the area of market control, the Institute performs controls, on behalf of the Ministry for Economic Development, of conformity to safety and health requirements for products used by workers; as Notified Body it issues, also through its local departments, declarations of conformity to the PED, TPED, SVP, ATEX European Directives. Research in the field of occupational safety is also carried on by Universities and ISS (Istituto Superiore di Sanità -National Health Institute).');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5149, 24777, 'EN', 1, 'UNI – National Italian Unification Body, Ente Nazionale italiano di Unificazione');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5150, 24778, 'EN', 1, 'See more in its <a target="_blank" href="http://www.uni.com/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Italy">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5151, 24779, 'EN', 1, 'UNI  is a non-for-profit private association founded in 1921 and recognised by the State and the European Union; it studies, elaborates, approves and publishes the voluntary technical dispositions – the so-called “UNI standards” – in all industrial, commercial and service sectors (except the electrical and electrotechnical ones). UNI associates are enterprises, professionals, associations, public bodies, research centres and schools. UNI represents Italy at the European (CEN) and Worldwide (ISO) standard organisations. The purpose of this body is to define technical rules and standards to contribute to the improvement of efficiency and effectiveness of the Italian social and economical system, by supporting tools for technological innovation, competitiveness, promotion of trade, protection of consumers and environment, product and process quality.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5152, 24780, 'EN', 1, 'Ministry of Welfare');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5153, 24781, 'EN', 1, 'See more in its <a target="_blank" href="http://www.lm.gov.lv/ \r\r\n">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Latvia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5154, 24782, 'EN', 1, '<p>The main tasks of the Ministry of Welfare in the field of occupational safety and health are following:</p><ul type="square"><li>To develop the national policy of OSH and to facilitate its implementation;</li><li>To ensure elaboration of national OSH legislation and its compliance with the EU and international legal acts in this field;</li><li>To promote creation of safe and harmless working environment, protection of the employees’ right to occupational health and safety and social guarantees;</li><li>To facilitate the process of development of OSH administration system;</li><li>To promote informing of employers and employees on occupational safety and health issues.</li></ul><p>In order to achieve these objectives the Ministry of Welfare is actively working together with several other institutions and social partners. The structure of the Ministry of Welfare is based on several departments of which the Department of Labour Relations and OSH Policy is responsible for OSH area. Several other departments are involved (like Social Insurance Department, Department of Labour Market policy etc.).&nbsp;</p><p>The Ministry of Welfare mainly collaborates with all the institutions under its supervision and authority, including the key institution for supervision and control of the occupational safety and health protection system – the State Labour Inspectorate. The Ministry also collaborates with social partners like the Latvian Trade Union Confederation and the Employers’ Confederation of Latvia. Collaboration is mostly accomplished during the process of elaboration of new OSH legislative acts. Representatives of the social partners are involved in the process as from the initial stages of drafting legislation providing opportunity for their contribution co-ordinate joint activities. The Department of Labour Relations and OSH Policy in collaboration with the State Labour Inspectorate is also responsible for coordination of proposals for the annual preventive action plan funded by the State Social Insurance Agency (Valsts Soci?l?s apdrošin?šanas a?ent?ra).</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5155, 24783, 'EN', 1, 'State Labour Inspectorate');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5156, 24784, 'EN', 1, 'See more in its <a target="_blank" href="http://www.vdi.gov.lv/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Latvia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5157, 24785, 'EN', 1, '<p>The State Labour Inspectorate is the key control and supervisory institution in the field of OSH operating under the supervision of the Ministry of Welfare (it is the direct management authority). The legal status, function, tasks and the operational procedure of the State Labour Inspectorate is defined in the State Labour Inspectorate Law adopted on 19 June 2008. The State Labour Inspectorate is the only governmental institution that carries out the implementation of the policy in the field of labour legal relations and occupational safety and health. The State Labour Inspectorate carries out various activities to ensure that the Latvian population are socially and legally protected and could work in a safe working environment.</p><p>The State Labour Inspectorate has set following mission - to achieve observation of legislation on labour legal relations and occupational safety and health in Latvia in order to be established and maintained a safe, healthy working environment and proper legal labour relations, thereby reducing the number of occupational patients and victims of the accidents at work.</p><p>The State Labour Inspectorate has following main functions:</p><ul><li>Supervise and control observance of the requirements of the regulatory enactments regarding labour legal relationships and OSH.</li><li>Control how employers and employees mutually fulfil the obligations set by employment contracts and collective labour agreements.</li></ul><p>Provide free consultations to employers and employees on labour legal relations and OSH.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5158, 24786, 'EN', 1, 'State Social Insurance Agency');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5159, 24787, 'EN', 1, 'See more in its <a target="_blank" href="http://www.vsaa.lv/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Latvia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5160, 24788, 'EN', 1, 'There is only one insurance and compensation body in Latvia as the social insurance system and accident insurance system is state owned. The system is provided by the State Social Insurance Agency working under the supervision of the Ministry of Welfare. The main functions of the State Social Insurance Agency include administration of the special social insurance budgets (including insurance against accidents at work and occupational diseases) and the public social services. The State Social Insurance Agency also provides funding for preventive activities in the field of occupational health and safety. System for insurance against accidents at work and occupational diseases is set by the Law on Compulsory Social Insurance in respect of Accidents at Work and Occupational Diseases. The State Social Insurance Agency is handling the compensation system for occupational accidents and diseases. Generally every employee that is legally employed is insured against occupational accidents and diseases as small part of social tax is paid into special social insurance budget. If there is an occupational accident, it must be properly investigated and registered in the State Labour Inspectorate in accordance with the Cabinet Regulations "Procedures for Investigation and Registration of Accidents at Work". Occupational diseases are recognised and registered by the Centre of Occupational and Radiation Medicine (Aroda un radi?cijas medic?nas centrs) in accordance with the Cabinet Regulations "Procedures on Investigation and Registration of Occupational Diseases".');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5161, 24789, 'EN', 1, 'None');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5162, 24790, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Latvia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5163, 24791, 'EN', 1, 'There are no official prevention institutions in the field of OSH in the traditional sense of the word. Currently the only institution that has governmental funding and functions is the Institute for Occupational Safety and Environmental Health (Darba droš?bas un vides vesel?bas instit?ts) of the Riga Stradi?š University that is an academic, scientific and training institution. The Institute for Occupational Safety and Environmental Health has several functions including training for undergraduate and postgraduate students on topics, concerning occupational health and safety, occupational medicine and environmental health, research and expert services.\r\nAccording to the "Law on Compulsory Social Insurance in respect of Accidents at Work and Occupational Diseases" every year 0.5% of the special insurance budget is spent on preventive activities in the field of occupational health and safety. The annual plan is approved by the Information Board of the Latvian Focal point for the European Agency for Safety and Health at Work which consists of competent representatives from the Ministry of Welfare, the State Labour Inspectorate, the Latvian Free Trade Union Federation, the Latvian Employers’ Confederation, as well as the Institute of Occupational Safety and Environmental Health .');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5164, 24792, 'EN', 1, 'Latvian Standard (Standartiz?cijas birojs)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5165, 24793, 'EN', 1, 'See more in its <a target="_blank" href="http://www.lvs.lv/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Latvia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5166, 24794, 'EN', 1, '<p>The Latvian Standard&nbsp; is one of three independent agencies operating as part of limited liability company the Standardization, Accreditation and Metrology Centre (SIA „Standartiz?cijas, akredit?cijas un metrolo?ijas centrs”). It works in accordance with the Standardisation Law. It carries out its specific functions that are:</p><ul type="square"><li>To organize development of the Latvian national standards in standardization technical committees;</li><li>To adapt all the standards published by European standardization bodies and international standards which are essential for the national economy;</li><li>To ensure a stock formation of standards and related documents, the stock maintenance and amendment, as well as information dissemination in the field of standardization;</li></ul><p>To participate in the work of international and regional standardization bodies.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5167, 24795, 'EN', 1, 'The Ministry of Social Security and Labour (Socialin?s apsaugos ir darbo ministerija)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5168, 24796, 'EN', 1, 'See more in its <a target="_blank" href="http://www.socmin.lt/\r\r\n">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5169, 24797, 'EN', 1, 'The Ministry of Social Security and Labour (Socialin?s apsaugos ir darbo ministerija) and the Ministry of Health (Sveikatos apsaugos ministerija) implement State policy in the OSH area in accordance with the Constitution of the Republic of Lithuania, the Labour Code, the laws, resolutions of the Government and other regulations. The Minister of Social Security and Labour himself or together with another minister or ministers approves the OSH regulations establishing the procedure for their entry into force and application. The Health Minister approves health care regulations (hygiene norms), which establish the levels of working environment factors not harmful to workers’ health.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5170, 24798, 'EN', 1, 'The Ministry of Health (Sveikatos apsaugos ministerija)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5171, 24799, 'EN', 1, 'See more in its <a target="_blank" href="http://www.sam.lt/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5172, 24800, 'EN', 1, 'The Ministry of Social Security and Labour (Socialin?s apsaugos ir darbo ministerija) and the Ministry of Health (Sveikatos apsaugos ministerija) implement State policy in the OSH area in accordance with the Constitution of the Republic of Lithuania, the Labour Code, the laws, resolutions of the Government and other regulations. The Minister of Social Security and Labour himself or together with another minister or ministers approves the OSH regulations establishing the procedure for their entry into force and application. The Health Minister approves health care regulations (hygiene norms), which establish the levels of working environment factors not harmful to workers’ health.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5173, 24801, 'EN', 1, 'The State Labour Inspectorate');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5174, 24802, 'EN', 1, 'See more in its <a target="_blank" href="http://www.vdi.lt/\r\r\n">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5175, 24803, 'EN', 1, '<p>Remit of the State Labour Inspectorate under the Ministry of Social Security and Labour includes the prevention of accidents at work, occupational diseases and violations of occupational safety and health requirements of standard acts on labour law. It also covers the control of compliance of the Labour Code of the Republic of Lithuania, laws and other standard acts regulating occupational safety and health as well as labour relations in enterprises, institutions, organisations or other organisational structures, irrespective of their forms of ownership, type, nature of activity, also in these cases when an employer is a natural person (hereinafter referred to as the „employers“).</p><p>The State Labour Inspectorate consists of the administration and territorial divisions. The administration consists of the Chief State Labour Inspector and his deputies as well as divisions and services coordinating and organising activities of the State Labour Inspectorate. The number and subordination of these divisions and services as well as territorial divisions is established by approval of the structure of the State Labour Inspectorate. Sectors uniting specialists of certain activities may be established within the structural subdivisions.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5176, 24804, 'EN', 1, 'The State Social Insurance Fund (SODRA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5177, 24805, 'EN', 1, 'See more in its <a target="_blank" href="http://www.sodra.lt/index.php?cid=2585">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5178, 24806, 'EN', 1, '<p><em>The State Social Insurance Fund (SODRA)</em>&nbsp;has been established in order to implement the social insurance. SODRA is implementing the state social insurance for already twenty years. The system guarantees income for the insured persons and persons who have lost capacity for work due to sickness, also in the case of maternity, paternity, old-age, disability, or other instances set out in the laws with seeking to reduce or to compensate for lost income due to unemployment. The state social insurance system is based on the principle of generation solidarity and current financing, when the collected contributions are distributed right away. Therefore it is very closely related to and dependent on the country’s economic potential. The country’s economic growth rates started to slowdown from 2008 followed by increasing unemployment and enterprise bankruptcies what has resulted in an economic decline. This had a detrimental effect on the social insurance system.</p><p>The state social insurance system is comprised of:</p><ul type="disc"><li>Ministry of Social Security and Labour.</li><li>Council of the State Social Insurance Fund.</li><li>State Social Insurance Fund Board and its territorial offices and other institutions relate to the Fund administration.</li><li>State Tax Inspectorate under the Ministry of Finance.</li><li>Lithuanian Labour Exchange under the Ministry of Social Security and Labour.</li><li>State Patient Fund under the Ministry of Health.</li><li>Pension saving companies.</li><li>SODRA’s clients (insurers, insured and benefit recipients).</li></ul><p>In Lithuania, like in a number of countries of the world, there are following types of the state social insurance:</p><ul type="square"><li>Pension social insurance for the basic or supplementary parts of pension.</li><li>Sickness and maternity social insurance.</li><li>Occupational accidents and occupational diseases social insurance.</li><li>Health insurance.</li></ul><p>Most persons are covered by these types of the state social insurance on a compulsory basis. Persons who are not insured by the compulsory state social insurance may insure themselves by the pension social insurance and social insurance for sickness and maternity benefits on voluntary basis. They are insured by making individual agreements with the territorial offices of the State Social Insurance Fund Board and paying defined social insurance contributions.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5179, 24807, 'EN', 1, 'The Public Health Centres (Visuomen?s sveikatos centrai)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5180, 24808, 'EN', 1, 'See more in its <a target="_blank" href="http://www.sam.lt/index.php?597392768">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5181, 24809, 'EN', 1, 'The Public Health Centres (Visuomen?s sveikatos centrai) under the Ministry of Health have been established seeking to ensure public health safety and to protect rights of the health service consumers by implementing the state health policy. The main tasks of the Public Health Centres are to implement national public health safety control and to ensure safety of the environment according to the law requirements.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5182, 24810, 'EN', 1, 'The Occupational Health Centre (Profesin?s sveikatos centras)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5183, 24811, 'EN', 1, 'See more in its <a target="_blank" href="http://www.hi.lt/profesines-sveikatos-centras.html">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5184, 24812, 'EN', 1, 'The Occupational Health Centre (Profesin?s sveikatos centras), previously called Occupational Medicine Centre (established in 1992 at the Institute of Hygiene), is a national institution, responsible for research, training, expertise, consultation and information in the field of occupational health in Lithuania.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5185, 24813, 'EN', 1, 'State Energy Inspectorate');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5186, 24814, 'EN', 1, 'See more in its <a target="_blank" href="http://www.vei.lt/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5187, 24815, 'EN', 1, 'The State Energy Inspectorate (Valstybin? energetikos inspekcija) [54] under the Ministry of Energy (Energetikos ministerija) was established to control the energy sector seeking to ensure reliability, efficiency and safety of energy sources, energy production, supply and use, to manage public resources and the reserve energy supplies.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5188, 24816, 'EN', 1, 'State Non Food Products Inspectorate');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5189, 24817, 'EN', 1, 'See more in its <a target="_blank" href="http://www.inspekcija.lt/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5190, 24818, 'EN', 1, 'The State Non Food Products Inspectorate (Valstybin? ne maisto produkt? inspekcija) under the Ministry of Economy (?kio ministerija) was established in 2000 after the reorganization of the the Lithuanian State Quality Inspectorate, the State Hygiene Inspectorate and the State Veterinary Service. The main objectives of the Inspectorate are to protect the national market from dangerous and low quality non food products (articles and services) and to ensure an effective market surveillance of non food products in compliance with the European Union law. From 2016 functions of the State Non Food Products Inspectorate are assigned to the State Consumer Rights Protection Authority.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5191, 24819, 'EN', 1, 'State Fire and Rescue Service');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5192, 24820, 'EN', 1, 'See more in its <a target="_blank" href="http://www.vpgt.lt/go.php/lit/English/49">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5193, 24821, 'EN', 1, 'The State Fire and Rescue Service (Valstybin? priešgaisrin? gelb?jimo tarnyba) is made up of the following bodies: the Fire and Rescue Department (Priešgaisrin?s apsaugos ir gelb?jimo departamentas) under the Ministry of the Interior and its 17 subordinate services, the Specialized Fire and Rescue Service, the Firefighters Training School, the Fire Research Centre and the Emergency Response Centre. The Fire and Rescue Department under the Ministry of the Interior is responsible for the protection of people, property and environment in emergency cases. In addition, it is in charge of fire and emergency prevention.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5194, 24822, 'EN', 1, 'Radiation Protection Centre');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5195, 24823, 'EN', 1, 'See more in its <a target="_blank" href="http://www.rsc.lt/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5196, 24824, 'EN', 1, 'The Radiation Protection Centre (Radiacin?s saugos centras) is an institution which co-ordinates the activities of executive and other bodies of public administration and local government in the field of radiation protection. It also exercises the state supervision and control of radiation protection, monitoring and expert examination of public exposure. The Radiation Protection Centre was established in 1997.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5197, 24825, 'EN', 1, 'State Territorial Planning and Construction Inspectorate');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5198, 24826, 'EN', 1, 'See more in its <a target="_blank" href="http://www.vtpsi.lt/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5199, 24827, 'EN', 1, 'The State Territorial Planning and Construction Inspectorate (Valstybin?s teritorij? planavimo ir statybos inspekcija) under the Ministry of Environment (Aplinkos ministerija) was established to take part in developing the state policy on territorial planning and the state supervision of construction and use of structures as well as to control and coordinate the implementation of this policy.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5200, 24828, 'EN', 1, 'State Road Transport Inspectorate');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5201, 24829, 'EN', 1, 'See more in its <a target="_blank" href="http://www.vkti.gov.lt/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5202, 24830, 'EN', 1, 'The State Road Transport Inspectorate (Valstybin? keli? transporto inspekcija) under the Ministry of Transport and Communications. Its main objectives are to ensure proper conditions for road transport activity and safety on roads. The Inspectorate officers supervise carriage of goods and passengers by the road transport, centres of the state technical inspection, driving schools and agencies which provide training on carrying dangerous goods. They also check how the drivers follow the driving and rest regime, control technical condition of vehicles including vehicles carrying dangerous goods and over-dimensioned and heavy goods vehicles.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5203, 24831, 'EN', 1, 'State Railway Inspectorate');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5204, 24832, 'EN', 1, 'See more in its <a target="_blank" href="http://www.dsti.lt/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5205, 24833, 'EN', 1, 'The State Railway Inspectorate (Valstybin? geležinkelio inspekcija) [66] under the Ministry of Transport and Communications [65] has to ensure safety of the railway transport and railway traffic.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5206, 24834, 'EN', 1, 'The Lithuanian standards board (Lietuvos standartizacijos departamentas (LSD)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5207, 24835, 'EN', 1, 'See more in its <a target="_blank" href="http://lsd.lt/index.php?256157671">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Lithuania ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5208, 24836, 'EN', 1, '<p>The Lithuanian standards board (Lietuvos standartizacijos departamentas (LSD)) was established in 1990. Lithuanian Standards Board is the budgetary institution of public administration functioning as National Standards Body (NSB) and within its competence taking part in establishing and implementing the policy of the Government of the Republic of Lithuania within the standardization field and taking active part in the activities of international and European standardization organizations by representing interests of Lithuanian economy.</p><p>LST develops and improves national standardization system, sets up technical committees for the preparation of Lithuanian standards and other publications as well as coordinates their preparation, establishes procedures for the adoption of international, European and foreign standards as Lithuanian standards, adopts, publishes and distributes Lithuanian standards and other publications applied on a voluntary basis, seeks that copyright of International and European standards is not infringed in Lithuania, provides information on standards, technical regulations and legal acts on conformity assessment procedures valid in Lithuania and their drafts.</p><p>LST implements Lithuanian standards program, participates in implementation of European Union directives. Activities of technical committees are based on the consensus principle, i.e. interested parties (producers, consumers and representatives of state, scientific and public organizations) make decisions by consensus. Standardization Council deals with strategical and inter-branch standardization matters. Technical committees assist in the implementation of Lithuanian standards program through the preparation of Lithuanian standards and other publications.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5209, 24837, 'EN', 1, 'The Luxembourg Institute for the Standardisation, Accreditation, Safety and Quality of Products and Services (Institut Luxembourgeois de la Normalisation et de l’Accréditation, de la Sécurité et Qualité des Produits et Services, ILNAS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5210, 24838, 'EN', 1, 'See more in its <a target="blank" href="https://portail-qualite.public.lu/fr/acteurs/ilnas.html">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Luxembourg">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5211, 24839, 'EN', 1, '<p>The Luxembourg Institute for the Standardisation, Accreditation, Safety and Quality of Products and Services (Institut Luxembourgeois de la Normalisation et de l’Accréditation, de la Sécurité et Qualité des Produits et Services, ILNAS) is an administrative body under the supervision of the Minister responsible for the economy. It was created on the basis of the law of 20 May 2008 and commenced its activities on 1 June 2008.</p><p>&nbsp;</p><p>ILNAS fulfils the following roles among others:</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Standardisation,</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; The accreditation of conformity assessment bodies,</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; The monitoring of general product safety,</p><p>The promotion of quality management.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5212, 24840, 'EN', 1, 'The Luxembourg Association of Designated Workers - Association des Travailleurs Désignés Luxembourg, ATDL');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5213, 24841, 'EN', 1, 'See more in its <a target="blank" href="http://atdl.lu/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Luxembourg">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5214, 24842, 'EN', 1, 'The main goal of the Luxembourg Association of Designated Workers (Association des Travailleurs Désignés Luxembourg, ATDL) is the exchanging of experience between members. The Association also gives its members help when they need it. The ATDL makes proposals to the authorities regarding adjustments to the function of the designated worker in order to help legislators compile future laws and Grand-Ducal regulations regarding the function of designated workers.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5215, 24843, 'EN', 1, 'The Luxembourg Association of Safety and Health Coordinators – Association des Coordinateurs Sécurité et Santé Luxembourg, ACSSL');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5216, 24844, 'EN', 1, 'See more in its <a target="blank" href="http://www.acssl.lu/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Luxembourg">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5217, 24845, 'EN', 1, '<p>The goal of the Luxembourg Association of Safety and Health Coordinators () is to:</p><ul><li>bring together safety and health coordinators in an association;</li><li>define the profession’s ethical and professional rules;</li><li>form study groups of relevance to the profession;</li><li>represent and defend the interests of the profession in the Grand Duchy of Luxembourg and abroad;</li><li>collaborate with or join any companies, associations or institutions in Luxembourg or abroad with similar, complementary or related goals;</li><li>offer its good offices to settle disputes and other controversial issues that are submitted to it;</li></ul><p>perform any activities directly or indirectly related to its goal and tending to further its attainment.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5218, 24846, 'EN', 1, 'The Accident Insurance Association (Association d’Assurance Accident, AAA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5219, 24847, 'EN', 1, 'See more in its <a target="blank" href="http://www.aaa.lu/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Luxembourg">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5220, 24848, 'EN', 1, 'The Accident Insurance Association (Association d’Assurance Accident, AAA) is a public institution responsible for the prevention and payment of compensation for work accidents and occupational diseases.\r\nThe organisation is overseen by the Ministry of Social Security and is run by a management committee consisting of an official appointed by the government to chair the committee, eight employers’ delegates and eight employees’ delegates.\r\nThe AAA provides insurance and pays compensation in the event of accident or illness for workers in both the private and public sector.\r\nThe organisation has a Prevention Service and a Payment Service (compensation). The reform of accident insurance, in 2010, put an end to the compulsory prevention regulations introduced specifically by the organisation in addition to the legislation on safety and health; these regulations were replaced by recommendations.\r\nThe role of the AAA’s prevention service is to:\r\n-	analyse the causes of work-related accidents and illnesses;\r\n-	monitor the legal and regulatory requirements with regard to OSH;\r\n-	devise prevention recommendations;\r\n-	inform, advise and raise the awareness of employers,\r\n-	propose financial incentives to employers (no claims bonus system, financial support for the management of occupational health and safety in companies).');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5221, 24849, 'EN', 1, 'The Inspectorate of Labour and Mines (Inspection du Travail et des Mines, ITM)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5222, 24850, 'EN', 1, 'See more in its <a target="blank" href="http://www.itm.lu">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Luxembourg">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5223, 24851, 'EN', 1, '<p>The Inspectorate of Labour and Mines (Inspection du Travail et des Mines, ITM) has the task of inspecting labour relations, working conditions and workers’ occupational health and safety in the private sector. The ITM is in charge in particular of ensuring that the provisions of the law and of agreements regard working conditions are applied, and of providing information and technical advice to employers and workers regarding the most effective means of adhering to the legal provisions.</p><p>&nbsp;</p><p>The ITM is also responsible for preventing and settling any labour disputes that do not fall within the competence of the National Conciliation Office (Office national de Conciliation).</p><p>&nbsp;</p><p>In terms of its structure, the ITM has a functional organizational chart since April 2015. At the operational level, the management is supported by various departments: Training and General Services (Formations et Services Généraux), Help Call Center Establishments submitted to authorizations (Etablissements soumis à Autorisations), Accidents, Investigations and Counseling (Accidents, Enquêtes et Conseils), Inspections, investigations and controls (Inspections, Enquêtes et Contrôles ) and International affairs.</p><p>&nbsp;</p><p>The Help Call Center acts as a competence center in terms of labour law and safety and health at work, including the duration of the working hours (overtime, Sunday and plans of work organization), collective agreements, labour elections, social elections, etc. at the level of the Jurisprudence, FAQs, and as training center for trainee inspectors. Since March 2015, the Help Call Center has been strengthened by staff responsible for the reception of ITM clients at the regional desks located in Diekirch, Esch-sur-Alzette, Wiltz (since April 2016) and Strassen, in order to allow the receipt of complaints and the communication of information to employees and employers and thus to guarantee the proximity to the latter by releasing the labour inspectors, whose most crucial priority must be to exercise the field surveillance.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5224, 24852, 'EN', 1, 'Ministry of Health – Ministère de la Santé');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5225, 24853, 'EN', 1, 'See more in its <a target="blank" href="http://www.sante.public.lu/fr/politique-sante/ministere-sante/direction-sante/div-travail-environnement/index.html">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Luxembourg">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5226, 24854, 'EN', 1, 'The Occupational Health Division of the Ministry of Health[42] is in charge of organising, coordinating and overseeing the functioning of the occupational health services. It is also an appeal body for employers and workers in cases where the opinion of an occupational doctor is disputed.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5227, 24855, 'EN', 1, 'Ministry of Labour and Employment - Ministère du Travail, de l\'Emploi et de l\'Économie sociale et solidaire');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5228, 24856, 'EN', 1, 'See more in its <a target="blank" href="https://mteess.gouvernement.lu/fr.html">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Luxembourg">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5229, 24857, 'EN', 1, 'The Ministry of Labour is in charge of the execution of the national policy on employment and labour, and coordinates actions relating to labour law and working conditions as well as industrial relations.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5230, 24858, 'EN', 1, 'The ‘’Malta Standard Authority’’ (Malta Competition and Consumer Affairs Authority)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5231, 24859, 'EN', 1, 'See more in its <a target="blank" href="http://mccaa.org.mt/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Malta">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5232, 24860, 'EN', 1, 'The ‘’Malta Standard Authority’’ (Malta Competition and Consumer Affairs Authority) is the national organisation responsible for the development and publication of standards. The MSA is a government agency under the Office of the Prime Minister. The MSA represents Malta at the European Level, as a member of the European Standardization Committee and at the worldwide level as a member of the International Standardization Organization.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5233, 24861, 'EN', 1, 'The Institute of Health & Safety (IHS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5234, 24862, 'EN', 1, 'See more in its <a target="blank" href="https://ihs.com.mt/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Malta">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5235, 24863, 'EN', 1, 'The Institute of Health & Safety (IHS) provides health & safety, environmental, and medical services to international corporate clients.IHS offer a comprehensive range of internationally-accredited professional training and consultancy services in our areas of expertise. In addition they offer a number of accredited health and safety courses.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5236, 24864, 'EN', 1, 'Occupational Health and Safety Authority');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5237, 24865, 'EN', 1, 'See more in its <a target="blank" href="http://www.ohsa.org.mt/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Malta">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5238, 24866, 'EN', 1, 'Occupational Health and Safety Authority is the main organisation in relation to health and safety in Malta. It is part of the Ministry of Social Policy.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5239, 24867, 'EN', 1, 'Maltese Social Security Division - Injury Benefit/Industrial Disease');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5240, 24868, 'EN', 1, 'See more in its <a target="blank" href="http://socialsecurity.gov.mt/en/Pages/default.aspx">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Malta">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5241, 24869, 'EN', 1, '<p>Malta has a social security system as part of its social welfare national scheme. An individual who suffers a personal injury caused by accident arising out of or in the course of his/her employment or self-occupation; or has developed an illness or disease as a result of their work envrionment may be entitled to compensation through the Maltese Social Security Division: entitled Injury Benefit/Industrial Diseas. In order to claim for this benefit the worker must complete a standard issued application form with a report of the incidence or illness. The claim form must be signed by a the treating doctor (indicating the nature and duration fo the injury/ disease and anticipated recovery period), and counter signed by the employer. Once a claim is received, the case is referred to a Medical panel, appointed by the Minister of Education, Employment and the Family. The role of this panel is to examine the claim from a medical prespective, and establish whether the injury/ disease is a consequence of the work environment.<br />All organisations in Malta require industrial accident insurance, which is in the hands of private insurers. The Association of Insurance Brokers (AIB) is an association of most of the major insurance brokers in Malta, and represents the interests of the insurance brokerage industry. The Association interacts actively on behalf of its members with other insurance bodies in Malta, such as the Malta Insurance Association and the Malta Insurance Managers Association.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5242, 24870, 'EN', 1, 'Occupational Health and Safety Authority');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5243, 24871, 'EN', 1, 'See more in its <a target="blank" href=" http://www.ohsa.org.mt/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Malta">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5244, 24872, 'EN', 1, '<p>The occupational safety and health national infrastructure is structured and influenced by the OHSA. The main aims and objectives of the Occupational Health and Safety Authority include raising awareness of health and safety as an issue in the workplace, providing information and promoting training on health and safety matters as well as consulting widely and effectively on health and safety regulations and guidance. The OHSA is responsible for the ensuring that the physical, psychological and social well-being of all Maltese workers in all workplaces is promoted; and, in turn, to ensure that workers health and safety is actively safeguarded, by whoever is obliged to do so, by ensuring that the levels of occupational health and safety protection (as established by the Act and by the regulations made under this Act) are maintained. Since Malta’s accession to the European Union, the OHS Authority has continued the exercise of harmonization of all new occupational health and safety legislation with EU OHS Directives, while at the same time reviewing existing legislation to ensure that there are no regulatory gaps, and to identify any conflicting or burdensome legislation, with the scope of simplifying it.</p><p>&nbsp;</p><p>Among its various fuctions, the OHSA:</p><ol><li>applies the provisions of the Act and any regulations or orders made there under;</li><li>establishes strategies by which the general national policy relating to occupational health and safety may be implemented;</li><li>advises the Minister regarding the making of regulations;</li><li>monitors compliance with the relevant health and safety legislation;</li><li>prepares regulations and codes of practice;</li><li>promotes the dissemination of information, education and training;</li><li>collates and analyses data and statistics;</li><li>keeps registers of plants, installations, equipment, machinery, articles, substances or chemicals in use at work;</li><li>carries out investigations on any matter concerning occupational health and safety;</li><li>promotes and carries out scientific research; and</li><li>keeps registers of persons competent to give advice on matters related to OHS.</li></ol><p>&nbsp;</p><p>As aforementiond, the OHSA is the national statutory body with responsibility for enforcing ocupational safety and health law, promoting and encouraging accident prevention, and providing infomation and advice to companies, organisations and individuals. The Authority considers enforcement as one of its key core functions – the purpose of enforcement is to ensure that duty holders effectively control risks at their place of work. Through the enactment of the Act (Act XXVII of 2000), a number of health and safety officers were appointed by the OHSA. These officers are deemed to be Public Officers under Criminal Code, and, thus, are protected by law whilst on duty. These inspection officers have a number of powers at law, including: to enter freely into any workplace (day or night) without any pervious notice; and to inspect any document the keeping of which which is prescribed by any OSH regulation; and to issue order to any person to preserve OHS.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5245, 24873, 'EN', 1, 'Raad voor Accreditatie (Accreditation Council)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5246, 24874, 'EN', 1, 'See more in its <a target="blank" href="http://www.rva.nl/home/ ">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5247, 24875, 'EN', 1, 'In many countries, accreditation is a government task. But in the Netherlands, the accreditation institute is an organisation governed by private law, whereby the government acts as client, regulator and discussion partner. If the government considers it necessary to implement accreditation in particular sectors, the government is the client. But the government is not only the client: it is also a discussion partner for the accreditation institute. The RvA (Accreditation Council) holds regular discussions with the Ministry of Housing, Spatial Planning and the Environment and the Ministry of Social Affairs and Employment. The agreement between the State and the RvA allows room for formulating tailor-made agreements for specific areas of special attention with various ministries.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5248, 24876, 'EN', 1, 'Foundation for monitoring certification in health & safety services (Stichting Beheer Certificatie Arbodiensten, SBCA)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5249, 24877, 'EN', 1, 'See more in its <a target="blank" href="http://www.sbca.nl/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5250, 24878, 'EN', 1, 'In the SBCA (Foundation for monitoring certification in health & safety services), Government, trade unions and employers’ organisations work together to set the standards that health & safety services must satisfy in order to be admitted to the profession.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5251, 24879, 'EN', 1, 'The Standards Organization of the Netherlands The Standards Organization of the Netherlands (Nederlands Normalisatie Instituut, NEN)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5252, 24880, 'EN', 1, 'See more in its <a target="blank" href="https://www.nen.nl">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5253, 24881, 'EN', 1, 'The Standards Organization of the Netherlands (Nederlands Normalisatie Instituut, NEN) supports the making of agreements on products, processes and services, and publishes standards. Employers and employees are mutually responsible for setting their own standards, so the NEN supplies them with the knowledge and experience they need for their consultations. The NEN works in close collaboration with European (CEN, CENELEC and ETSI) and worldwide (ISO, IEC and ITU) partners.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5254, 24882, 'EN', 1, 'N.A.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5255, 24883, 'EN', 1, 'See more in its <a target="blank" href="http://sbca.nl/over-sbca/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5256, 24884, 'EN', 1, 'There are many different Arbodiensten in the Netherlands, ranging from large national companies, to small regional or branch specific services. Arbodiensten usually are multidisciplinary and provide a wide range of services from preventive work checks, to checkups during sickness absenteeism, and health checks. A list of all certified arbodiensten can be found at the website of SBCA, which is the owner of the certification scheme of the arbodiensten. Arbodiensten can employ workers of various backgrounds, but are required to have expertise in at least four core disciplines: an occupational physician; an Industrial Hygienist, a Safety expert and a Work Organization expert.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5257, 24885, 'EN', 1, 'Employee Insurance Agencie (Uitvoeringsinstituut Werknemersverzekeringen, UWV)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5258, 24886, 'EN', 1, 'See more in its <a target="blank" href="https://www.uwv.nl/particulieren/index.aspx">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5259, 24887, 'EN', 1, 'UWV provides expert and efficient implementation of employee insurance, such as the Unemployment Insurance Act, WIA, WAO, WAZ, Wazo and Sickness Benefits Act. In addition, UWV offers labor market and data services. UWV performs these services as an independent administrative body (ZBO) on behalf of the Ministry of Social Affairs and Employment.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5260, 24888, 'EN', 1, 'Inspectorate SZW (Inspectie SZW)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5261, 24889, 'EN', 1, 'See more in its <a target="blank" href="http://www.inspectieszw.nl/ ">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5262, 24890, 'EN', 1, 'Compliance to the rules and regulations is monitored by the Inspectorate SZW (Inspectie SZW). Since January 2012 the Inspectorate SZW combines the organizations and the activities of the former Labour Inspectorate (AI), the Work and Income Inspectorate (IWI) and the Social and Intelligence Investigation Service (SIOD) of the Ministry of Social Affairs and Employment.\r\nThe Inspectorate SZW aims at achieving healthy and safe working conditions for all. The Inspectorate of Social Affairs is working according to a vision of an integrated and risk-oriented supervision. The inspection does not only focus on enforcement, but also has preventive tasks.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5263, 24891, 'EN', 1, 'Ministry of Social affairs and Employment (Ministerie van Sociale Zaken en Werkgelegenheid)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5264, 24892, 'EN', 1, 'See more in its <a target="blank" href="http://www.rijksoverheid.nl/ministeries/szw ">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Netherlands">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5265, 24893, 'EN', 1, 'On a national level Occupational Safety and Health is the responsibility of the Ministry of Social affairs and Employment (Ministerie van Sociale Zaken en Werkgelegenheid). The ministry sets target regulations that state the degree to which the health and safety of employees at work should be protected.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5266, 24894, 'EN', 1, 'The Polish Committee for Standardization (PKN)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5267, 24895, 'EN', 1, 'See more in its <a target="blank" href="http://www.pkn.pl/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5268, 24896, 'EN', 1, 'The Polish Committee for Standardization (Polski Komitet Normalizacyjny, PKN) is a national standards body responsible for the organisation of standardisation activities. The PKN is not a government agency but a body governed by public law. The basis for its operations constitutes the Polish Standardization Act of 12 September 2002, defining the principal goals and responsibilities of the Committee. PKN is a legally authorised body to represent the interests of Poland in the international standardisation arena.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5269, 24897, 'EN', 1, 'N.A.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5270, 24898, 'EN', 1, 'See more in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland">OSHwiki</a>');
