INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5272, 24900, 'EN', 1, 'The Agricultural Social Insurance Fund (Kasa Rolniczego Ubezpieczenia Spo?ecznego, KRUS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5273, 24901, 'EN', 1, 'See more in its <a target="blank" href="https://www.krus.gov.pl/en">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5274, 24902, 'EN', 1, 'The Agricultural Social Insurance Fund (Kasa Rolniczego Ubezpieczenia Spo?ecznego, KRUS)[36] has been established as a unique, social insurance institution, fully dedicated to rural areas. The purpose of creating such a separate, independent social insurance body was to ensure an efficient administration of the agricultural social insurance funds as well as undertake entirely new responsibilities resulting from the particular needs of rural communities');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5275, 24903, 'EN', 1, 'Social Insurance Institution (ZUS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5276, 24904, 'EN', 1, 'See more in its <a target="blank" href="http://www.zus.pl/ ">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5277, 24905, 'EN', 1, '<p>Social Insurance Institution (Zak?ad Ubezpiecze? Spo?ecznych , ZUS) collects social and health insurance contributions of citizens and distributes benefits (e.g. pensions, sickness allowance, maternity allowance, etc.). The Social Insurance Institution is the disposer of the Social Insurance Fund (Fundusz Ubezpiecze? Spo?ecznych, FUS; SIF) established on 1 January 1999 by virtue of the Social Insurance System Act, to perform social insurance related tasks[34]. The following funds are distinguished within SIF:</p><ul><li>old-age pension fund, established to finance old-age pensions and public deficit&nbsp; expenditure in order to guarantee the pay out of funded pensions;</li><li>pension fund established to finance, among others: incapacity benefit, training benefit, survivors’ pensions, supplements to survivors’ pensions for complete orphans, nursing allowance, funeral grants, etc.;</li><li>sickness fund, established to finance sickness, maternity, care, compensatory allowances, rehabilitation benefits;</li><li>accident fund, established to finance occupational accident pensions and allowances, lump-sum compensations, incapacity benefit as a direct result of occupational accident or disease.</li></ul><p>Compulsory occupational accident insurance covers persons subject to pension insurance, for example: employees, freelancers (contractors), members of agricultural production cooperatives, persons running an agriculture related business and their contractors.</p><p>A percentage rate of the occupational accident insurance contribution is differentiated for individual contribution payers and depends on the occupational risk category as well as the number of persons covered by the work accident insurance.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5278, 24906, 'EN', 1, 'State Mining Authority (Wy?szy Urz?d Górniczy, WUG)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5279, 24907, 'EN', 1, 'See more in its <a target="blank" href="http://www.wug.gov.pl/ ">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5280, 24908, 'EN', 1, '<p>State Mining Authority (Wy?szy Urz?d Górniczy, WUG) was established by the Geological and Mining Law Act and is presided by the President of the State Mining Authority who is subordinate to the Minister of Energy (Minister Energii). The main responsibility of the State Mining Authority is to perform control and supervision over work safety and health; fire protection; mine rescue; management of mineral deposits in the extraction process; environmental protection, including damage prevention; mining plant construction and closure, including land reclamation and post mining area rehabilitation.</p><p>The mission of the State Mining Authority is to:</p><ul><li>improve work safety in mines;</li><li>protect miners’ health;</li><li>ensure efficient and sustainable management of mineral deposits;</li></ul><p>reduce the negative impact of the extractive industry on the environment</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5281, 24909, 'EN', 1, 'Office of Technical Inspection (Urz?d Dozoru Technicznego, UDT)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5282, 24910, 'EN', 1, 'See more in its <a target="blank" href="https://www.udt.gov.pl/ ">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5283, 24911, 'EN', 1, 'Office of Technical Inspection (Urz?d Dozoru Technicznego, UDT) is a Polish inspection body established in order to ensure safety of technical devices and installations. The Office\'s main goal is to assess the conformity of technical equipment with relevant regulations and specifications in the product design, manufacture and service process. Its duties also include technical safety & failure analysis as well as dissemination of information on technical safety and related issues. The Office of Technical Inspection is a non-profit organisation, independent at both financial and operational level.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5284, 24912, 'EN', 1, 'State Sanitary Inspectorate Inspection (Pa?stwowa Inspekcja Sanitarna, PIS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5285, 24913, 'EN', 1, 'See more in its <a target="blank" href="https://gis.gov.pl/en/ ">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5286, 24914, 'EN', 1, '<p>State Sanitary Inspectorate Inspection (Pa?stwowa Inspekcja Sanitarna, PIS) is subordinate to the Ministry of Health and presided by the Chief Sanitary Inspector. The State Sanitary Inspectorate is the primary institution responsible for the public health protection. It focuses particularly on infectious disease control, food and nutrition safety, environmental hygiene, health promotion and other public health related issues. The State Sanitary Inspectorate realises its goals through the following steps:</p><ul><li>conducting a sustained and preventive sanitary supervision;</li><li>preparing epidemiological analysis, studies and assessments;</li><li>overseeing working conditions in various workplaces, focusing on harmful factors such as dust, noise, vibration, chemical agents and their levels;</li><li>supervision of environmental hygiene and food safety,</li></ul><p>promotion of public health, dissemination of proper hygiene habits and disease prevention methods.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5287, 24915, 'EN', 1, 'National Labour Inspectorate (Pa?stwowa Inspekcja Pracy, PIP)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5288, 24916, 'EN', 1, 'See more in its <a target="blank" href="https://www.pip.gov.pl/en">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5289, 24917, 'EN', 1, '<p>National Labour Inspectorate (Pa?stwowa Inspekcja Pracy, PIP) is subordinate to the Lower Chamber of the Polish Parliament (Sejm) and supervised by the Labour Protection Council whose role it to evaluate the Inspectorate’ programmes, tasks and activities, and analyse labour protection issues at national level. The Inspectorate is formed of the Chief Labour Inspectorate and District Labour Inspectorates and presided by the Chief Labour Inspector. A District Labour Inspectorate covers one or more provinces. Sub-district offices may be established within the structure of District Labour Inspectorates.</p><p>The main responsibilities of the National Labour Inspectorate’s include:</p><ul><li>supervision and inspection of enterprise compliance with labour law;</li><li>OSH regulations compliance audit of refurbished and modernised workplaces, machinery, technical devices and other technologies;</li><li>legal employment and other paid work inspection (including employment of foreign workers);</li><li>OSH standards compliance audit of sold PPE merchandise as governed by separate regulations;</li><li>undertaking steps to reduce health related work-hazards;</li><li>cooperation with environmental protection agencies on auditing regulations aimed at countering industry environmental hazards;</li></ul><p>reviewing labour law acts drafts.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5290, 24918, 'EN', 1, 'Labour Protection Council of the Sejm of the Republic of Poland (Rada Ochrony Pracy, ROP)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5291, 24919, 'EN', 1, 'See more in its <a target="blank" href="http://rop.sejm.gov.pl/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5292, 24920, 'EN', 1, 'Labour Protection Council of the Sejm of the Republic of Poland (Rada Ochrony Pracy, ROP)is an institution of the Lower Chamber of the Polish Parliament (Sejm) that oversees general working conditions in Poland and the National Labour Inspectorate’s operations. Its major responsibilities include: assessing the National Labour Inspectorate’s activities, reviewing labour protection legal acts drafts and analysis of labour protection issues at national level.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5293, 24921, 'EN', 1, 'The Ministry of Family, Labour and Social Policy');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5294, 24922, 'EN', 1, 'See more in its <a target="blank" href="https://www.mpips.gov.pl/en/">website</a> and in <a target="blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Poland">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5295, 24923, 'EN', 1, '<p>The Ministry of Family, Labour and Social Policy occupies a key role in the labour protection system in Poland. The Ministry is the main body responsible for the development and implementation of OSH national strategy and policies.</p><p>The institutions overseeing enterprise compliance with the OSH regulations include the National Labour Inspectorate (Pa?stwowa Inspekcja Pracy), the State Sanitary Inspection (Pa?stwowa Inspekcja Sanitarna), the Office of Technical Inspection (Urz?d Dozoru Technicznego), and the State Mining Authority (Wy?szy Urz?d Górniczy).</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5296, 24924, 'EN', 1, 'The Portuguese Quality Institute (IPQ – Instituto Portugues da Qualidade)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5297, 24925, 'EN', 1, 'See more in its <a target="blank" href="http://www.ipq.pt/backhtmlfiles/ipq_mei.htm">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5298, 24926, 'EN', 1, 'The Portuguese Quality Institute (IPQ – Instituto Portugues da Qualidade)  is the national organization that manages and promotes the development of the Portuguese System for Quality (SQP - Sistema Português para a Qualidade), with its three sub-systems - Standardization, Metrology and Qualification. Therefore, IPQ is the Portuguese representative body in the quality field at international level, and keeps a close co-operation with its European counterparts. Within the SPQ framework, IPQ copes with the role of National Standardization Body (ONN), thus ensuring the co-ordination with European and International standardization bodies, and supervises the activity of the Central Laboratory of Metrology.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5299, 24927, 'EN', 1, 'The Protection against Occupational Hazards Department (DPRP - Departamento de Protecção contra os Riscos Profissionais');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5300, 24928, 'EN', 1, 'See more in its <a target="blank" href="http://www2.seg-social.pt/left.asp?05.17">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5301, 24929, 'EN', 1, '<p>The Protection against Occupational Hazards Department (DPRP - Departamento de Protecção contra os Riscos Profissionais), is the service of the Institute of Social Security (Instituto de Segurança Social), nationwide responsible for managing the treatment and recovery from illness or disability arising from occupational hazards. According to Portaria) no. 135/2012 of 08 May&nbsp; the competences of DPRP at company level, are as follows:</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; To evaluate and determine the disability of injury, functional disturbance or disease arising from occupational hazards;</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; To ensure the provision of medical care to treat disease or disability resulting from occupational hazards;</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; To pay compensation for temporary disability and permanent disability pensions;</p><p>-&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; To promote clinical recovery and professional reclassification of beneficiaries with an occupational disease;</p><p id="tmgmt-1">To promote the placement of rehabilitated workers in occupations compatible with his physical condition and ability to work.</p><p id="tmgmt-2">&nbsp;</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5302, 24930, 'EN', 1, 'The Division of Environmental and Occupational Health (DSAO - Divisão de Saúde Ambiental e Ocupacional (DSAO) from Directorate General of Health (DGS – Direção Geral de Saúde)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5303, 24931, 'EN', 1, 'See more in its <a target="blank" href="https://www.dgs.pt/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5304, 24932, 'EN', 1, 'The Division of Environmental and Occupational Health (DSAO - Divisão de Saúde Ambiental e Ocupacional (DSAO) from Directorate General of Health (DGS – Direção Geral de Saúde)  has responsibilities to propose strategies and specific programs for evaluation coordination and collaboration in the management of risk to human health in several areas, notably water, the built spaces, waste, chemical and biological substances, genetically modified organisms and ionizing and non ionizing radiation; for monitoring, technical advice and licensing facilities, equipment and chemical and biological substances in accordance with law; to propose strategies, coordinate activities and ensure programs within the occupational health; to propose strategies and coordinate programs and activities in ensuring the prevention of accidents.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5305, 24933, 'EN', 1, 'The Regional Directorate of Labour in the Autonomous Region of Madeira (Direção Regional do Trabalho da Região Autónoma da Madeira)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5306, 24934, 'EN', 1, 'See more in its <a target="blank" href="https://www.madeira.gov.pt/drtai/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5307, 24935, 'EN', 1, 'The Regional Directorate of Labour in the Autonomous Region of Madeira (Direção Regional do Trabalho da Região Autónoma da Madeira) has the mission to contribute to the improvement of working conditions and to the agreement on labour relations within Autonomous Region of Madeira. The Regional Directorate has expertise in the area of labor, specifically in the areas of labor legislation, collective bargaining, professional organizations, assessment of working conditions, safety and health at work, labor statistics, and more recently the voluntary resolution of work conflicts.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5308, 24936, 'EN', 1, 'The Regional Inspectorate of Employment from the Regional Government of Açores (Inspecção Regional do Trabalho do Governo Regional dos Açores');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5309, 24937, 'EN', 1, 'See more in its <a target="blank" href="http://www.azores.gov.pt/Portal/pt/entidades/vp-irt/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5310, 24938, 'EN', 1, 'The Regional Inspectorate of Employment from the Regional Government of Açores (Inspecção Regional do Trabalho do Governo Regional dos Açores) has the mission to develop advisory methods and action and to perform inspections in companies and other organisations, with the fundamental aim of improving working conditions.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5311, 24939, 'EN', 1, 'The Directorate-General for Employment and Labour Relations (DGERT- Direcção-Geral do Emprego e das Relações de Trabalho)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5312, 24940, 'EN', 1, 'See more in its <a target="blank" href="http://www.dgert.gov.pt/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5313, 24941, 'EN', 1, 'The Directorate-General for Employment and Labour Relations (DGERT- Direcção-Geral do Emprego e das Relações de Trabalho) is responsible for helping to develop policies on employment and vocational training and on labour relations, including working conditions and health, safety and welfare at work. It is also responsible for monitoring and encouraging collective bargaining and the prevention of trade disputes, and for promoting the accreditation of training bodies.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5314, 24942, 'EN', 1, 'The Working Conditions Authority (ACT- Autoridade para as Condições de Trabalho)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5315, 24943, 'EN', 1, 'See more in its <a target="blank" href=" http://www.act.gov.pt/(pt-PT)/Paginas/default.aspx">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Portugal">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5316, 24944, 'EN', 1, '<p>The Working Conditions Authority (ACT- Autoridade para as Condições de Trabalho) is responsible for promoting improved working conditions by ensuring compliance with labour regulations and working conditions in the context of the private sector and compliance with working conditions in the public sector, and for promoting occupational risk prevention policies. The central services of ACT are located in Lisbon, and decentralized services, spread over all districts of Portugal.</p><p>&nbsp;</p><p>The central services of ACT, are constituted by three service directorates and 10 divisions:</p><ul><li>Directorate of Support to the supervision (DSAAI);</li><li>Directorate for the Promotion of Safety and Health at Work (DSPSST);</li></ul><p>Services Directorate for the Management Support (DSAG).</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5317, 24945, 'EN', 1, 'The Romanian Standards Association (ASRO)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5318, 24946, 'EN', 1, 'See more in its <a target="blank" href="http://www.asro.ro/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5319, 24947, 'EN', 1, '<p>ASRO represents the National Standardization Body that develops standardization activities in all the fields of the European and international standardization as member of ISO, IEC, CEN, CENELEC and ETSI.</p><p>ASRO is a non-profit private body of public interest authorized by the Government through the Law No. 355/2002. In Romania, ASRO is developing the standardization activity since 1928 when it was put in place for all the national economy sectors.</p><p>At present, ASRO operates through the representatives of all the economic sectors, the national authorities, the industry, R&amp;D entities, the academic media, employer and employee organizations’ members, consumers’ associations, etc.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5320, 24948, 'EN', 1, 'The National Public Health Institute (INSP)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5321, 24949, 'EN', 1, 'See more in its <a target="blank" href="http://www.insp.gov.ro/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5322, 24950, 'EN', 1, '<p>It is a public health body subordinated to the Ministry of Health. The institute scope resides in the surveillance and control of the public health at national level, including the occupational diseases of all the activity sectors. The Public Health Institute carries out health surveys at national/regional/sectoral level and has the evidence of all the data obtained.</p><p>On the basis of this evidence, the institute accomplishes public health monitoring and promotes adequate solutions for the improvement of the population’s health condition.</p><p>In the OSH area, the Public Health Institute acts as a major workplace health promoter, contributes to occupational health education and the improvement of the working conditions in all the activity sectors. In addition, the institute is developing public health regulations and substantiates to a large extent the national public and occupational health strategies and policies to be drawn up by the Ministry of Health and the Ministry of Labour, Family and Social Protection as well.</p><p>The Public Health Institute also develops and co-ordinates the specific health services in the area of public health surveillance and occupational health promotion.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5323, 24951, 'EN', 1, 'The National House of Public Pensions (CNPP)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5324, 24952, 'EN', 1, 'See more in its <a target="blank" href="https://www.cnpp.ro/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5325, 24953, 'EN', 1, 'The National House of Public Pensions (CNPP) represents the national authority in the field of insurance against work accidents and occupational diseases.\r\nThe former National House of Pensions and Other Social Insurance Rights – CNPAS was established through the Law No. 19 of 17 March 2000 on the public system of pensions and other social insurance rights thus managing the system of public pensions at national level.\r\nThe Insurance System Against Work Accidents and Occupational Diseases entered into force on 1 January 2005 through the Law No. 346/2002 amended.\r\nThe Law No. 346/2002 defines the insurance against work accidents and occupational diseases as ‘a person related insurance belonging to the social insurance system that is guaranteed by the state’. It regulates the social protection of the employees against diminishing or loss of their working capacity and decease in case of work accidents and occupational diseases.\r\nThe National House of Public Pensions acts as compensation and insurance body through the Direction – General of Work Accidents and Occupational Diseases (DGAMBP).\r\nServices provided by the Direction – General of Work Accidents and Occupational Diseases:\r\n•	Medical rehabilitation of workers and recovery of their working capacity\r\n•	Workers’ rehabilitation and reintegration\r\n•	Indemnities for temporary loss of working capacity, decease, etc.\r\nIn addition to its role as national insurance body, CNPP does also represent a significant actor in terms of prevention i.e. the reduction in the dues paid by the companies to CNPP constitutes an incentive for the companies towards increased prevention and less work accidents and occupational diseases.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5326, 24954, 'EN', 1, 'The Labour Inspection (IM)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5327, 24955, 'EN', 1, 'See more in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5328, 24956, 'EN', 1, '<p><span lang="EN-US">The Labour Inspection (IM)</span><span lang="EN-US">– represents the competent public authority in charge with the unitary enforcement of the legislation on occupational safety and health within public and private undertakings of all the economic sectors.<span class="apple-converted-space">&nbsp;</span></span></p><p><span lang="EN-US">The Labour Inspection establishment and organizational structure are regulated by the Law No. 108/1999 republished and also by the Regulation of organization and functioning approved through the Government Decision No. 1377/2009 amended.<span class="apple-converted-space">&nbsp;</span></span></p><p><span lang="EN-US">The Labour Inspection main objective resides in controlling the observance of the legal obligations by the employers from the public and private sector both in terms of labour relations and the employees’ safety and health at work of all the economic sectors.<span class="apple-converted-space">&nbsp;</span></span></p><p><span lang="EN-US">The Labour Inspection includes a number of 41 Labour Territorial Inspectorates in its organizational structure. They correspond to the 41 districts of the country enforce the Labour Inspection policy at territorial level. The Labour Inspectorate of Bucharest constitutes a separate entity corresponding to the size of the capital of the country and its significant number of companies.</span></p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5329, 24957, 'EN', 1, 'Ministry of Health (MS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5330, 24958, 'EN', 1, 'See more in its <a target="blank" href="http://www.ms.gov.ro/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5331, 24959, 'EN', 1, '<p>The Ministry of Health (MS) is a legal person and central administration body in the field of public health care.</p><p>Similarly with the Ministry of Labour, Family, Social Protection and Elderly, the Ministry of Health transposes into practice the governmental policies for the healthcare sector, both within the public health care system and in private medical units.</p><p>In the occupational health area, the Ministry of Health is drafting regulations for the medical sector, particularly for the development of the health at work activities, in line with the EU legislation in force.</p><p>At the same time, the Ministry of Health promotes the education of the occupational medicine physicians at academic level in collaboration with the Ministry of National Education, through occupational medicine clinics hosted by the biggest hospitals in the country.</p><p>The Ministry of Health is also elaborating and implementing adequate strategies and programmes meant to the improvement of workers’ health at the workplace and the reduction/elimination of the risk factors that may result in severe occupational diseases with significant costs in terms of human life and financial resources.</p><p>The Ministry of Health also co-ordinates the activity of the Public Health Institute in charge with monitoring the public health at national level, including the workplace related diseases, and the evidence of the occupational diseases as well.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5332, 24960, 'EN', 1, 'Ministry of Labour, Family, Social Protection and Elderly (MMFPSPV)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5333, 24961, 'EN', 1, 'See more in its <a target="blank" href="http://www.mmuncii.ro/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_systems_at_national_level_-_Romania">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5334, 24962, 'EN', 1, '<p>The Ministry of Labour, Family, Social Protection and Elderly (MMFPSPV) is a specialized body of the central public administration and legal person subordinated to the Government of Romania, in charge with the implementation of the governmental policies in the area of labour, family, equal opportunities, social protection and the elderly.</p><p>MMFPSPV acts in the OSH field through the Direction of Safety and Health at Work co-ordinated at the Ministry top management level by a Secretary of State. This Direction coordinates two research and development (R&amp;D) national institutes in the field of OSH and social security, respectively, while also monitoring the implementation of the policies and legislative acts in the area.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5335, 24963, 'EN', 1, 'The Slovak Standards Institute (Slovenský ústav technickej normalizácie – SÚTN)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5336, 24964, 'EN', 1, 'See more in its <a target="blank" href="http://www.sutn.sk/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5337, 24965, 'EN', 1, 'The Slovak Standards Institute (Slovenský ústav technickej normalizácie – SÚTN) has been established by the Slovak Office of Standards, Metrology and Testing (Úrad pre normalizáciu, metrológiu a skúšobníctvo Slovenskej republiky – ÚNMS SR). SUTN is exclusively designated to development, approval and publishing of Slovak Standards, fulfilment of obligations following from international contracts and from the membership in international and European standards organisations.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5338, 24966, 'EN', 1, 'Institute of Occupational Safety (Inštitút bezpe?nosti práce)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5339, 24967, 'EN', 1, 'See more in its <a target="blank" href="http://ibp.sk/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5340, 24968, 'EN', 1, 'The Institute provides services and trainings in occupational and fire safety for companies.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5341, 24969, 'EN', 1, 'Social Insurance Agency (Sociálna pois?ov?a)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5342, 24970, 'EN', 1, 'See more in its <a target="blank" href="http://www.socpoist.sk/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5343, 24971, 'EN', 1, 'The statutory institution has a nationwide competency in the area of sickness insurance, pension insurance, accident insurance, unemployment insurance and guarantee insurance. The headquarters regulates, controls and methodologically guides the 37 branch offices. It is in charge of the pension insurance area and of two types of benefits from the accident insurance. The executive body branch offices are responsible for the collection and recovery of sickness, unemployment and guarantee contributions, benefits pay out, as well as for a direct contact with clients.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5344, 24972, 'EN', 1, 'Nuclear Regulatory Authority of the Slovak Republic (Úrad jadrového dozoru Slovenskej republiky, ÚJD SR)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5345, 24973, 'EN', 1, 'See more in its <a target="blank" href="https://www.ujd.gov.sk/ujd/www1.nsf/viewByKeyMenu/En-01">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5346, 24974, 'EN', 1, 'The Nuclear Regulatory Authority of the Slovak Republic (NRA SR) is a central government authority of the Slovak Republic for nuclear regulation. The authority exercises state supervision over nuclear safety of nuclear installations, including radioactive waste management, spent fuel management and other stages of the fuel cycle, over nuclear materials, including their inspection and registration, as well as over physical protection of nuclear installations and nuclear materials provided for by the relevant license holder. The Authority performs assessment of plans for use of nuclear energy and the quality of classified equipment and nuclear technology devices and the commitments of the Slovak Republic arising from international treaties on nuclear safety of nuclear installations and nuclear materials management. NRA SR is a legal entity; rules for managing funds of a state budgetary organization apply, it has a separate chapter in the state budget, it has no subordinated bodies on a regional or district levels, and it is not a founder of any state budgetary or state contributory organization or any other legal entities.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5347, 24975, 'EN', 1, 'Supreme Mining Authority of the Slovak Republic (Hlavný banský úrad)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5348, 24976, 'EN', 1, 'See more in its <a target="blank" href="http://www.hbu.sk/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5349, 24977, 'EN', 1, 'Amongst other tasks, the Authority ensures the safety and health protection at work and safety of operation. It is entitled to order the necessary measures.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5350, 24978, 'EN', 1, 'Public Health Authority of the Slovak Republic (Úrad verejného zdravotníctva Slovenskej republiky – UVZSR)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5351, 24979, 'EN', 1, 'See more in its <a target="blank" href="http://www.uvzsr.sk/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5352, 24980, 'EN', 1, 'State supervision in the field of public health is determined by the Act No. 355/2007 Coll. The Public Health Authority of the Slovak Republic, which is a budgetary organisation of the state with a registered office in Bratislava, is the supreme office for the regional public health authorities. It manages, controls and coordinates the execution of state administration carried out by regional public health offices. State administration in the sector of health on regional level is ensured by 36 regional public health authorities, 8 of which are in county towns, ensure professional and methodical directing of the others. Public health authorities supervise employers’ adherence to legislation and fulfilment of measures directed for prevention of occupational diseases. They also supervise the performance of occupational health services, and carry out specialized tasks focused on monitoring of health status of employees in relation with working conditions. The public health authorities provide counselling for employers on the implementation of pertinent legal requirements.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5353, 24981, 'EN', 1, 'National Labour Inspectorate (Národný inšpektorát práce – NIP)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5354, 24982, 'EN', 1, 'See more in its <a target="blank" href="http://www.nip.sk/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5355, 24983, 'EN', 1, 'Labour inspection is regulated by Act No. 125/2006 Coll. The National Labour Inspectorate, is a state administration body with its own budget with a seat in Košice. The National Labour Inspectorate manages and controls the eight labour inspectorates in the regions and unifies and rationalises working methods of the labour inspectors, who are civil servants. A labour inspectorate is independent at the performance of labour inspection. Inspections aim at adherence to legal provisions in several fields of OSH: safety at work, safety of technical equipment, working environment agents, nuclear equipment, major industrial accidents, chemicals, and market surveillance of certain products. Recently the provision of free consultations to employers has been rising. It is the National Labour Inspectorate that issues and revokes authorisation to perform safety technical services and OSH training and education. The tasks of authorisation for verification of meeting the requirements for safety of technical equipment and certification to authorised safety technicians are also legally assigned to NIP.\r\nState defence, police and armed forces have their own labour inspection bodies. There is also a system of trade union control for the supervision of the employees´ health protection, which is independent from labour inspection. This right of unions to carry out inspection is stipulated in the Labour Law. The activities of these inspectors are paid for by the state');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5356, 24984, 'EN', 1, 'Ministry of Health of the Slovak Republic (Ministerstvo Zdravotníctva Slovenskej republiky, MZ SR)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5357, 24985, 'EN', 1, 'See more in its <a target="blank" href="http://www.health.gov.sk/Index.aspx">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5358, 24986, 'EN', 1, 'Occupational health and hygiene are within the competence of the Ministry of Health of the Slovak Republic. Therefore there are separate executive agencies of the ministries, which closely co-operate on national and regional levels in the field of OSH');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5359, 24987, 'EN', 1, 'The Ministry of Labour, Social Affairs and Family of the Slovak Republic (Ministerstvo práce, sociálnych vecí a rodiny Slovenskej republiky, MPSVR SR)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5360, 24988, 'EN', 1, 'See more in its <a target="blank" href="https://www.employment.gov.sk/en/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovakia ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5361, 24989, 'EN', 1, 'Traditionally, occupational safety and occupational health are distinct features in Slovakia. The Ministry of Labour, Social Affairs and Family of the Slovak Republic[29] is responsible for occupational safety and labour inspection.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5362, 24990, 'EN', 1, 'Slovenian Accreditation (Slovenska akreditacija)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5363, 24991, 'EN', 1, 'See more in its <a target="blank" href="http://www.slo-akreditacija.si/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5364, 24992, 'EN', 1, 'Slovenian Accreditation (Slovenska akreditacija) is a public institute for carrying out tasks of national accreditation services. It follows the Accreditation Act and the Decision on the establishment of a public institute for Slovenian Accreditation (SA). It is responsible for the harmonious development of standards in Slovenia and is a member of the European cooperation for Accreditation, the International Accreditation Forum and the International Laboratory Accreditation Cooperation.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5365, 24993, 'EN', 1, 'Institute of Public Health of the Republic of Slovenia');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5366, 24994, 'EN', 1, 'See more in its <a target="blank" href="http://www.nijz.si/en ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5367, 24995, 'EN', 1, '<p>The National Institute of Public Health (Inštitut za varovanje zdravja Republike Slovenije) is the central national institution that studies, protects and increases the level of health of the population through raising awareness and operating other preventive measures. Together with partners, the Institute of Public Health is a resource of data and information as a basis for decisions and actions of individuals, professionals and health policy. It recognises the critical public health challenges in the population including the determinants that affect health and proposes measures to improve it. The National Institute of Public Health monitors the health care system, prepares analyses and proposes measures to improve the availability, performance and development priorities of the health care system. It identifies potential health threats, assesses risks and prepares measures for the protection of health. With research and international cooperation, it contributes to and disseminates new knowledge and good practice.</p><p>The Institutes of Public Health perform basic preventive health care in the Republic of Slovenia. Notably through the implementation of a national programme, they implement the strategic direction of the country in the field of preventive medicine and public health. There are nine regional health care institutions. The actions of individual health care institutions do not differ significantly. Among the regional institutes, the Institute of Public Health of Ljubljana (Zavod za zdravstveno varstvo Ljubljana) is both in size (area) and by population the largest institute. The area covered by its activity has 40 municipalities with more than 600,000 inhabitants.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5368, 24996, 'EN', 1, 'Institute of Pension and Invalidity Insurance of Slovenia');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5369, 24997, 'EN', 1, 'See more in its <a target="blank" href="http://www.zpiz.si ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5370, 24998, 'EN', 1, 'The Institute of Pension and Invalidity Insurance of Slovenia is also a public institution. It is a carrier and provider of compulsory pension and invalidity insurance. Compulsory insurance is covered by contributions of insured persons, employers and the Republic of Slovenia. The rates of contributions are determined by the Social Security Contributions Act . Compulsory pension and invalidity insured persons are: employees in Slovenia; Slovenian nationals employed in foreign companies; self-employed; farmers; apprentices; top sportsmen, top chess players and others. This compulsory insurance covers invalidity pension, occupational rehabilitation, invalidity benefit, reassignment, part-time work and other benefits.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5371, 24999, 'EN', 1, 'Health Insurance Institute of Slovenia');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5372, 25000, 'EN', 1, 'See more in its <a target="blank" href="http://www.zzzs.si ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5373, 25001, 'EN', 1, 'The Health Insurance Institute of Slovenia is a public institution that carries out compulsory health insurance. Compulsory health insurance is covered by contributions of insured persons, employers, the Republic of Slovenia and others. The rates of contributions are determined by the Social Security Contributions Act. Categories of persons that are compulsorily health insured person are: employees in Slovenia; Slovenian nationals employed in foreign companies; self-employed; farmers; apprentices; top sportsmen, top chess players and others. Compulsory health insurance allows insured persons to claim the right to health services, medicines and medical devices and to cash compensation such as salary compensation during temporary absence from work, funeral and death and reimbursement of travel expenses in connection with the exercise of rights to health services.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5374, 25002, 'EN', 1, 'Health Inspectorate (Zdravstveni inšpektorat Republike Slovenije)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5375, 25003, 'EN', 1, 'See more in its <a target="blank" href="http://www.zi.gov.si/en/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5376, 25004, 'EN', 1, 'The Health Inspectorate of the Republic of Slovenia performs inspection over implementation of laws and other regulations with the objective of ensuring public health covering areas such as infectious diseases, food safety, drinking water, the medical profession, etc.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5377, 25005, 'EN', 1, 'Ministry of Health of the Republic of Slovenia (Ministrstvo za zdravje)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5378, 25006, 'EN', 1, 'See more in its <a target="blank" href="http://www.mz.gov.si/en/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5379, 25007, 'EN', 1, 'The Ministry of Health of the Republic of Slovenia deals with activities concerning health at work that are carried out by occupational physicians. Within its scope, the control function concerning protection of human health against the harmful effects of ionizing and non-ionizing radiation is performed by the Slovenian Nuclear Safety Administration (Uprava Republike Slovenije za varstvo pred sevanji). Drafting of regulations, granting of concessions to occupational physicians, coordination of health at work programmes and leading and coordinating activities concerning health promotion for managers and working population are within the powers of the Ministry itself.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5380, 25008, 'EN', 1, 'Labour Inspectorate');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5381, 25009, 'EN', 1, 'See more in its <a target="blank" href="http://www.id.gov.si/en/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5382, 25010, 'EN', 1, 'Labour Inspectorate The Slovenian Labour Inspectorate in its repressive role performs inspection duties in the field of labour relations, health and safety at work and since 2004 also in the field of social welfare. From 1 January 2005, it has acted as a minor offences authority on First Instance in an accelerated procedure on the basis of the Minor Offences Act  [the Labour Inspectorate supervises the implementation of the Health and Safety at Work Act, which also defines offenses for violation of its provisions. When labour inspectors carry out regular inspection duties upon this law, in the case of offenses detected, they at the same time keep the proceedings for the offense in an accelerated procedure, as a First Instance authority. First Instance authority means that against the labour inspector’s decision about the offense there may be an appeal in the form of a request for judicial protection. Through its preventive and advisory role as Inspectorate, it provides expert help and advice both to employers and employees, companies and workers, it is involved in law-making and it informs the public of its findings, actions and consequences of violations of regulations in order to protect the rights of legal and natural persons.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5383, 25011, 'EN', 1, 'Directorate General of Labour Relations and Labour Rights (Direktorat za delovna razmerja in pravice iz dela)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5384, 25012, 'EN', 1, 'See more in its <a target="blank" href="http://www.mddsz.gov.si/en/areas_of_work/labour_relations_and_labour_rights/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>\r\n\r\nand_labour_rights/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>\r\n\r\nand_labour_rights/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>\r\n\r\nand_labour_rights/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5385, 25013, 'EN', 1, 'The main executive body within the Ministry of Labour, Family, Social Affairs and Equal Opportunities is the Directorate General of Labour Relations and Labour Rights (Direktorat za delovna razmerja in pravice iz dela) and in its scope the Health and Safety at Work Section (Sektor za varnost in zdravje pri delu). Other key bodies within the Ministry of Labour, Family, Social Affairs and Equal Opportunities are the Labour Inspectorate (Inšpektorat Republike Slovenije za delo) and the Council for Health and Safety at Work as the expert advisory body that considers and adopts the views and recommendations on innovation, strategy and implementation of integrated policies and priorities for health and safety at work.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5386, 25014, 'EN', 1, 'Ministry of Labour, Family, Social Affairs and Equal Opportunities of the Republic of Slovenia (Ministrstvo Za Delo, Družino, Socialne Zadeve in Enake Možnosti)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5387, 25015, 'EN', 1, 'See more in its <a target="blank" href="http://www.mddsz.gov.si/en/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5388, 25016, 'EN', 1, 'Key OSH authorities are located within the Ministry of Labour, Family, Social Affairs and Equal Opportunities of the Republic of Slovenia');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5389, 25017, 'EN', 1, 'The Spanish Association Standardisation and Certification (Asociación Española de Normalización y Certificación, AENOR)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5390, 25018, 'EN', 1, 'See more in its <a target="blank" href="http://www.aenor.es/aenor/inicio/home/home.asp">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5391, 25019, 'EN', 1, '<p>The Spanish Association Standardisation and Certification (Asociación Española de Normalización y Certificación, AENOR) was founded in 1986 as a private non-profit organisation. AENOR is a member organisation in the International Standardisation Organisation (ISO) and the European Committee for Standardization (CEN).</p><p>&nbsp;</p><p>AENOR offers certification for safety and health management systems in accordance with OHSAS 18001 standard. They also check legal compliance in companies (e.g. carrying out risk assessment, documentation etc</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5392, 25020, 'EN', 1, 'The Foundation for the Prevention of Occupational Risks (Fundación para la Prevención de Riesgos Laborales, FUNPRL');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5393, 25021, 'EN', 1, 'See more in its <a target="blank" href="http://www.funprl.es/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5394, 25022, 'EN', 1, 'The Foundation for the Prevention of Occupational Risks (Fundación para la Prevención de Riesgos Laborales, FUNPRL) is a national body under tripartite governance that grants funding for prevention activities. Representatives from the national government, from the Autonomous Communities, from trade unions and employer associations take decisions in the board (patronato) and the bureau (comisión delegada). The Foundation is funded in part by the Prevention and Rehabilitation Fund, drawn on management surpluses of the Social Security Work Accident and Professional Sickness Provident Entities (the ‘MATEPSS’). Applications for funding can be presented from national, regional, or sectoral stakeholders, as well as from the authorities or social partners. In 2009 the foundation granted 26.5 million euros for 230 different projects, and the calculated budget for 2010 was 25.6 million euros.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5395, 25023, 'EN', 1, 'Trade Union Institute for Work, Environment and Health (Instituto Sindical de Trabajo, Ambiente y Salud, ISTAS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5396, 25024, 'EN', 1, 'See more in its <a target="blank" href="http://www.istas.net/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5397, 25025, 'EN', 1, 'The Trade Union Institute for Work, Environment and Health (Instituto Sindical de Trabajo, Ambiente y Salud, ISTAS) was founded in 1996 by the Spanish trade union Comisiones Obreras (CC.OO), which still supports its work. This non-profit, independent research and education institute was set up as a foundation. The work is supervised by the Board of Directors, which is responsible for strategic and financial planning. The majority of the members are nominated by the CC.OO trade union');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5398, 25026, 'EN', 1, 'The National Institute for Safety and Hygiene at Work (Instituto nacional de seguridad e higiene en el trabajo, INSHT)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5399, 25027, 'EN', 1, 'See more in its <a target="blank" href="http://www.insht.es/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5400, 25028, 'EN', 1, '<p>The National Institute for Safety and Hygiene at Work (Instituto nacional de seguridad e higiene en el trabajo, INSHT) is the specialized scientific/technical body of the Spanish Government Administration for OSH matters. It was created in 1978 as an autonomous body of the Ministry of Employment and Social Security. It focuses on analysing, promoting, supporting and improving OSH issues. Its main tasks are:</p><ul><li>Technical assistance: providing specialized technical assistance to departments within the Administration, to stakeholders, and to OSH experts from public institutions or private companies. This activity represents approximately 30% of its global resources.</li><li>OSH research: Social research (i.e. working conditions surveys); epidemiological studies based on working accidents; field and laboratory research on chemicals, biological, physical agents and ergonomics. It also promotes and funds research through grants.</li><li>Training: An annual programme of activities.</li><li>Dissemination of information: several specialised publications are issued, e.g. on methods for measuring chemicals, or collections of preventive technical notes, OSH posters, leaflets, etc.</li><li>Guidelines and standardisation: Technical assistance in developing new OSH-related legislation, regulations and guidelines. Standardisation process: INSHT collaborates on technical committees for the formulation of UNE, EN and ISO standards.</li><li>Testing and certification of protective equipment and machinery.</li><li>Secretariat of the National Commision for Safety and Heath at Work: INSHT give technical support and general assistance to the National Commission for Safety and Health at Work.</li></ul><p>National Reference Center for EU-OSHA and EU Institutions, ensuring the coordination and transmission of information on OSH, a national level.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5401, 25029, 'EN', 1, 'Mutuas de Accidentes de Trabajo y Enfermedades Profesionales de la Seguridad Social');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5402, 25030, 'EN', 1, 'See more in its <a target="blank" href="http://www.amat.es/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5403, 25031, 'EN', 1, '<p>The Spanish worker compensation system can be characterised as a hybrid private and public system. Occupational accident insurance and compensation and rehabilitation of occupational diseases is usually organised by Social Security Work Accident and Professional Sickness Provident Entities, the ‘MATEPSS’ (Mutuas de Accidentes de Trabajo y Enfermedades Profesionales de la Seguridad Social). The legal basis is the Royal decree 1/1994 on the General Law on Social Security (see: section VI, Art. 67 ff. [28])</p><p>‘MATEPSS’ are established as private associations of companies and need formal approval from the Ministry of Employment and Social Security (MEYSS) which is the supervision authority. The MATEPPS are obliged to cooperate with the authorities of the public social security system. Members of the associations are companies who can join voluntarily - otherwise workers’ compensation is guaranteed by the public insurance institute INSS (Instituto Nacional de Seguridad Social) . Some sectors are excluded from the general regime, like mining, fisheries and agriculture . In those cases the public system again guarantees the worker compensation. As of December 2011, some 1.52 million companies were members of a Mutua, representing nearly 13.86 million workers [30]. Employers must bear all costs of the legal requirements for the protection of employees. The insurance system is financed by employer contributions, and can vary between 0.81% and 16.2% of the payroll, depending on the risk class of the company (average contribution is roughly 2% of the payroll).</p><p>At the moment there are 20 MATEPSS. They are members of the Association of Work Accident Insurance Companies (Associación de Mutuas de Accidentes de Trabajo, AMAT), which is a non-profit organisation, established in 1986. AMAT is the interest group for the insurance bodies. It represents common positions of the Mutuas and their member companies in negotiations within the social security system</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5404, 25032, 'EN', 1, 'The National Labour Inspection Authority (Inspección de Trabajo y Seguridad Social, ITSS)');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5405, 25033, 'EN', 1, 'See more in its <a target="blank" href="http://www.meyss.es/itss/web/index.html">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5406, 25034, 'EN', 1, '<p>The National Labour Inspection Authority (Inspección de Trabajo y Seguridad Social, ITSS) is under the authority of and funded by the MEYSS. The labour inspectorate of Cataluña is an exceptional case, which is independent in function and status to the regional authorities. As the name already indicates, ITSS does not only supervise safety and health in the companies, it is also responsible for general labour relations and compliance with social security regulations (including checking for illegal employment). The inspectorate has specialists for OSH, employment, and social security.</p><p>Despite being a national authority, the labour inspectorate is organised in local branches: Each of the 50 provinces has teams of labour inspectors. They follow action plans that are set up by the governments of the Autonomous Communities. These action plans define priorities as well as inspection goals. In this way, the labour inspectors are able to work with high-risk sectors and companies. Together, the regional plans make up the so-called national programme, which needs the approval of the Sectoral Conference (conferencia sectoral). The labour inspectors also respond to incidents (accidents and diseases) in the workplace and may impose sanctions in the case of any breach of law. Homogenous inspection standards can be set by the MEYSS.</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5407, 25035, 'EN', 1, 'Ministry for Employment and Social Security - Ministerio de Empleo y Seguridad Social, MEYSS');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5408, 25036, 'EN', 1, 'See more in its <a target="blank" href="http://www.myess.es/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Spain">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5409, 25037, 'EN', 1, '<p>Ministry for Employment and Social Security (Ministerio de Empleo y Seguridad Social, MEYSS) is responsible for the national OSH policies, and supervision of social security and the MATEPPS.</p><p>The National Commission for Safety and Health at Work (Comisión nacional de seguridad y salud en el trabajo, CNSST) advises the MEYSS, discusses new developments, and guides the implementation and evaluation of the OSH strategy. Four groups of stakeholders are represented in the Commission, representing national authorities, the Autonomous Communities, employers and trade unions. The Commission votes and adopts proposals by majority - the national and regional authorities execute one common vote, while employer representatives and trade unions have one vote each. In this way the Commission can be considered as a tripartite body.&nbsp;</p>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5410, 25038, 'EN', 1, 'Swedish Standardisation association');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5411, 25039, 'EN', 1, 'See more in its <a target="blank" href="http://www.standardiseringsforbundet.se/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Sweden">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5412, 25040, 'EN', 1, 'Swedish Standardisation association (Sveriges Standardiseringsförbund) has since 1 January 2012 taken over most of the activities from the Swedish Standards Council (SSR – Sveriges Standardiseringsråd).The Swedish Standardisation association is a collaborative organization for the three Swedish standardisation associations: ITS – Informationstekniska Standardiseringen (for telecommunication standards) SEK – Svensk Elstandard (for electrical standards) SIS – Swedish Standard Institute.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
