INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5414, 25042, 'EN', 1, 'See more in its <a target="blank" href="http://www.prevent.se/ ">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Sweden ">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(5415, 25043, 'EN', 1, 'Prevent Sweden (Former Joint Industrial Safety Council, Arbetarskyddsnämnden)[59] works to prevent accidents and improve the work environment, and provides training and training materials. Prevent Sweden is a non-profit organisation and acts on behalf of Sweden´s labour market principals: The Confederation of Swedish Enterprise (Svenskt Näringsliv), The Swedish Trade Union Confederation (Landsorganisationen, LO), and The Federation of Salaried Employees in Industry and Services (Privattjänstemannakartellen, PTK).');
