INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(6020, 25648, 'EN', 1, 'See more in its <a target="blank" href="http://www.mddsz.gov.si/en/areas_of_work/labour_relations_and_labour_rights/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>\r\n\r\nand_labour_rights/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>\r\n\r\nand_labour_rights/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>\r\n\r\nand_labour_rights/">website</a> and in <a target="_blank" href="https://oshwiki.eu/wiki/OSH_system_at_national_level_-_Slovenia">OSHwiki</a>');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
	(6021, 25649, 'EN', 1, 'The main executive body within the Ministry of Labour, Family, Social Affairs and Equal Opportunities is the Directorate General of Labour Relations and Labour Rights (Direktorat za delovna razmerja in pravice iz dela) and in its scope the Health and Safety at Work Section (Sektor za varnost in zdravje pri delu). Other key bodies within the Ministry of Labour, Family, Social Affairs and Equal Opportunities are the Labour Inspectorate (Inšpektorat Republike Slovenije za delo) and the Council for Health and Safety at Work as the expert advisory body that considers and adopts the views and recommendations on innovation, strategy and implementation of integrated policies and priorities for health and safety at work.');
INSERT INTO `translation` (`id`, `literal_id`, `language`, `is_default`, `text`) VALUES
